mapper.update:
MERGE INTO TB_SVPD_VHC_DSB_IZ T1 USING ( SELECT #{vhcMngtNo} AS VHC_MNGT_NO , (CASE WHEN (NVL(#{vhcMngtSn}, 0)) = 0 THEN (SELECT NVL(MAX(A.VHC_MNGT_SN), '0') + 1 FROM TB_SVPD_VHC_DSB_IZ A WHERE A.VHC_MNGT_NO = #{vhcMngtNo}) ELSE #{vhcMngtSn} END) AS VHC_MNGT_SN , #{carno} AS CARNO , #{vhcMngtOgTpCd} AS VHC_MNGT_OG_TP_CD , #{vhcMngtPrtnrNo} AS VHC_MNGT_PRTNR_NO , #{vhcPymdt} AS VHC_PYMDT , #{dsbEnddt} AS DSB_ENDDT , #{vhcMngtTpCd} AS VHC_MNGT_TP_CD , #{insrAgeCd} AS INSR_AGE_CD , #{rflngCdnoEncr} AS RFLNG_CDNO_ENCR , #{hipsCdnoEncr} AS HIPS_CDNO_ENCR , #{vhcDsbRmkCn} AS VHC_DSB_RMK_CN FROM DUAL ) T2 ON ( T2.VHC_MNGT_NO = T1.VHC_MNGT_NO AND T2.VHC_MNGT_SN = T1.VHC_MNGT_SN ) WHEN MATCHED THEN UPDATE SET T1.CARNO = T2.CARNO , T1.VHC_MNGT_OG_TP_CD = T2.VHC_MNGT_OG_TP_CD , T1.VHC_MNGT_PRTNR_NO = T2.VHC_MNGT_PRTNR_NO , T1.VHC_PYMDT = T2.VHC_PYMDT , T1.DSB_ENDDT = T2.DSB_ENDDT , T1.VHC_MNGT_TP_CD = T2.VHC_MNGT_TP_CD , T1.INSR_AGE_CD = T2.INSR_AGE_CD , T1.RFLNG_CDNO_ENCR = T2.RFLNG_CDNO_ENCR , T1.HIPS_CDNO_ENCR = T2.HIPS_CDNO_ENCR , T1.VHC_DSB_RMK_CN = T2.VHC_DSB_RMK_CN
WHEN NOT MATCHED THEN INSERT ( T1.VHC_MNGT_NO , T1.VHC_MNGT_SN , T1.CARNO , T1.VHC_MNGT_OG_TP_CD , T1.VHC_MNGT_PRTNR_NO , T1.VHC_PYMDT , T1.DSB_ENDDT , T1.VHC_MNGT_TP_CD , T1.INSR_AGE_CD , T1.RFLNG_CDNO_ENCR , T1.HIPS_CDNO_ENCR , T1.VHC_DSB_RMK_CN , T1.DTA_DL_YN
) VALUES ( T2.VHC_MNGT_NO , T2.VHC_MNGT_SN , T2.CARNO , T2.VHC_MNGT_OG_TP_CD , T2.VHC_MNGT_PRTNR_NO , T2.VHC_PYMDT , T2.DSB_ENDDT , T2.VHC_MNGT_TP_CD , T2.INSR_AGE_CD , T2.RFLNG_CDNO_ENCR , T2.HIPS_CDNO_ENCR , T2.VHC_DSB_RMK_CN , 'N'
)
