mapper.update:
UPDATE TB_SVPD_RGLVL_AW_BASE_IZ /* 급지수당기준내역 */
   SET APY_ENDDT = TO_CHAR(TO_DATE(#{apyStrtdt}, 'YYYYMMDD') - 1, 'YYYYMMDD')

 WHERE 1 = 1
   AND RGLVL_DV_CD = #{rglvlDvCd}
   AND TRIM(BIZ_RGLVL_CD) = #{bizRglvlCd}
   AND IZ_SN = (
                SELECT MAX(V1.IZ_SN)
                  FROM TB_SVPD_RGLVL_AW_BASE_IZ V1
                 WHERE V1.RGLVL_DV_CD = #{rglvlDvCd}
                   AND TRIM(V1.BIZ_RGLVL_CD) = #{bizRglvlCd}
               )

mapper.insert:
INSERT INTO TB_SVPD_RGLVL_AW_BASE_IZ (  /* 급지수당기준내역 */
       RGLVL_DV_CD      /* 급지구분코드 */
     , BIZ_RGLVL_CD     /* 업무급지코드 */
     , IZ_SN            /* 내역일련번호 */
     , APY_STRTDT       /* 적용시작일 */
     , APY_ENDDT        /* 적용종료일 */
     , MMT_LDTM         /* 이동소요시간 */
     , MMT_DSTN         /* 이동거리 */
     , RGLVL_GD_CD      /* 급지등급코드 */
     , RGLVL_AW_AMT     /* 급지수당금액 */
     , M_PER_MANHO      /* 분당공수 */
     , RGLVL_WEIT       /* 급지비중 */
     , AV_VE            /* 평균속도 */
     , DTA_DL_YN        /* 데이터삭제여부 */

     )
VALUES (
       #{rglvlDvCd}
     , #{bizRglvlCd}
     , (SELECT MAX(V1.IZ_SN) + 1
         FROM TB_SVPD_RGLVL_AW_BASE_IZ V1
        WHERE V1.RGLVL_DV_CD = #{rglvlDvCd}
          AND TRIM(V1.BIZ_RGLVL_CD) = #{bizRglvlCd})
     , #{apyStrtdt}
     , '99991231'
     , ${mmtLdtm}
     , ${mmtDstn}
     , #{rglvlGdCd}
     , ${rglvlAwAmt}
     , ${minPerManho}
     , ${rglvlWeit}
     , ${avVe}
     , 'N'

     )
