mapper.select:
SELECT B1.PDLV_DV_CD , B1.PDLV_NO , (SELECT MAX(APY_STRTDT) FROM TB_SVPD_PDLV_HIST WHERE PDLV_NO = H1.PDLV_NO) AS APY_STRTDT_MAX /* 적용시작일-최대값 */ , H1.APY_STRTDT , H1.APY_ENDDT , H1.PDLV_NM , H1.ZIP , H1.PDLV_ADR , H1.PDLV_DTL_ADR , H1.CNR_OG_ID FROM TB_SVPD_PDLV_BAS B1 INNER JOIN TB_SVPD_PDLV_HIST H1 ON B1.PDLV_NO = H1.PDLV_NO AND B1.PDLV_DV_CD = H1.PDLV_DV_CD WHERE 1 = 1 AND B1.DTA_DL_YN = 'N'
AND B1.PDLV_DV_CD = #{pdlvDvCd}
AND H1.PDLV_NM LIKE '%'||#{pdlvNm}||'%'
AND H1.CNR_OG_ID = #{ogId}
AND #{applyDate} BETWEEN H1.APY_STRTDT AND H1.APY_ENDDT

mapper.update:
UPDATE TB_SVPD_PDLV_BAS
   SET DTA_DL_YN = 'Y'

 WHERE PDLV_DV_CD = #{pdlvDvCd}
   AND PDLV_NO    = #{pdlvNo}

mapper.insert:
INSERT INTO TB_SVPD_PDLV_BAS
     ( PDLV_NO
     , PDLV_DV_CD
     , APY_STRTDT
     , APY_ENDDT
     , PDLV_NM
     , ZIP
     , PDLV_ADR
     , PDLV_DTL_ADR
     , CNR_OG_ID
     , LATD_GPS_VAL
     , LO_GPS_VAL
     , DTA_DL_YN

     )
    VALUES
     ( #{pdlvNo}
     , #{pdlvDvCd}
     , #{apyStrtdt}
     , #{apyEnddt}
     , #{pdlvNm}
     , #{zip}
     , #{pdlvAdr}
     , #{pdlvDtlAdr}
     , #{cnrOgId}
     , ''
     , ''
     , 'N'

     )

INSERT INTO TB_SVPD_PDLV_HIST
     ( PDLV_DV_CD
     , PDLV_NO
     , PDLV_SN
     , APY_STRTDT
     , APY_ENDDT
     , PDLV_NM
     , ZIP
     , PDLV_ADR
     , PDLV_DTL_ADR
     , CNR_OG_ID
     , LATD_GPS_VAL
     , LO_GPS_VAL
     , DTA_DL_YN

     )
VALUES
     ( #{pdlvDvCd}
     , #{pdlvNo}
     , NVL((SELECT MAX(PDLV_SN) +1
             FROM TB_SVPD_PDLV_HIST
            WHERE PDLV_DV_CD = #{pdlvDvCd}
              AND PDLV_NO = #{pdlvNo}),1)
     , #{apyStrtdt}
     , #{apyEnddt}
     , #{pdlvNm}
     , #{zip}
     , #{pdlvAdr}
     , #{pdlvDtlAdr}
     , #{cnrOgId}
     , ''
     , ''
     , 'N'

     )

mapper.update:
UPDATE TB_SVPD_PDLV_HIST
   SET APY_ENDDT = (#{apyStrtdt} - 1)

 WHERE PDLV_NO    = #{pdlvNo}
   AND PDLV_DV_CD = #{pdlvDvCd}
   AND APY_ENDDT = ( SELECT MAX(H1.APY_ENDDT)
                       FROM TB_SVPD_PDLV_HIST H1
                      WHERE H1.PDLV_NO = PDLV_NO
                   )

UPDATE TB_SVPD_PDLV_BAS
   SET PDLV_NM    = #{pdlvNm}
     , APY_STRTDT = #{apyStrtdt}
     , APY_ENDDT  = #{apyEnddt}
     , CNR_OG_ID  = #{cnrOgId}
     , DTA_DL_YN  = 'N'

 WHERE PDLV_NO    = #{pdlvNo}
   AND PDLV_DV_CD = #{pdlvDvCd}
