WITH TEMP0 AS ( SELECT V3.NEW_ADR_ZIP , COUNT(1) AS MGT_CNT FROM TB_SVPD_MCBY_CST_SV_OJ_IZ M1 /* 월별고객서비스대상내역 */ INNER JOIN TB_SSCT_CNTR_DTL C1 /* 계약상세 */ ON C1.CNTR_NO = M1.CNTR_NO AND C1.CNTR_SN = M1.CNTR_SN AND C1.DTA_DL_YN = 'N' INNER JOIN (SELECT V1.DTL_CNTR_NO, V1.DTL_CNTR_SN, V2.ADR_ID FROM TB_SSCT_CNTR_ADR_REL V1 /* 계약주소관계 */ INNER JOIN TB_SSCT_CNTR_ADRPC_BAS V2 /* 계약주소지기본 */ ON V2.CNTR_ADRPC_ID = V1.CNTR_ADRPC_ID AND V2.DTA_DL_YN = 'N' WHERE V1.DTA_DL_YN = 'N' AND V1.ADRPC_TP_CD = '3' /* 설치처 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN V1.VL_STRT_DTM AND V1.VL_END_DTM ) V2 ON V2.DTL_CNTR_NO = C1.CNTR_NO AND V2.DTL_CNTR_SN = C1.CNTR_SN INNER JOIN TB_GBCO_ADR_BAS V3 /* 주소지기본 */ ON V3.ADR_ID = V2.ADR_ID AND V3.DTA_DL_YN = 'N' WHERE M1.DTA_DL_YN = 'N' AND M1.MNGT_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND M1.REQD_DT IS NULL AND M1.CAN_DT IS NULL AND M1.MSH_WDWAL_DT IS NULL AND M1.PD_SELL_TP_CD != '9' GROUP BY V3.NEW_ADR_ZIP ORDER BY V3.NEW_ADR_ZIP ), TEMP1 AS ( /* 월별수임건수 */ SELECT NEW_ADR_ZIP , ROUND(SUM(WRK_CNT) / 3, 2) AS WRK_CNT FROM (SELECT V3.NEW_ADR_ZIP , COUNT(1) AS WRK_CNT FROM TB_SVPD_CST_SVAS_IST_ASN_IZ T1 /* 고객서비스AS설치배정내역 */ INNER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ T2 /* 고객서비스AS설치대상내역 */ ON T2.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T2.DTA_DL_YN = 'N' INNER JOIN (SELECT V1.DTL_CNTR_NO, V1.DTL_CNTR_SN, V2.ADR_ID FROM TB_SSCT_CNTR_ADR_REL V1 /* 계약주소관계 */ INNER JOIN TB_SSCT_CNTR_ADRPC_BAS V2 /* 계약주소지기본 */ ON V2.CNTR_ADRPC_ID = V1.CNTR_ADRPC_ID AND V2.DTA_DL_YN = 'N' WHERE V1.DTA_DL_YN = 'N' AND V1.ADRPC_TP_CD = '3' /* 설치처 */ AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN V1.VL_STRT_DTM AND V1.VL_END_DTM ) V2 ON V2.DTL_CNTR_NO = T1.CNTR_NO AND V2.DTL_CNTR_SN = T1.CNTR_SN INNER JOIN TB_GBCO_ADR_BAS V3 /* 주소지기본 */ ON V3.ADR_ID = V2.ADR_ID AND V3.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.WK_PRGS_STAT_CD = '20' /* 작업진행상태 = '작업완료' */ AND T1.WK_EXCN_DT BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -3), 'YYYYMMDD') || '01' AND TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') || '31' AND T1.WK_GRP_CD = #{wkGrpCd} GROUP BY V3.NEW_ADR_ZIP UNION ALL SELECT T3.NEW_ADR_ZIP , COUNT(1) AS WRK_CNT FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ T1 /* 고객서비스BS배정내역 */ INNER JOIN TB_SVPD_CST_SV_BFSVC_OJ_IZ T2 /* 고객서비스BS대상내역 */ ON T2.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T2.DTA_DL_YN = 'N' INNER JOIN TB_GBCO_ADR_BAS T3 /* 주소지기본 */ ON T3.ADR_ID = T2.ADR_ID AND T3.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.VST_PRGS_STAT_CD = '20' /* 작업진행상태 = '작업완료' */ AND T1.CNFM_PSIC_DV_CD = '2' AND T1.WK_EXCN_DT BETWEEN TO_CHAR(ADD_MONTHS(SYSDATE, -3), 'YYYYMMDD') || '01' AND TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') || '31' AND T1.WK_GRP_CD = #{wkGrpCd} GROUP BY T3.NEW_ADR_ZIP ) GROUP BY NEW_ADR_ZIP ), TEMP2 AS ( SELECT T1.NEW_ADR_ZIP /* 신주소우편번호 */ , T1.EMD_SN /* 읍면동일련번호 */ , T1.CTPV_NM /* 시도명 */ , T1.CTCTY_NM /* 시군구명 */ , T1.LAWC_EMD_NM /* 법정읍면동명 */ , T1.AMTD_NM /* 행정동명 */ , T3.RPB_LOCARA_CD /* 책임지역코드 */ , T3.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , T4.OG_NM /* 조직명 */ , T4.PRTNR_NO /* 파트너번호 */ , T4.PRTNR_KNM /* 파트너한글명 */ , T3.VST_DOW_VAL /* 방문요일값 */ , T1.PDLV_NO /* 출고지번호 */ , T5.PDLV_NM /* 출고지명 */ , (CASE WHEN T5.PDLV_DTL_ADR IS NOT NULL THEN T5.PDLV_ADR || ' ' || T5.PDLV_DTL_ADR ELSE T5.PDLV_ADR END) AS PDLV_ADR /* 출고지주소 */ , T1.ILD_YN /* 섬여부 */ , ROW_NUMBER() OVER(PARTITION BY T1.NEW_ADR_ZIP, T2.FR2P_LGLD_CD, T2.CTPV_NM, T2.CTCTY_NM, T2.LAWC_EMD_NM, T2.AMTD_NM ORDER BY T2.APY_STRTDT DESC) AS RN FROM (SELECT M1.NEW_ADR_ZIP, M1.EMD_SN, M1.FR2P_LGLD_CD, M1.CTPV_NM, M1.CTCTY_NM, M1.LAWC_EMD_NM, M1.AMTD_NM, M1.KYNOR_LOCARA_YN, M1.ILD_YN, M1.PDLV_NO FROM TB_SVPD_EGER_ASN_ADR_IZ M1 WHERE M1.EMD_SN = (SELECT MAX(EMD_SN) FROM TB_SVPD_EGER_ASN_ADR_IZ WHERE NEW_ADR_ZIP = M1.NEW_ADR_ZIP AND DTA_DL_YN = 'N') ) T1 /* 엔지니어배정용주소내역 */ INNER JOIN TB_SVPD_RPB_LOCARA_MNGT_ADR_IZ T2 /* 책임지역별관리주소내역 */ ON T2.FR2P_LGLD_CD = T1.FR2P_LGLD_CD AND T2.CTPV_NM = T1.CTPV_NM AND T2.CTCTY_NM = T1.CTCTY_NM AND T2.LAWC_EMD_NM = T1.LAWC_EMD_NM AND T2.AMTD_NM = T1.AMTD_NM AND T2.DTA_DL_YN = 'N' AND TO_CHAR(SYSDATE,