), TEMP3 AS ( SELECT T1.NEW_ADR_ZIP /* 신주소우편번호 */ , T1.EMD_SN /* 읍면동일련번호 */ , T1.CTPV_NM /* 시도명 */ , T1.CTCTY_NM /* 시군구명 */ , T1.LAWC_EMD_NM /* 법정읍면동명 */ , T1.AMTD_NM /* 행정동명 */ , T1.RPB_LOCARA_CD /* 책임지역코드 */ , T1.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , T4.OG_NM /* 조직명 */ , T4.PRTNR_NO /* 파트너번호 */ , T4.PRTNR_KNM /* 파트너한글명 */ , T1.VST_DOW_VAL /* 방문요일값 */ , T1.PDLV_NO /* 출고지번호 */ , T5.PDLV_NM /* 출고지명 */ , (CASE WHEN T5.PDLV_DTL_ADR IS NOT NULL THEN T5.PDLV_ADR || ' ' || T5.PDLV_DTL_ADR ELSE T5.PDLV_ADR END) AS PDLV_ADR /* 출고지주소 */ , T1.ILD_YN /* 섬여부 */ FROM TEMP2 T1 LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ T4 /* 월파트너내역 */ ON T4.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T4.PRTNR_NO = T1.ICHR_PRTNR_NO AND T4.OG_TP_CD = T1.ICHR_PRTNR_OG_TP_CD AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN (SELECT V5.PDLV_NO, V5.PDLV_DV_CD, V5.APY_STRTDT, V5.APY_ENDDT, V5.PDLV_NM, V5.PDLV_ADR, V5.PDLV_DTL_ADR , ROW_NUMBER() OVER(PARTITION BY PDLV_NO ORDER BY APY_STRTDT DESC) AS RN FROM TB_SVPD_PDLV_HIST V5 WHERE V5.PDLV_DV_CD = 'Z' ) T5 /* 출고지이력 */ ON T5.PDLV_NO = T1.PDLV_NO AND T5.RN = 1 WHERE 1 = 1 ) SELECT DISTINCT T3.NEW_ADR_ZIP /* 신주소우편번호 */ , T3.EMD_SN /* 읍면동일련번호 */ , T0.MGT_CNT /* 서비스계정수 */ , T1.WRK_CNT /* 월별수임건수 */ , T3.CTPV_NM /* 시도명 */ , T3.CTCTY_NM /* 시군구명 */ , T3.LAWC_EMD_NM /* 법정읍면동명 */ , T3.AMTD_NM /* 행정동명 */ , T3.RPB_LOCARA_CD /* 책임지역코드 */ , T3.RPB_LOCARA_GRP_CD /* 책임지역그룹코드 */ , T3.OG_NM /* 조직명 */ , T3.PRTNR_NO /* 파트너번호 */ , T3.PRTNR_KNM /* 파트너한글명 */ , T3.VST_DOW_VAL /* 방문요일값 */ , T3.PDLV_NO /* 출고지번호 */ , T3.PDLV_NM /* 출고지명 */ , T3.PDLV_ADR /* 출고지주소 */ , T3.ILD_YN /* 섬여부 */ FROM TEMP3 T3 LEFT OUTER JOIN TEMP0 T0 ON T0.NEW_ADR_ZIP = T3.NEW_ADR_ZIP LEFT OUTER JOIN TEMP1 T1 ON T1.NEW_ADR_ZIP = T3.NEW_ADR_ZIP WHERE 1 = 1 ORDER BY T3.CTPV_NM, T3.CTCTY_NM, T3.AMTD_NM, T3.RPB_LOCARA_CD, T3.NEW_ADR_ZIP
