mapper.update:
 MERGE
  INTO TB_SVPD_SPMT_SV_PIF_U_AG_IZ T1
 USING DUAL
    ON ( T1.CNTR_NO = #{cntrNo}
     AND T1.CNTR_SN = #{cntrSn}
     AND T1.SPMT_CST_SV_CD = #{customerServiceCode} )
  WHEN MATCHED THEN
UPDATE
   SET T1.SPMT_CST_SV_U_AG_YN = #{customerServiceUseAgreeYn}
     , T1.PIF_THP_OFR_AG_YN   = #{personalInfoAgreeYn}
     , T1.AGP_NM              = #{agreePersonName}
     , T1.APLC_WCON_IMG_CN    = #{agreementImageContent}

  WHEN NOT MATCHED THEN
INSERT
     ( CNTR_NO
     , CNTR_SN
     , SPMT_CST_SV_CD
     , SPMT_CST_SV_U_AG_YN
     , PIF_THP_OFR_AG_YN
     , AGP_NM
     , APLC_WCON_IMG_CN

     )
VALUES
     ( #{cntrNo}
     , #{cntrSn}
     , #{customerServiceCode}
     , #{customerServiceUseAgreeYn}
     , #{personalInfoAgreeYn}
     , #{agreePersonName}
     , #{agreementImageContent}

     )
