mapper.select:
SELECT T1.BASE_YM , T1.OG_TP_CD , T1.PRTNR_NO , T1.PRTNR_KNM , T1.OG_ID , T1.OG_CD , T1.OG_NM , T2.QOM_ASN_APY_YN , T2.APY_SN , T3.WARE_NO , T3.WARE_NM , T3.WARE_DV_CD , T3.WARE_DTL_DV_CD , F_CMZ_CD_NM(#{session.tenantId} , 'WARE_DTL_DV_CD' , T3.WARE_DTL_DV_CD) AS WARE_DTL_DV_NM , T3.HGR_WARE_NO , ( SELECT WARE_NM FROM TB_SVST_MCBY_WARE_IZ S2 WHERE S2.APY_YM = T3.APY_YM AND S2.WARE_NO = T3.HGR_WARE_NO ) AS HGR_WARE_NM , T3.DIDY_DV_CD , ( CASE WHEN T3.DIDY_DV_CD = '2' THEN '독립' ELSE '개인' END ) AS DIDY_DV_NM , T3.BLD_CD , T3.ADR_USE_YN , T3.RMK_CN , T3.WARE_ADR_ID , T4.RNADR , T4.RDADR , T4.NEW_ADR_ZIP , T5.BLD_NM FROM TB_OGBS_MM_PRTNR_IZ T1 INNER JOIN TB_SVST_QOM_ASN_PRTNR_APY_IZ T2 ON T2.PRTNR_NO = T1.PRTNR_NO AND T2.APY_SN = (SELECT MAX(APY_SN) FROM TB_SVST_QOM_ASN_PRTNR_APY_IZ WHERE PRTNR_NO = T1.PRTNR_NO) INNER JOIN TB_SVST_MCBY_WARE_IZ T3 ON T3.WARE_MNGT_PRTNR_NO = T2.PRTNR_NO AND T3.APY_YM = T1.BASE_YM INNER JOIN TB_GBCO_ADR_BAS T4 ON T3.WARE_ADR_ID = T4.ADR_ID INNER JOIN TB_OGBS_BLD_BAS T5 ON T3.BLD_CD = T5.BLD_CD
AND T1.BASE_YM = #{baseYm}
AND T1.OG_TP_CD = #{ogTpCd}
AND T1.PRTNR_NO = #{prtnrNo}
AND T1.PRTNR_KNM LIKE '%'||#{prtnrKnm}||'%'
AND T3.WARE_DV_CD = #{wareDvCd}
AND T3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND T3.WARE_NO = #{wareNo}
AND T3.WARE_NO = #{hgrWareNo}
AND T3.WARE_USE_YN = 'Y' AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' AND T3.DTA_DL_YN = 'N' AND T4.DTA_DL_YN = 'N' AND T5.DTA_DL_YN = 'N'

SELECT OG_ID , HGR_OG_ID , OG_TP_CD , OG_LEVL_DV_CD , OG_CD , ( CASE OG_LEVL_DV_CD WHEN '1' THEN NULL WHEN '2' THEN DGR1_LEVL_OG_CD WHEN '3' THEN DGR2_LEVL_OG_CD WHEN '4' THEN DGR3_LEVL_OG_CD WHEN '5' THEN DGR4_LEVL_OG_CD ELSE NULL END ) AS HGR_OG_CD , OG_NM , BLD_CD , BLD_NM , HOO_OG_TP_CD , HOO_PRTNR_NO , HOO_PRTNR_NM FROM TB_OGBS_MM_OG_IZ WHERE BASE_YM = #{baseYm}
AND OG_LEVL_DV_CD = #{ogLevlDvCd}
AND OG_ID = #{ogId}
AND OG_TP_CD = #{ogTpCd}
AND CLO_YN = 'N' AND DTA_DL_YN = 'N' AND OG_CD IS NOT NULL AND OG_NM IS NOT NULL START WITH HGR_OG_ID IS NULL CONNECT BY PRIOR OG_ID = HGR_OG_ID

SELECT OG_ID
     , PRTNR_NO
     , PRTNR_KNM
  FROM TB_OGBS_MM_PRTNR_IZ
 WHERE BASE_YM = #{baseYm}

   AND OG_ID = #{ogId}
