mapper.select:
SELECT T1.STR_WARE_NO /*입고창고번호*/ , T1.ITM_STR_NO /*품목입고번호*/ , T1.STR_SN AS ITM_STR_SN /*입고순번*/ , T2.WARE_NM /*창고명*/ , T1.STR_RGST_DT /*입고등록일자*/ , P1.SVPD_SAP_CD AS SAP_CD /*SAP코드*/ , T1.ITM_PD_CD /*품목상품코드*/ , T1.MNGT_UNIT_CD /*관리단위코드*/ , P1.SVPD_NM_ABBR1 AS ITM_PD_NM , T1.ITM_GD_CD /*품목등급코드*/ , T1.STR_QTY /*입고수량*/ , T1.RMK_CN /*입고사유*/ FROM TB_SVST_ITM_STR_IZ T1 INNER JOIN TB_SVST_MCBY_WARE_IZ T2 ON T1.STR_WARE_NO = T2.WARE_NO AND SUBSTR(T1.STR_RGST_DT,1,6) = T2.APY_YM INNER JOIN () P1 ON P1.SVPD_PD_CD = T1.ITM_PD_CD WHERE 1 = 1 AND T1.STR_RGST_DT LIKE #{strRgstDt} ||'%' AND P1.SVPD_ITEM_KND = '7'
AND T1.ITM_PD_CD = #{itmPdCd}
AND T1.STR_TP_CD = '117' AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' AND T2.WARE_USE_YN = 'Y' ORDER BY T1.ITM_STR_NO , T1.STR_SN

mapper.insert:
INSERT INTO TB_SVST_ITM_STR_IZ (
          ITM_STR_NO                /* 품목입고번호 */
        , STR_SN                    /* 입고일련번호 */
        , STR_TP_CD                 /* 입고유형코드 */
        , STR_RGST_DT               /* 입고등록일자 */
        , STR_WARE_NO               /* 입고창고번호 */
        , WARE_MNGT_PRTNR_NO        /* 창고관리파트너번호 */
        , WARE_MNGT_PRTNR_OG_TP_CD  /* 창고관리파트너조직유형코드 */
        , STR_WARE_DV_CD            /* 입고창고구분코드 */
        , DRT_STR_YN                /*직접입고여부*/
        , ITM_PD_CD                 /* 품목상품코드 */
        , ITM_GD_CD                 /* 품목등급코드 */
        , MNGT_UNIT_CD              /* 관리단위코드 */
        , STR_QTY                   /* 입고수량 */
        , STR_CONF_DT               /* 입고확인일자 */
        , STR_CONF_HH               /* 입고확인시간 */
        , STR_CONF_PRTNR_NO         /* 입고확인파트너번호 */
        , STR_CONF_PRTNR_OG_TP_CD   /* 입고확인파트너조직유형코드 */
        , RMK_CN                    /* 비고내용 */
        , DTA_DL_YN                 /* 데이터삭제여부 */
        )
    VALUES (
          #{itmStrNo}
        , #{itmStrSn}
        , #{strTpCd}
        , #{strRgstDt}
        , #{strWareNo}
        , ( SELECT WARE_MNGT_PRTNR_NO
              FROM TB_SVST_MCBY_WARE_IZ
             WHERE 1 = 1
               AND APY_YM =  SUBSTR(#{strRgstDt},1,6)
               AND WARE_NO = #{strWareNo})
        , (SELECT OG_TP_CD
             FROM TB_SVST_MCBY_WARE_IZ
            WHERE 1 = 1
              AND WARE_NO = #{strWareNo}
              AND APY_YM = TO_CHAR(SYSDATE,'YYYYMM'))
        , '2'
        , 'N'
        , #{itmPdCd}
        , #{itmGdCd}
        , #{mngtUnit}
        , #{strQty}
        , TO_CHAR(SYSDATE, 'YYYYMMDD')
        , TO_CHAR(SYSDATE, 'HH24MISS')
        , #{session.employeeIDNumber}
        , #{strConfPrtnrOgTpCd}
        , #{rmkCn}
        , 'N'

        )

mapper.update:
UPDATE TB_SVST_ITM_STR_IZ
   SET RMK_CN = #{rmkCn}

 WHERE 1 = 1
   AND ITM_STR_NO = #{itmStrNo}
   AND STR_SN = #{itmStrSn}

mapper.delete:
UPDATE TB_SVST_ITM_STR_IZ
   SET DTA_DL_YN = 'Y'

 WHERE 1 = 1
   AND ITM_STR_NO = #{itmStrNo}
   AND STR_SN = #{itmStrSn}
