mapper.select:
SELECT D1.WARE_NO /* 창고번호 */ , D1.WARE_NM /* 창고명 */ FROM TB_SVST_MCBY_WARE_IZ D1 /* 월별창고내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ D2 /* 월별창고내역 */ ON D2.APY_YM = D1.APY_YM AND D2.WARE_NO = D1.HGR_WARE_NO WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D1.WARE_USE_YN = 'Y' AND D1.APY_YM = #{baseYm} AND D1.WARE_DV_CD = #{wareDvCd}
AND D1.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( ( D1.WARE_DTL_DV_CD = '20' AND (D1.WARE_NM LIKE '%센터' OR D1.WARE_NM LIKE '%지점' OR D1.WARE_NM LIKE '%기장' OR D1.WARE_NM LIKE '%창고') ) OR ( D1.WARE_DTL_DV_CD = '21' AND (D2.WARE_NM LIKE '%센터' OR D2.WARE_NM LIKE '%지점' OR D2.WARE_NM LIKE '%기장' OR D2.WARE_NM LIKE '%창고') ) ) /* 상위창고명이 센터, 지점, 기장, 창고로 끝나는 창고만 조회 */ ORDER BY TO_NUMBER(D1.SORT_DV_VAL), D1.WARE_NM, D1.WARE_NO

SELECT TO_CHAR(TO_NUMBER(D2.SVPD_SAP_CD)) AS SAP_CD /* SAP코드 */ , D1.ITM_PD_CD /* 품목상품코드 */ , D2.SVPD_NM_ABBR1 AS ITM_PD_NM /* 상품명 */ , D1.JBF_MMS3_OSTR_QTY /* 직전3개월출고수량 */ , TO_CHAR(D1.MLMN_OSTR_QTY, 'FM999999990.0') AS MLMN_OSTR_QTY /* 월평균출고수량 */ , TO_CHAR(D1.D_AV_OSTR_QTY, 'FM999999990.0') AS D_AV_OSTR_QTY /* 일평균출고수량 */ , D1.ITM_MNGT_GD_CD /* 품목관리등급코드 */ , D1.CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , D1.RMK_CN /* 비고내용 */ , D1.MNGT_YM /* 관리년월 */ FROM ( SELECT MNGT_YM /* 관리년월 */ , ITM_PD_CD /* 품목상품코드 */ , ITM_MNGT_GD_CD /* 품목관리등급코드 */ , CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , NVL(JBF_MMS3_OSTR_QTY, 0) AS JBF_MMS3_OSTR_QTY /* 직전3개월출고수량 */ , NVL(MLMN_OSTR_QTY, 0) AS MLMN_OSTR_QTY /* 월평균출고수량 */ , NVL(D_AV_OSTR_QTY, 0) AS D_AV_OSTR_QTY /* 일평균출고수량 */ , RMK_CN /* 비고내용 */ , ROW_NUMBER() OVER(PARTITION BY MNGT_YM, WARE_NO, ITM_PD_CD ORDER BY ITM_GD_SN DESC) AS RN /* 순번 */ FROM TB_SVST_CST_SV_ITM_GD_IZ WHERE DTA_DL_YN = 'N' AND MNGT_YM = #{baseYm} AND ITM_KND_CD = #{itmKndCd}
AND ITM_PD_CD = #{itmPdCd}
AND ( ITM_MNGT_GD_CD = #{itmMngtGdCd} OR CTR_ITM_MNGT_GD_CD = #{itmMngtGdCd} )
AND ( ITM_MNGT_GD_CD IN ('S', 'A', 'B') OR CTR_ITM_MNGT_GD_CD IN ('S', 'A', 'B') )
AND ( ITM_MNGT_GD_CD IN ('C', 'D') OR CTR_ITM_MNGT_GD_CD IN ('C', 'D') )
AND WARE_NO = '000001' /* 창고번호가 없는 경우 전체로 조회하기 위함 */
AND WARE_NO = ( SELECT CASE WHEN WARE_DTL_DV_CD = '20' THEN WARE_NO ELSE HGR_WARE_NO /* 조직창고가 아닌 경우 상위창고로 조회 */ END FROM TB_SVST_MCBY_WARE_IZ /* 월별창고내역 */ WHERE DTA_DL_YN = 'N' AND APY_YM = #{baseYm} AND WARE_DV_CD = #{wareDvCd} AND WARE_NO = #{wareNo} )
) D1 INNER JOIN () D2 /* 상품 */ ON D2.SVPD_PD_CD = D1.ITM_PD_CD WHERE D1.RN = 1
AND D2.SVPD_COMM_GB = #{matUtlzDvCd}
AND D2.SVPD_USE_YN = #{useYn}
ORDER BY SAP_CD, D1.ITM_PD_CD

WITH PD AS ( SELECT T1.PD_CD AS ITM_PD_CD /* 품목상품코드 */ , T2.PD_PRP_VAL19 AS ITM_KND_CD /* 품목종류코드 */ , T1.PD_ABBR_NM AS ITM_PD_NM /* 품목상품명 */ , TO_CHAR(TO_NUMBER(T1.SAP_MAT_CD)) AS SAP_CD /* SAP코드 */ , T2.PD_PRP_VAL15 AS BASE_GB /* 상시보유자재 */ , T2.PD_PRP_VAL21 AS COMM_GB /* 공통부품구분 */ FROM TB_PDBS_PD_BAS T1 /* 상품기본 */ INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL T2 /* 상품각사속성상세 */ ON T1.PD_CD = T2.PD_CD WHERE T1.PD_TP_CD = 'M' AND T1.DTA_DL_YN = 'N' AND T2.PD_EXTS_PRP_GRP_CD = 'PART' AND T2.DTA_DL_YN = 'N' AND T2.PD_PRP_VAL19 = #{itmKndCd}
AND T1.PD_CD = #{itmPdCd}
AND T2.PD_PRP_VAL21 = #{matUtlzDvCd}
AND ( T2.PD_PRP_VAL32 IS NULL OR T2.PD_PRP_VAL32 = 'Y' )
AND T2.PD_PRP_VAL32 <> 'Y'
) SELECT T1.SAP_CD /* SAP코드 */ , T1.ITM_PD_CD /* 품목상품코드 */ , T1.ITM_PD_NM /* 상품명 */ , NVL(T2.QTY_SUM, 0) AS JBF_MMS3_OSTR_QTY /* 직전3개월출고수량 */ , ROUND(NVL(T2.QTY_SUM, 0) / 3, 1) AS MLMN_OSTR_QTY /* 월평균출고수량 */ , ROUND(NVL(T2.QTY_SUM, 0) / 3 / 23, 1) AS D_AV_OSTR_QTY /* 일평균출고수량 */ , CASE WHEN ROUND(T2.QTY_SUM / 3, 1) >= 100 THEN 'S' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 50 THEN 'A' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 30 THEN 'B' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 10 THEN 'C' ELSE 'D' END AS ITM_MNGT_GD_CD /* 품목관리등급코드 */ , T3.CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , T3.RMK_CN /* 비고내용 */ , #{baseYm} AS MNGT_YM /* 관리년월 */ FROM PD T1 LEFT OUTER JOIN ( SELECT T.ITM_PD_CD , NVL(SUM(T.QTY), 0) AS QTY_SUM FROM ( SELECT D1.ITM_PD_CD , D1.OSTR_QTY AS QTY FROM TB_SVST_ITM_OSTR_IZ D1 /* 품목출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD AND D2.ITM_KND_CD = D1.ITM_KND_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.OSTR_WARE_NO AND D3.APY_YM = SUBSTR(D1.OSTR_DT, 1, 6) WHERE D1.OSTR_TP_CD = '217' /* 기타출고 */ AND D1.ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.OSTR_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
UNION ALL SELECT /*+ INDEX(D1 IX_SVST_SV_WK_OSTR_IZ_01) */ D1.ITM_PD_CD , D1.USE_QTY AS QTY FROM TB_SVST_SV_WK_OSTR_IZ D1 /* 서비스작업출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.WK_WARE_NO AND D3.APY_YM = SUBSTR(D1.FNL_VST_FSH_DT, 1, 6) WHERE D1.FNL_ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D1.SV_BIZ_HCLSF_CD IN ('1', '2', '3') /* 설치, B/S, A/S */ AND D1.FNL_MDFC_USR_ID NOT LIKE 'CONV%' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.FNL_VST_FSH_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -1), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
UNION ALL SELECT D1.ITM_PD_CD , D1.OSTR_QTY AS QTY FROM TB_SVST_ITM_OSTR_IZ D1 /* 품목출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD AND D2.ITM_KND_CD = D1.ITM_KND_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.OSTR_WARE_NO AND D3.APY_YM = SUBSTR(D1.OSTR_DT, 1, 6) WHERE D1.OSTR_TP_CD = '217' /* 기타출고 */ AND D1.ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -2), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.OSTR_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -2), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
UNION ALL SELECT /*+ INDEX(D1 IX_SVST_SV_WK_OSTR_IZ_01) */ D1.ITM_PD_CD , D1.USE_QTY AS QTY FROM TB_SVST_SV_WK_OSTR_IZ D1 /* 서비스작업출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.WK_WARE_NO AND D3.APY_YM = SUBSTR(D1.FNL_VST_FSH_DT, 1, 6) WHERE D1.FNL_ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D1.SV_BIZ_HCLSF_CD IN ('1', '2', '3') /* 설치, B/S, A/S */ AND D1.FNL_MDFC_USR_ID NOT LIKE 'CONV%' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -2), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.FNL_VST_FSH_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -2), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
UNION ALL SELECT D1.ITM_PD_CD , D1.OSTR_QTY AS QTY FROM TB_SVST_ITM_OSTR_IZ D1 /* 품목출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD AND D2.ITM_KND_CD = D1.ITM_KND_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.OSTR_WARE_NO AND D3.APY_YM = SUBSTR(D1.OSTR_DT, 1, 6) WHERE D1.OSTR_TP_CD = '217' /* 기타출고 */ AND D1.ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -3), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.OSTR_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -3), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
UNION ALL SELECT /*+ INDEX(D1 IX_SVST_SV_WK_OSTR_IZ_01) */ D1.ITM_PD_CD , D1.USE_QTY AS QTY FROM TB_SVST_SV_WK_OSTR_IZ D1 /* 서비스작업출고내역 */ INNER JOIN PD D2 /* WITH절 상품 */ ON D2.ITM_PD_CD = D1.ITM_PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역 */ ON D3.WARE_NO = D1.WK_WARE_NO AND D3.APY_YM = SUBSTR(D1.FNL_VST_FSH_DT, 1, 6) WHERE D1.FNL_ITM_GD_CD = 'A' AND D1.DTA_DL_YN = 'N' AND D1.SV_BIZ_HCLSF_CD IN ('1', '2', '3') /* 설치, B/S, A/S */ AND D1.FNL_MDFC_USR_ID NOT LIKE 'CONV%' AND D3.DTA_DL_YN = 'N' AND D3.WARE_USE_YN = 'Y' AND D3.APY_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -3), 'YYYYMM') AND D3.WARE_DV_CD = #{wareDvCd} /* 서비스센터 */ AND D1.FNL_VST_FSH_DT LIKE TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{baseYm}, 'YYYYMM'), -3), 'MM'), 'YYYYMM') || '%'
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.WARE_DTL_DV_CD = #{wareDtlDvCd}
AND ( D3.HGR_WARE_NO = #{wareNo} OR D3.WARE_NO = #{wareNo} )
) T GROUP BY T.ITM_PD_CD
HAVING ROUND(SUM(T.QTY) / 3, 1) >= 100
HAVING ROUND(SUM(T.QTY) / 3, 1) < 100 AND ROUND(SUM(T.QTY) / 3, 1) >= 50
HAVING ROUND(SUM(T.QTY) / 3, 1) < 50 AND ROUND(SUM(T.QTY) / 3, 1) >= 30
HAVING ROUND(SUM(T.QTY) / 3, 1) < 30 AND ROUND(SUM(T.QTY) / 3, 1) >= 10
HAVING ROUND(NVL(SUM(T.QTY), 0) / 3, 1) < 10
HAVING ROUND(SUM(T.QTY) / 3, 1) >= 30
HAVING ROUND(NVL(SUM(T.QTY), 0) / 3, 1) < 30
) T2 ON T2.ITM_PD_CD = T1.ITM_PD_CD LEFT OUTER JOIN ( SELECT ITM_PD_CD /* 품목상품코드 */ , CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , RMK_CN /* 비고내용 */ , ROW_NUMBER() OVER(PARTITION BY ITM_PD_CD ORDER BY ITM_GD_SN DESC) AS RN /* 순번 */ FROM TB_SVST_CST_SV_ITM_GD_IZ WHERE DTA_DL_YN = 'N' AND WARE_NO = '000001' AND MNGT_YM = #{baseYm}
AND ITM_PD_CD = #{itmPdCd}
AND CTR_ITM_MNGT_GD_CD = #{itmMngtGdCd}
AND CTR_ITM_MNGT_GD_CD IN ('S', 'A', 'B')
AND CTR_ITM_MNGT_GD_CD IN ('C', 'D')
) T3 ON T3.ITM_PD_CD = T1.ITM_PD_CD AND T3.RN = 1
WHERE ( T3.CTR_ITM_MNGT_GD_CD = #{itmMngtGdCd} OR CASE WHEN ROUND(T2.QTY_SUM / 3, 1) >= 100 THEN 'S' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 50 THEN 'A' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 30 THEN 'B' WHEN ROUND(T2.QTY_SUM / 3, 1) >= 10 THEN 'C' ELSE 'D' END = #{itmMngtGdCd} )
WHERE ( T3.CTR_ITM_MNGT_GD_CD IN ('S', 'A', 'B') OR ROUND(T2.QTY_SUM / 3, 1) >= 30 )
WHERE ( T3.CTR_ITM_MNGT_GD_CD IN ('C', 'D') OR ROUND(NVL(T2.QTY_SUM, 0) / 3, 1) < 30 )
ORDER BY T1.SAP_CD, T1.ITM_PD_CD

mapper.insert:
INSERT INTO TB_SVST_CST_SV_ITM_GD_IZ /* 고객서비스품목등급내역 */ ( MNGT_YM /* 관리년월 */ , WARE_NO /* 창고번호 */ , ITM_PD_CD /* 품목상품코드 */ , ITM_GD_SN /* 품목등급일련번호 */ , ITM_KND_CD /* 품목종류코드 */ , ITM_MNGT_GD_CD /* 품목관리등급코드 */ , CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , WARE_DV_CD /* 창고구분코드 */ , JBF_MMS3_OSTR_QTY /* 직전3개월출고수량 */ , MLMN_OSTR_QTY /* 월평균출고수량 */ , D_AV_OSTR_QTY /* 일평균출고수량 */ , RMK_CN /* 비고내용 */ , DTA_DL_YN /* 데이터삭제여부 */
) SELECT T2.MNGT_YM /* 관리년월 */ , T2.WARE_NO /* 창고번호 */ , T2.ITM_PD_CD /* 품목상품코드 */ , ( SELECT MAX(ITM_GD_SN) + 1 FROM TB_SVST_CST_SV_ITM_GD_IZ WHERE MNGT_YM = T2.MNGT_YM AND WARE_NO = T2.WARE_NO AND ITM_PD_CD = T2.ITM_PD_CD ) AS ITM_GD_SN /* 품목등급일련번호 */ , T2.ITM_KND_CD /* 품목종류코드 */ , T2.ITM_MNGT_GD_CD /* 품목관리등급코드 */ , #{ctrItmMngtGdCd} AS CTR_ITM_MNGT_GD_CD /* 조정품목관리등급코드 */ , T2.WARE_DV_CD /* 창고구분코드 */ , T2.JBF_MMS3_OSTR_QTY /* 직전3개월출고수량 */ , T2.MLMN_OSTR_QTY /* 월평균출고수량 */ , T2.D_AV_OSTR_QTY /* 일평균출고수량 */ , #{rmkCn} AS RMK_CN /* 비고내용 */ , 'N' AS DTA_DL_YN /* 데이터삭제여부 */
FROM ( SELECT WARE_NO FROM TB_SVST_MCBY_WARE_IZ /* 월별창고내역 */ WHERE APY_YM = #{baseYm} AND WARE_DV_CD = '2' /* 서비스센터 */ AND WARE_DTL_DV_CD = '20' /* 조직창고 */ AND DTA_DL_YN = 'N' AND WARE_USE_YN = 'Y' AND ( WARE_NM LIKE '%센터' OR WARE_NM LIKE '%지점' OR WARE_NM LIKE '%기장' OR WARE_NM LIKE '%창고' ) UNION ALL SELECT '000001' AS WARE_NO FROM DUAL ) T1 INNER JOIN ( SELECT MNGT_YM , WARE_NO , ITM_PD_CD , ITM_KND_CD , ITM_MNGT_GD_CD , WARE_DV_CD , JBF_MMS3_OSTR_QTY , MLMN_OSTR_QTY , D_AV_OSTR_QTY , ROW_NUMBER() OVER(PARTITION BY WARE_NO ORDER BY ITM_GD_SN DESC) AS RN FROM TB_SVST_CST_SV_ITM_GD_IZ /* 고객서비스품목등급내역 */ WHERE DTA_DL_YN = 'N' AND MNGT_YM = #{baseYm} AND ITM_PD_CD = #{itmPdCd} ) T2 ON T2.WARE_NO = T1.WARE_NO WHERE T2.RN = 1
