SELECT /*+ NO_MERGE(T1 T3) */ T1.BLD_CD /* 빌딩코드 */ , T1.BLD_NM /* 빌딩명 */ , TO_CHAR(TO_NUMBER(T1.SAP_MAT_CD)) AS SAP_MAT_CD /* SAP코드 */ , T1.CSMB_PD_CD /* 품목코드 */ , T2.PD_NM /* 품목명 */ , T1.NWCMR /* 신입 배부유형 */ , T1.INDV /* 개인 배부유형 */ , T1.BLD /* 빌딩 배부유형 */ , NVL(T1.DDLV_QTY_SUM, 0) AS DDLV_QTY_SUM /* 합계 배부수량 */ , NVL(T1.MMS11B_DDLV_QTY, 0) AS MM1_QTY /* 11개월전 배부수량 */ , NVL(T1.MMS10B_DDLV_QTY, 0) AS MM2_QTY /* 10개월전 배부수량 */ , NVL(T1.MMS9B_DDLV_QTY, 0) AS MM3_QTY /* 9개월전 배부수량 */ , NVL(T1.MMS8B_DDLV_QTY, 0) AS MM4_QTY /* 8개월전 배부수량 */ , NVL(T1.MMS7B_DDLV_QTY, 0) AS MM5_QTY /* 7개월전 배부수량 */ , NVL(T1.MMS6B_DDLV_QTY, 0) AS MM6_QTY /* 6개월전 배부수량 */ , NVL(T1.MMS5B_DDLV_QTY, 0) AS MM7_QTY /* 5개월전 배부수량 */ , NVL(T1.MMS4B_DDLV_QTY, 0) AS MM8_QTY /* 4개월전 배부수량 */ , NVL(T1.MMS3B_DDLV_QTY, 0) AS MM9_QTY /* 3개월전 배부수량 */ , NVL(T1.MMS2B_DDLV_QTY, 0) AS MM10_QTY /* 2개월전 배부수량 */ , NVL(T1.MMS1B_DDLV_QTY, 0) AS MM11_QTY /* 1개월전 배부수량 */ , NVL(T1.TLST_MCNT_QTY, 0) AS MM12_QTY /* 당월 배부수량 */ , NVL(T3.VST_ACC_SUM, 0) AS VST_ACC_SUM /* 합계 방문계정 */ , NVL(T3.BDT_INDV, 0) AS BDT_INDV /* 비데(개인) */ , NVL(T3.BDT_CRP, 0) AS BDT_CRP /* 비데(법인) */ , NVL(T3.WRFR, 0) AS WRFR /* 정수기 */ , NVL(T3.ARCLE_INDV, 0) AS ARCLE_INDV /* 공기청정기(개인) */ , NVL(T3.ARCLE_CRP, 0) AS ARCLE_CRP /* 공기청정기(법인) */ , NVL(T3.WTR_SFTNR, 0) AS WTR_SFTNR /* 연수기 */ , NVL(T3.MSGCR, 0) AS MSGCR /* 안마의자 */ , NVL(T3.CFF_MCHN, 0) AS CFF_MCHN /* 커피머신 */ , NVL(T3.DRYR, 0) AS DRYR /* 건조기 */ , NVL(T3.WASH, 0) AS WASH /* 세탁기 */ , NVL(T3.ARDRSSR, 0) AS ARDRSSR /* 에어드레서 */ , NVL(T3.SSCLING, 0) AS SSCLING /* 삼성청소기 */ FROM ( SELECT /*+ INDEX(BLD PK_OGBS_BLD_BAS) */ T.BLD_CD /* 빌딩코드 */ , BLD.BLD_NM /* 빌딩명 */ , T.SAP_MAT_CD /* SAP코드 */ , T.CSMB_PD_CD /* 소모품상품코드 */ , LISTAGG(DISTINCT T.NWCMR, ', ') WITHIN GROUP(ORDER BY T.NWCMR) AS NWCMR /* 신입 배부유형 */ , LISTAGG(DISTINCT T.INDV, ', ') WITHIN GROUP(ORDER BY T.INDV) AS INDV /* 개인 배부유형 */ , LISTAGG(DISTINCT T.BLD, ', ') WITHIN GROUP(ORDER BY T.BLD) AS BLD /* 빌딩 배부유형 */ , SUM(T.BFSVC_CSMB_DDLV_QTY) AS DDLV_QTY_SUM /* 합계 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -11), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS11B_DDLV_QTY /* 11개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -10), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS10B_DDLV_QTY /* 10개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -9), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS9B_DDLV_QTY /* 9개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -8), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS8B_DDLV_QTY /* 8개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -7), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS7B_DDLV_QTY /* 7개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -6), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS6B_DDLV_QTY /* 6개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -5), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS5B_DDLV_QTY /* 5개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -4), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS4B_DDLV_QTY /* 4개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -3), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS3B_DDLV_QTY /* 3개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -2), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS2B_DDLV_QTY /* 2개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYmTo}, 'YYYYMM'), -1), 'YYYYMM') THEN T.BFSVC_CSMB_DDLV_QTY END) AS MMS1B_DDLV_QTY /* 1개월전 배부수량 */ , SUM(CASE WHEN T.MNGT_YM = #{mngtYmTo} THEN T.BFSVC_CSMB_DDLV_QTY END) AS TLST_MCNT_QTY /* 당월 배부수량 */ FROM ( SELECT /*+ INDEX(D1 PK_SVST_BFSVC_CSMB_DDLV_IZ) INDEX(D2 PK_SVST_BFSVC_CSMB_BASE_DTL) */ CASE WHEN D1.BFSVC_CSMB_DDLV_OJ_CD IN ('1', '2') THEN ( SELECT S2.BLD_CD FROM TB_OGBS_MM_PRTNR_IZ S1 INNER JOIN TB_OGBS_MM_OG_IZ S2 ON S2.BASE_YM = S1.BASE_YM AND S2.OG_TP_CD = S1.OG_TP_CD AND S2.OG_ID = S1.OG_ID WHERE S1.DTA_DL_YN = 'N' AND S1.OG_TP_CD = 'W02' AND S2.DTA_DL_YN = 'N' AND S2.OG_TP_CD = 'W02' AND S1.BASE_YM = D1.MNGT_YM AND S1.PRTNR_NO = D1.STR_WARE_NO ) ELSE ( SELECT BLD_CD FROM TB_OGBS_BLD_BAS WHERE DTA_DL_YN = 'N' AND OG_TP_CD = 'W02' AND BLD_CD = D1.STR_WARE_NO ) END AS BLD_CD /* 빌딩코드 */ , D1.SAP_MAT_CD /* SAP코드 */ , D1.CSMB_PD_CD /* 소모품상품코드 */ , CASE WHEN D1.BFSVC_CSMB_DDLV_OJ_CD = '1' THEN F_CMZ_CD_NM('TNT_WELLS', 'BFSVC_CSMB_DDLV_TP_CD', D2.BFSVC_CSMB_DDLV_TP_CD, #{session.langId}) END AS NWCMR /* 신입 배부유형 */ , CASE WHEN D1.BFSVC_CSMB_DDLV_OJ_CD = '2' THEN F_CMZ_CD_NM('TNT_WELLS', 'BFSVC_CSMB_DDLV_TP_CD', D2.BFSVC_CSMB_DDLV_TP_CD, #{session.langId}) END AS INDV /* 개인 배부유형 */ , CASE WHEN D1.BFSVC_CSMB_DDLV_OJ_CD = '3' THEN F_CMZ_CD_NM('TNT_WELLS', 'BFSVC_CSMB_DDLV_TP_CD', D2.BFSVC_CSMB_DDLV_TP_CD, #{session.langId}) END AS BLD /* 빌딩 배부유형 */ , D1.BFSVC_CSMB_DDLV_QTY /* 배부수량 */ , D1.MNGT_YM /* 관리년월 */ FROM TB_SVST_BFSVC_CSMB_DDLV_IZ D1 /* BS소모품배부내역 */ LEFT OUTER JOIN TB_SVST_BFSVC_CSMB_BASE_DTL D2 /* BS소모품기준상세 */ ON D2.MNGT_YM = D1.MNGT_YM AND D2.CSMB_PD_CD = D1.CSMB_PD_CD AND D2.BFSVC_CSMB_DDLV_OJ_CD = D1.BFSVC_CSMB_DDLV_OJ_CD AND D2.DTA_DL_YN = 'N' AND D2.MNGT_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo} WHERE D1.DTA_DL_YN = 'N' AND D1.BFSVC_CSMB_DDLV_STAT_CD = '30' AND D1.MNGT_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo}
AND D1.CSMB_PD_CD = #{itmCd}
AND D1.BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
AND D1.SAP_MAT_CD >= LPAD(#{sapCdFrom}, 18, '0')
AND D1.SAP_MAT_CD <= LPAD(#{sapCdTo}, 18, '0')
) T INNER JOIN TB_OGBS_BLD_BAS BLD /* 빌딩기본 */ ON BLD.BLD_CD = T.BLD_CD WHERE BLD.DTA_DL_YN = 'N' AND BLD.OG_TP_CD = 'W02'
AND BLD.BLD_CD IN
#{item}
GROUP BY T.BLD_CD, BLD.BLD_NM, T.SAP_MAT_CD, T.CSMB_PD_CD ) T1 INNER JOIN TB_PDBS_PD_BAS T2 /* 상품기본 */ ON T2.PD_CD = T1.CSMB_PD_CD LEFT OUTER JOIN ( SELECT /*+ USE_HASH(D1 D2 D3) */ D7.BLD_CD , COUNT(1) AS VST_ACC_SUM /* 방문계정수 */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '1' AND D4.COPN_DV_CD = '1' THEN 1 END) AS BDT_INDV /* 비데(개인) */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '1' AND D4.COPN_DV_CD = '2' THEN 1 END) AS BDT_CRP /* 비데(법인) */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '2' THEN 1 END) AS WRFR /* 정수기 */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '3' AND D4.COPN_DV_CD = '1' THEN 1 END) AS ARCLE_INDV /* 공기청정기(개인) */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '3' AND D4.COPN_DV_CD = '2' THEN 1 END) AS ARCLE_CRP /* 공기청정기(법인) */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '4' THEN 1 END) AS WTR_SFTNR /* 연수기 */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '93' THEN 1 END) AS MSGCR /* 안마의자 */ , COUNT(CASE WHEN D5.PD_PRP_VAL20 = '96' THEN 1 END) AS CFF_MCHN /* 커피머신 */ , COUNT(CASE WHEN D5.PD_CD IN ('WM04100121' , 'WM04100152' , 'WM04100153') THEN 1 END) AS DRYR /* 건조기 */ , COUNT(CASE WHEN D5.PD_CD IN ('WM04100127' , 'WM04100155' , 'WM04100156') THEN 1 END) AS WASH /* 세탁기 */ , COUNT(CASE WHEN D5.PD_CD IN ('WM04100164', 'WM04100166', 'WM04100192', 'WM04100193', 'WM04100194', 'WM07101222') THEN 1 END) AS ARDRSSR /* 에어드레서 */ , COUNT(CASE WHEN D5.PD_CD IN ('WM04100200', 'WM04100201', 'WM04100202') THEN 1 END) AS SSCLING /* 삼성청소기 */ FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ D1 /* 고객서비스BS배정내역 */ INNER JOIN TB_SVPD_CST_SV_BFSVC_OJ_IZ D2 /* 고객서비스BS대상내역 */ ON D2.ASN_OJ_YM = D1.ASN_OJ_YM AND D2.CST_SV_ASN_NO = D1.CST_SV_ASN_NO INNER JOIN TB_SVPD_CST_SV_EXCN_IZ D3 /* 고객서비스수행내역 */ ON D3.CNTR_NO = D1.CNTR_NO AND D3.CNTR_SN = D1.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS D4 /* 계약기본 */ ON D4.CNTR_NO = D1.CNTR_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D5 /* 상품각사속성상세 */ ON D5.PD_CD = D2.PDCT_PD_CD INNER JOIN TB_OGBS_MM_PRTNR_IZ D6 /* 월파트너내역 */ ON D6.BASE_YM = D1.ASN_OJ_YM AND D6.OG_TP_CD = D1.CNFM_PSIC_PRTNR_OG_TP_CD AND D6.PRTNR_NO = D1.CNFM_PSIC_PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ D7 /* 월조직내역 */ ON D7.BASE_YM = D6.BASE_YM AND D7.OG_TP_CD = D6.OG_TP_CD AND D7.OG_ID = D6.OG_ID WHERE D1.DTA_DL_YN = 'N' AND D1.CNFM_PSIC_PRTNR_OG_TP_CD = 'W02' AND D2.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D4.DTA_DL_YN = 'N' AND D5.DTA_DL_YN = 'N' AND D5.PD_EXTS_PRP_GRP_CD = 'PART' AND D5.PD_PRP_VAL20 <> '11' /* 모종제외 */ AND D6.DTA_DL_YN = 'N' AND D6.OG_TP_CD = 'W02' AND D7.DTA_DL_YN = 'N' AND D7.OG_TP_CD = 'W02' AND ( D1.VST_PRGS_STAT_CD = '20' OR ( D1.VST_PRGS_STAT_CD = '00' AND D3.CNTR_DTL_STAT_CD = '101' ) ) /* 작업완료이거나 정상인 작업대기 건 */ AND D1.ASN_OJ_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo} AND D2.ASN_OJ_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo} AND D6.BASE_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo} AND D7.BASE_YM BETWEEN #{mngtYmFrom} AND #{mngtYmTo} GROUP BY D7.BLD_CD ) T3 ON T3.BLD_CD = T1.BLD_CD WHERE T2.DTA_DL_YN = 'N' AND T2.PD_TP_CD = 'M' ORDER BY T1.BLD_NM, T1.CSMB_PD_CD
