mapper.select:
SELECT D1.MNGT_YM /* 관리년월 */ , NVL(D5.GO_DV_CD, D1.GO_DV_CD) AS GO_DV_CD /* 발주구분코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'GO_DV_CD', NVL(D5.GO_DV_CD, D1.GO_DV_CD), #{session.langId}) AS GO_DV_NM /* 발주구분 */ , TO_CHAR(TO_NUMBER(D2.SAP_MAT_CD)) AS SVPD_SAP_CD /* SAP코드 */ , D1.CSMB_PD_CD /* 소모품상품코드 */ , D2.PD_NM AS ITM_KNM /* 품목한글명 */ , NVL(D5.MNGT_UNIT_CD, D1.MNGT_UNIT_CD) AS MNGT_UNIT_CD /* 관리단위코드 */ , NVL(D5.MMS6B_DDLV_QTY, NVL(D4.MMS6B_DDLV_QTY, 0)) AS MMS6B_DDLV_QTY /* 6개월 이전 배부수량 */ , NVL(D5.MMS5B_DDLV_QTY, NVL(D4.MMS5B_DDLV_QTY, 0)) AS MMS5B_DDLV_QTY /* 5개월 이전 배부수량 */ , NVL(D5.MMS4B_DDLV_QTY, NVL(D4.MMS4B_DDLV_QTY, 0)) AS MMS4B_DDLV_QTY /* 4개월 이전 배부수량 */ , NVL(D5.MMS3B_DDLV_QTY, NVL(D4.MMS3B_DDLV_QTY, 0)) AS MMS3B_DDLV_QTY /* 3개월 이전 배부수량 */ , NVL(D5.MMS2B_DDLV_QTY, NVL(D4.MMS2B_DDLV_QTY, 0)) AS MMS2B_DDLV_QTY /* 2개월 이전 배부수량 */ , NVL(D5.MMS1B_DDLV_QTY, NVL(D4.MMS1B_DDLV_QTY, 0)) AS MMS1B_DDLV_QTY /* 1개월 이전 배부수량 */ , FLOOR( ( NVL(D5.MMS6B_DDLV_QTY, NVL(D4.MMS6B_DDLV_QTY, 0)) + NVL(D5.MMS5B_DDLV_QTY, NVL(D4.MMS5B_DDLV_QTY, 0)) + NVL(D5.MMS4B_DDLV_QTY, NVL(D4.MMS4B_DDLV_QTY, 0)) + NVL(D5.MMS3B_DDLV_QTY, NVL(D4.MMS3B_DDLV_QTY, 0)) + NVL(D5.MMS2B_DDLV_QTY, NVL(D4.MMS2B_DDLV_QTY, 0)) + NVL(D5.MMS1B_DDLV_QTY, NVL(D4.MMS1B_DDLV_QTY, 0)) ) / 6 ) AS MM_AV_DDLV_QTY /* 월평균 배부수량 */ , NVL(D5.STR_STNB_QTY, 0) AS STR_STNB_QTY /* 입고대기 */ , NVL(D5.GO_UPRC, NVL(D1.GO_UPRC, 0)) AS GO_UPRC /* 발주단가 */ , NVL(D5.MIN_ORD_QTY, NVL(TO_NUMBER(REGEXP_REPLACE(D3.PD_PRP_VAL07, '[^0-9]', '')), 0)) AS MIN_ORD_QTY /* 최소주문수량 */ , NVL(D5.PYPD_DC, NVL(TO_NUMBER(D3.PD_PRP_VAL06), 0)) AS PYPD_DC /* 납기일수 */ , NVL(D5.RMK_CN, D1.RMK_CN) AS RMK_CN /* 비고내용 */ , CASE WHEN D5.CSMB_PD_CD IS NOT NULL THEN 'Y' ELSE 'N' END AS RGST_YN /* 등록여부 */ , NVL(D5.PAJU_LGST_CNR_STOC_QTY, 0) AS PAJU_LGST_CNR_STOC_QTY /* 파주물류센터수량 */ , CASE WHEN #{sgsuExcludeYn} = '1' THEN 0 ELSE NVL(D5.SGSU_LGST_CNR_STOC_QTY, 0) END AS SGSU_LGST_CNR_STOC_QTY /* 성수물류센터재고수량 */ , CASE WHEN #{sgsuExcludeYn} = '1' THEN NVL(D5.WO_STOC_QTY, 0) - NVL(D5.SGSU_LGST_CNR_STOC_QTY, 0) ELSE NVL(D5.WO_STOC_QTY, 0) END AS WO_STOC_QTY /* 전체재고수량 */ , NVL(D5.STOC_PERS_MM_N, 0) AS STOC_PERS_MM_N /* 재고지속성월수 */ , D5.ET_EXS_DT /* 예상소진일자 */ , NVL(D5.NCST_QTY, 0) AS NCST_QTY /* 필요수량 */ , NVL(D5.GO_QTY, 0) AS GO_QTY /* 발주수량 */ , NVL(D5.GO_AMT, 0) AS GO_AMT /* 발주금액 */ FROM TB_SVST_BFSVC_CSMB_BASE_BAS D1 /* BS소모품기준기본 */ INNER JOIN TB_PDBS_PD_BAS D2 /* 상품기본 */ ON D2.PD_CD = D1.CSMB_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D3 /* 상품각사속성상세 */ ON D3.PD_CD = D2.PD_CD LEFT OUTER JOIN ( SELECT CSMB_PD_CD , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -6), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS6B_DDLV_QTY /* 6개월 이전 배부수량 */ , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -5), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS5B_DDLV_QTY /* 5개월 이전 배부수량 */ , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -4), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS4B_DDLV_QTY /* 4개월 이전 배부수량 */ , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -3), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS3B_DDLV_QTY /* 3개월 이전 배부수량 */ , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -2), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS2B_DDLV_QTY /* 2개월 이전 배부수량 */ , SUM(CASE WHEN MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -1), 'YYYYMM') THEN BFSVC_CSMB_DDLV_QTY END) AS MMS1B_DDLV_QTY /* 1개월 이전 배부수량 */ FROM TB_SVST_BFSVC_CSMB_DDLV_IZ /* BS소모품배부내역 */ WHERE DTA_DL_YN = 'N' AND BFSVC_CSMB_DDLV_STAT_CD = '30' /* 출고요청완료 */ AND MNGT_YM BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -6), 'YYYYMM') AND TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -1), 'YYYYMM') GROUP BY CSMB_PD_CD ) D4 ON D4.CSMB_PD_CD = D1.CSMB_PD_CD LEFT OUTER JOIN TB_SVST_BFSVC_CSMB_GQES_IZ D5 /* BS소모품발주수량산출내역 */ ON D5.MNGT_YM = D1.MNGT_YM AND D5.CSMB_PD_CD = D1.CSMB_PD_CD AND D5.DTA_DL_YN = 'N' WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.PD_TP_CD = 'M' AND D3.DTA_DL_YN = 'N' AND D3.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.MNGT_YM = #{mngtYm}
AND D3.PD_PRP_VAL19 = #{itmKndCd}
AND D1.GO_DV_CD = #{goDvCd}
AND D1.CSMB_PD_CD = #{csmbPdCd}
AND D1.CSMB_PD_CD >= #{csmbPdCdFrom}
AND D1.CSMB_PD_CD <= #{csmbPdCdTo}
AND D2.SAP_MAT_CD >= LPAD(#{sapMatCdFrom}, 18, '0')
AND D2.SAP_MAT_CD <= LPAD(#{sapMatCdTo}, 18, '0')
ORDER BY D1.CSMB_PD_CD

SELECT D1.MNGT_YM /* 관리년월 */ , D1.GO_DV_CD /* 발주구분코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'GO_DV_CD', D1.GO_DV_CD, #{session.langId}) AS GO_DV_NM /* 발주구분 */ , TO_CHAR(TO_NUMBER(D2.SAP_MAT_CD)) AS SVPD_SAP_CD /* SAP코드 */ , D1.CSMB_PD_CD /* 소모품상품코드 */ , D1.ITM_KNM /* 품목한글명 */ , D1.MNGT_UNIT_CD /* 관리단위코드 */ , NVL(D1.MMS6B_DDLV_QTY, 0) AS MMS6B_DDLV_QTY /* 6개월전배부수량 */ , NVL(D1.MMS5B_DDLV_QTY, 0) AS MMS5B_DDLV_QTY /* 5개월전배부수량 */ , NVL(D1.MMS4B_DDLV_QTY, 0) AS MMS4B_DDLV_QTY /* 4개월전배부수량 */ , NVL(D1.MMS3B_DDLV_QTY, 0) AS MMS3B_DDLV_QTY /* 3개월전배부수량 */ , NVL(D1.MMS2B_DDLV_QTY, 0) AS MMS2B_DDLV_QTY /* 2개월전배부수량 */ , NVL(D1.MMS1B_DDLV_QTY, 0) AS MMS1B_DDLV_QTY /* 1개월전배부수량 */ , NVL(D1.STR_STNB_QTY, 0) AS STR_STNB_QTY /* 입고대기수량 */ , NVL(D1.PAJU_LGST_CNR_STOC_QTY, 0) AS PAJU_LGST_CNR_STOC_QTY /* 파주물류센터재고수량 */ , CASE WHEN #{sgsuExcludeYn} = '1' THEN 0 ELSE NVL(D1.SGSU_LGST_CNR_STOC_QTY, 0) END AS SGSU_LGST_CNR_STOC_QTY /* 성수물류센터재고수량 */ , CASE WHEN #{sgsuExcludeYn} = '1' THEN NVL(D1.WO_STOC_QTY, 0) - NVL(D1.SGSU_LGST_CNR_STOC_QTY, 0) ELSE NVL(D1.WO_STOC_QTY, 0) END AS WO_STOC_QTY /* 전체재고수량 */ , NVL(D1.STOC_PERS_MM_N, 0) AS STOC_PERS_MM_N /* 재고지속월수 */ , D1.ET_EXS_DT /* 예상소진일자 */ , NVL(D1.GO_UPRC, 0) AS GO_UPRC /* 발주단가 */ , NVL(D1.NCST_QTY, 0) AS NCST_QTY /* 필요수량 */ , NVL(D1.GO_QTY, 0) AS GO_QTY /* 발주수량 */ , NVL(D1.GO_AMT, 0) AS GO_AMT /* 발주금액 */ , NVL(D1.MIN_ORD_QTY, 0) AS MIN_ORD_QTY /* 최소주문수량 */ , NVL(D1.PYPD_DC, 0) AS PYPD_DC /* 납기일수 */ , D1.RMK_CN /* 비고내용 */ FROM TB_SVST_BFSVC_CSMB_GQES_IZ D1 /* BS소모품발주수량산출내역 */ INNER JOIN TB_PDBS_PD_BAS D2 /* 상품기본 */ ON D2.PD_CD = D1.CSMB_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D3 /* 상품각사속성상세 */ ON D3.PD_CD = D2.PD_CD WHERE D2.DTA_DL_YN = 'N' AND D2.PD_TP_CD = 'M' AND D3.DTA_DL_YN = 'N' AND D3.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.MNGT_YM = #{mngtYm}
AND D3.PD_PRP_VAL19 = #{itmKndCd}
AND D1.GO_DV_CD = #{goDvCd}
AND D1.CSMB_PD_CD = #{csmbPdCd}
AND D1.CSMB_PD_CD >= #{csmbPdCdFrom}
AND D1.CSMB_PD_CD <= #{csmbPdCdTo}
AND D2.SAP_MAT_CD >= LPAD(#{sapMatCdFrom}, 18, '0')
AND D2.SAP_MAT_CD <= LPAD(#{sapMatCdTo}, 18, '0')
ORDER BY D1.CSMB_PD_CD

mapper.insert:
MERGE INTO TB_SVST_BFSVC_CSMB_GQES_IZ TB_TGT /* BS소모품발주수량산출내역 */ USING ( SELECT #{mngtYm} AS MNGT_YM /* 관리년월 */ , #{csmbPdCd} AS CSMB_PD_CD /* 소모품상품코드 */ , #{itmKnm} AS ITM_KNM /* 품목한글명 */ , #{mngtUnitCd} AS MNGT_UNIT_CD /* 관리단위코드 */ , #{goDvCd} AS GO_DV_CD /* 발주구분코드 */ , #{mms6bDdlvQty} AS MMS6B_DDLV_QTY /* 6개월전배부수량 */ , #{mms5bDdlvQty} AS MMS5B_DDLV_QTY /* 5개월전배부수량 */ , #{mms4bDdlvQty} AS MMS4B_DDLV_QTY /* 4개월전배부수량 */ , #{mms3bDdlvQty} AS MMS3B_DDLV_QTY /* 3개월전배부수량 */ , #{mms2bDdlvQty} AS MMS2B_DDLV_QTY /* 2개월전배부수량 */ , #{mms1bDdlvQty} AS MMS1B_DDLV_QTY /* 1개월전배부수량 */ , #{mmAvDdlvQty} AS MM_AV_DDLV_QTY /* 월평균배부수량 */ , #{strStnbQty} AS STR_STNB_QTY /* 입고대기수량 */ , #{pajuLgstCnrStocQty} AS PAJU_LGST_CNR_STOC_QTY /* 파주물류센터재고수량 */ , #{sgsuLgstCnrStocQty} AS SGSU_LGST_CNR_STOC_QTY /* 성수물류센터재고수량 */ , #{woStocQty} AS WO_STOC_QTY /* 전체재고수량 */ , #{stocPersMmN} AS STOC_PERS_MM_N /* 재고지속성월수 */ , #{etExsDt} AS ET_EXS_DT /* 예상소진일자 */ , #{goUprc} AS GO_UPRC /* 발주단가 */ , #{minOrdQty} AS MIN_ORD_QTY /* 최소주문수량 */ , #{pypdDc} AS PYPD_DC /* 납기일수 */ , #{ncstQty} AS NCST_QTY /* 필요수량 */ , #{goQty} AS GO_QTY /* 발주수량 */ , #{goAmt} AS GO_AMT /* 발주금액 */ , #{rmkCn} AS RMK_CN /* 비고내용 */ FROM DUAL ) TB_SRC ON ( TB_TGT.MNGT_YM = TB_SRC.MNGT_YM AND TB_TGT.CSMB_PD_CD = TB_SRC.CSMB_PD_CD ) WHEN MATCHED THEN UPDATE SET TB_TGT.ITM_KNM = TB_SRC.ITM_KNM , TB_TGT.MNGT_UNIT_CD = TB_SRC.MNGT_UNIT_CD , TB_TGT.GO_DV_CD = TB_SRC.GO_DV_CD , TB_TGT.MMS6B_DDLV_QTY = TB_SRC.MMS6B_DDLV_QTY , TB_TGT.MMS5B_DDLV_QTY = TB_SRC.MMS5B_DDLV_QTY , TB_TGT.MMS4B_DDLV_QTY = TB_SRC.MMS4B_DDLV_QTY , TB_TGT.MMS3B_DDLV_QTY = TB_SRC.MMS3B_DDLV_QTY , TB_TGT.MMS2B_DDLV_QTY = TB_SRC.MMS2B_DDLV_QTY , TB_TGT.MMS1B_DDLV_QTY = TB_SRC.MMS1B_DDLV_QTY , TB_TGT.MM_AV_DDLV_QTY = TB_SRC.MM_AV_DDLV_QTY , TB_TGT.STR_STNB_QTY = TB_SRC.STR_STNB_QTY , TB_TGT.PAJU_LGST_CNR_STOC_QTY = TB_SRC.PAJU_LGST_CNR_STOC_QTY , TB_TGT.SGSU_LGST_CNR_STOC_QTY = TB_SRC.SGSU_LGST_CNR_STOC_QTY , TB_TGT.WO_STOC_QTY = TB_SRC.WO_STOC_QTY , TB_TGT.STOC_PERS_MM_N = TB_SRC.STOC_PERS_MM_N , TB_TGT.ET_EXS_DT = TB_SRC.ET_EXS_DT , TB_TGT.GO_UPRC = TB_SRC.GO_UPRC , TB_TGT.MIN_ORD_QTY = TB_SRC.MIN_ORD_QTY , TB_TGT.PYPD_DC = TB_SRC.PYPD_DC , TB_TGT.NCST_QTY = TB_SRC.NCST_QTY , TB_TGT.GO_QTY = TB_SRC.GO_QTY , TB_TGT.GO_AMT = TB_SRC.GO_AMT , TB_TGT.RMK_CN = TB_SRC.RMK_CN
WHEN NOT MATCHED THEN INSERT ( TB_TGT.MNGT_YM , TB_TGT.CSMB_PD_CD , TB_TGT.ITM_KNM , TB_TGT.MNGT_UNIT_CD , TB_TGT.GO_DV_CD , TB_TGT.MMS6B_DDLV_QTY , TB_TGT.MMS5B_DDLV_QTY , TB_TGT.MMS4B_DDLV_QTY , TB_TGT.MMS3B_DDLV_QTY , TB_TGT.MMS2B_DDLV_QTY , TB_TGT.MMS1B_DDLV_QTY , TB_TGT.MM_AV_DDLV_QTY , TB_TGT.STR_STNB_QTY , TB_TGT.PAJU_LGST_CNR_STOC_QTY , TB_TGT.SGSU_LGST_CNR_STOC_QTY , TB_TGT.WO_STOC_QTY , TB_TGT.STOC_PERS_MM_N , TB_TGT.ET_EXS_DT , TB_TGT.GO_UPRC , TB_TGT.MIN_ORD_QTY , TB_TGT.PYPD_DC , TB_TGT.NCST_QTY , TB_TGT.GO_QTY , TB_TGT.GO_AMT , TB_TGT.RMK_CN , TB_TGT.DTA_DL_YN
) VALUES ( TB_SRC.MNGT_YM , TB_SRC.CSMB_PD_CD , TB_SRC.ITM_KNM , TB_SRC.MNGT_UNIT_CD , TB_SRC.GO_DV_CD , TB_SRC.MMS6B_DDLV_QTY , TB_SRC.MMS5B_DDLV_QTY , TB_SRC.MMS4B_DDLV_QTY , TB_SRC.MMS3B_DDLV_QTY , TB_SRC.MMS2B_DDLV_QTY , TB_SRC.MMS1B_DDLV_QTY , TB_SRC.MM_AV_DDLV_QTY , TB_SRC.STR_STNB_QTY , TB_SRC.PAJU_LGST_CNR_STOC_QTY , TB_SRC.SGSU_LGST_CNR_STOC_QTY , TB_SRC.WO_STOC_QTY , TB_SRC.STOC_PERS_MM_N , TB_SRC.ET_EXS_DT , TB_SRC.GO_UPRC , TB_SRC.MIN_ORD_QTY , TB_SRC.PYPD_DC , TB_SRC.NCST_QTY , TB_SRC.GO_QTY , TB_SRC.GO_AMT , TB_SRC.RMK_CN , 'N'
)
