mapper.select:
SELECT M1.BFSVC_CSMB_DDLV_STAT_CD /* BS소모품배부상태코드 */ , M1.REQ_YN /* 신청여부 */ , M1.BLD_CD /* 빌딩코드 */ , M1.BLD_NM /* 빌딩명 */ , M1.RSPP_PRTNR_NO /* 책임자파트너번호 */ , M1.VST_CST_N /* 방문계정수 */ , ${pivotColumns} /* PIVOT 컬럼 */ FROM (SELECT /*+ ORDERED USE_HASH(T1 T2) */ (SELECT MAX(X.BFSVC_CSMB_DDLV_STAT_CD) FROM TB_SVST_BFSVC_CSMB_DDLV_IZ X WHERE X.MNGT_YM = #{mngtYm} AND X.BFSVC_CSMB_DDLV_OJ_CD = '3' AND X.STR_WARE_NO = O3.BLD_CD ) AS BFSVC_CSMB_DDLV_STAT_CD , (SELECT CASE WHEN MAX(X.BFSVC_CSMB_DDLV_STAT_CD) = '10' THEN '신청' WHEN MAX(X.BFSVC_CSMB_DDLV_STAT_CD) = '20' THEN '신청확인' WHEN MAX(X.BFSVC_CSMB_DDLV_STAT_CD) = '30' THEN '출고요청완료' ELSE '미신청' END FROM TB_SVST_BFSVC_CSMB_DDLV_IZ X WHERE X.MNGT_YM = #{mngtYm} AND X.BFSVC_CSMB_DDLV_OJ_CD = '3' AND X.STR_WARE_NO = O3.BLD_CD ) AS REQ_YN , O3.BLD_CD , O3.BLD_NM , O3.RSPP_PRTNR_NO , COUNT(1) AS VST_CST_N FROM TB_SVPD_MCBY_CST_SV_OJ_IZ T1 INNER JOIN TB_SVPD_CST_SV_EXCN_IZ T2 ON T2.CNTR_NO = T1.CNTR_NO AND T2.CNTR_SN = T1.CNTR_SN AND T2.MNGT_PRTNR_OG_TP_CD = T1.MNGT_PRTNR_OG_TP_CD AND T2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON O1.BASE_YM = T1.MNGT_YM AND O1.OG_TP_CD = T2.MNGT_PRTNR_OG_TP_CD AND O1.PRTNR_NO = T2.MNGT_PRTNR_NO AND O1.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_MM_OG_IZ O2 ON O2.BASE_YM = O1.BASE_YM AND O2.OG_TP_CD = O1.OG_TP_CD AND O2.OG_ID = O1.OG_ID AND O2.CLO_YN = 'N' AND O2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_BLD_BAS O3 ON O3.OG_TP_CD = O2.OG_TP_CD AND O3.BLD_CD = O2.BLD_CD AND O3.DTA_DL_YN = 'N' WHERE T1.DTA_DL_YN = 'N' AND T1.MNGT_PRTNR_OG_TP_CD = 'W02' AND T1.CNTR_DTL_STAT_CD NOT IN ('401','402') AND T1.MNGT_YM = #{mngtYm}
AND O3.BLD_CD IN ( #{bldCd} )
GROUP BY O3.BLD_CD, O3.BLD_NM, O3.RSPP_PRTNR_NO ) M1 LEFT OUTER JOIN LATERAL (SELECT * FROM (SELECT NVL(V2.STR_WARE_NO, V1.STR_WARE_NO) AS STR_WARE_NO , NVL(V2.SAP_MAT_CD, V1.SAP_MAT_CD) AS SAP_MAT_CD , NVL(V2.BFSVC_CSMB_DDLV_QTY, V1.BFSVC_CSMB_DDLV_UNIT_QTY) AS BFSVC_CSMB_DDLV_UNIT_QTY FROM (SELECT D2.SORT_ODR , D2.BFSVC_CSMB_DDLV_TP_CD , '미신청' AS REQ_YN , D5.BLD_CD AS STR_WARE_NO , D2.CSMB_PD_CD , D3.SAP_MAT_CD , CASE WHEN D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '2' AND D2.BFSVC_CSMB_DDLV_UNIT_ACC_N != 0 THEN NVL(D2.BFSVC_CSMB_DDLV_UNIT_QTY, 0) WHEN D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '1' AND D2.BFSVC_CSMB_DDLV_UNIT_ACC_N != 0 THEN NVL( CEIL( SUM(D4.CNT) / D2.BFSVC_CSMB_DDLV_UNIT_ACC_N * D2.BFSVC_CSMB_DDLV_UNIT_QTY), 0) ELSE 0 END BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_OGBS_MM_OG_IZ D1 INNER JOIN TB_SVST_BFSVC_CSMB_BASE_DTL D2 ON D2.MNGT_YM = D1.BASE_YM INNER JOIN TB_PDBS_PD_BAS D3 ON D3.PD_CD = D2.CSMB_PD_CD LEFT OUTER JOIN ( SELECT /*+ ORDERED USE_HASH(S1 S2) */ O3.BLD_CD , S3.COPN_DV_CD , S4.PD_PRP_VAL20 , COUNT(1) AS CNT FROM TB_SVPD_CST_SV_EXCN_IZ S1 INNER JOIN TB_SVPD_MCBY_CST_SV_OJ_IZ S2 ON S2.CNTR_NO = S1.CNTR_NO AND S2.CNTR_SN = S1.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS S3 ON S3.CNTR_NO = S1.CNTR_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S4 ON S4.PD_CD = S2.PDCT_PD_CD INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON O1.BASE_YM = #{mngtYm} AND O1.OG_TP_CD = S1.MNGT_PRTNR_OG_TP_CD AND O1.PRTNR_NO = S1.MNGT_PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ O2 ON O2.BASE_YM = O1.BASE_YM AND O2.OG_ID = O1.OG_ID INNER JOIN TB_OGBS_BLD_BAS O3 ON O3.OG_TP_CD = O1.OG_TP_CD AND O3.BLD_CD = O2.BLD_CD WHERE S1.DTA_DL_YN = 'N' AND S1.MNGT_PRTNR_OG_TP_CD = 'W02' AND S1.CNTR_DTL_STAT_CD NOT IN ('401', '402') AND S2.DTA_DL_YN = 'N' AND S2.MNGT_YM = #{mngtYm} AND S3.DTA_DL_YN = 'N' AND S4.DTA_DL_YN = 'N' AND S4.PD_EXTS_PRP_GRP_CD = 'PART' AND O1.DTA_DL_YN = 'N' AND O2.DTA_DL_YN = 'N' GROUP BY O3.BLD_CD, S3.COPN_DV_CD, S4.PD_PRP_VAL20 ) D4 ON D4.BLD_CD = D1.BLD_CD AND D4.COPN_DV_CD = NVL(D2.BFSVC_CSMB_DDLV_OJ_ACC_TP_CD, D4.COPN_DV_CD) AND D4.PD_PRP_VAL20 = NVL(D2.BFSVC_CSMB_DDLV_OJ_PD_GRP_CD, D4.PD_PRP_VAL20) INNER JOIN TB_OGBS_BLD_BAS D5 ON D5.OG_TP_CD = 'W02' AND D5.BLD_CD = D1.BLD_CD WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D3.PD_TP_CD = 'M' AND D1.BASE_YM = #{mngtYm} AND D1.OG_TP_CD = 'W02' AND D2.MNGT_YM = #{mngtYm} AND D2.BFSVC_CSMB_DDLV_OJ_CD = '3' AND D2.BFSVC_CSMB_DDLV_TP_CD = '1' AND D2.BFSVC_CSMB_DDLV_ORT_YN = 'Y' GROUP BY D2.SORT_ODR, D2.BFSVC_CSMB_DDLV_TP_CD, D5.BLD_CD, D2.CSMB_PD_CD, D3.SAP_MAT_CD, D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD, NVL(D2.BFSVC_CSMB_DDLV_UNIT_QTY, 0), D2.BFSVC_CSMB_DDLV_UNIT_ACC_N, D2.BFSVC_CSMB_DDLV_UNIT_QTY UNION ALL SELECT /*+ USE_HASH(T1 O1 O2 O3) */ T1.SORT_ODR , T1.BFSVC_CSMB_DDLV_TP_CD , '미신청' AS REQ_YN , O3.BLD_CD AS STR_WARE_NO , T1.CSMB_PD_CD , (SELECT Y.SAP_MAT_CD FROM TB_PDBS_PD_BAS Y WHERE Y.PD_CD = T1.CSMB_PD_CD ) AS SAP_MAT_CD , 0 AS BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL T1 INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON O1.BASE_YM = T1.MNGT_YM AND O1.OG_TP_CD = 'W02' INNER JOIN TB_OGBS_MM_OG_IZ O2 ON O2.BASE_YM = O1.BASE_YM AND O2.OG_ID = O1.OG_ID INNER JOIN TB_OGBS_BLD_BAS O3 ON O3.OG_TP_CD = O2.OG_TP_CD AND O3.BLD_CD = O2.BLD_CD WHERE T1.BFSVC_CSMB_DDLV_OJ_CD = '3' AND T1.BFSVC_CSMB_DDLV_TP_CD = '2' AND T1.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND T1.MNGT_YM = #{mngtYm} AND O1.DTA_DL_YN = 'N' AND O2.DTA_DL_YN = 'N' AND O3.DTA_DL_YN = 'N' ) V1 LEFT OUTER JOIN (SELECT T2.SORT_ODR , T1.STR_WARE_NO , T1.CSMB_PD_CD , T1.SAP_MAT_CD , NVL(T1.BFSVC_CSMB_DDLV_QTY, '0') AS BFSVC_CSMB_DDLV_QTY , T1.BFSVC_CSMB_DDLV_STAT_CD , (CASE WHEN T1.BFSVC_CSMB_DDLV_STAT_CD = '10' THEN '신청' WHEN T1.BFSVC_CSMB_DDLV_STAT_CD = '20' THEN '신청확인' WHEN T1.BFSVC_CSMB_DDLV_STAT_CD = '30' THEN '출고요청완료' ELSE '미신청' END ) AS REQ_YN , T2.BFSVC_CSMB_DDLV_TP_CD FROM TB_SVST_BFSVC_CSMB_DDLV_IZ T1 , TB_SVST_BFSVC_CSMB_BASE_DTL T2 WHERE T1.DTA_DL_YN = 'N' AND T1.BFSVC_CSMB_DDLV_OJ_CD = '3' AND T2.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND T1.MNGT_YM = T2.MNGT_YM AND T1.BFSVC_CSMB_DDLV_OJ_CD = T2.BFSVC_CSMB_DDLV_OJ_CD AND T1.CSMB_PD_CD = T2.CSMB_PD_CD AND T1.MNGT_YM = #{mngtYm} ) V2 ON V2.CSMB_PD_CD = V1.CSMB_PD_CD AND V2.STR_WARE_NO = V1.STR_WARE_NO WHERE 1 = 1 ) A PIVOT (SUM(BFSVC_CSMB_DDLV_UNIT_QTY) FOR SAP_MAT_CD IN (${pivotInStr}) ) WHERE 1 = 1 ) M2 ON M2.STR_WARE_NO = M1.BLD_CD WHERE 1 = 1 ORDER BY M1.BLD_NM

SELECT T1.SORT_ODR , T1.BFSVC_CSMB_DDLV_TP_CD , '미신청' AS REQ_YN , #{strWareNo} AS STR_WARE_NO , T1.CSMB_PD_CD AS FXN_PD_CD , (SELECT SAP_MAT_CD FROM TB_PDBS_PD_BAS WHERE PD_CD = T1.CSMB_PD_CD ) AS FXN_SAP_CD , CASE WHEN T1.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '2' THEN NVL(T1.BFSVC_CSMB_DDLV_UNIT_QTY, 0) WHEN T1.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '1' THEN NVL((CEIL((SELECT COUNT(1) FROM TB_SVPD_MCBY_CST_SV_OJ_IZ A1 INNER JOIN TB_SVPD_CST_SV_EXCN_IZ A2 ON A2.CNTR_NO = A1.CNTR_NO AND A2.CNTR_SN = A1.CNTR_SN AND A2.MNGT_PRTNR_OG_TP_CD = 'W02' INNER JOIN TB_SSCT_CNTR_BAS A3 ON A1.CNTR_NO = A3.CNTR_NO AND A3.COPN_DV_CD = NVL(T1.BFSVC_CSMB_DDLV_OJ_ACC_TP_CD, A3.COPN_DV_CD) AND A3.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL A4 ON A1.CNTR_NO = A4.CNTR_NO AND A1.CNTR_SN = A4.CNTR_SN AND A4.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL A5 ON A1.PDCT_PD_CD = A5.PD_CD AND A5.PD_EXTS_PRP_GRP_CD = 'PART' AND A5.PD_PRP_VAL20 = NVL(T1.BFSVC_CSMB_DDLV_OJ_PD_GRP_CD, A5.PD_PRP_VAL20) AND A5.DTA_DL_YN = 'N' WHERE A1.DTA_DL_YN = 'N' AND A1.MNGT_YM = #{mngtYm} AND A1.CNTR_DTL_STAT_CD NOT IN ('401','402') AND A2.MNGT_PRTNR_NO IN (SELECT Y.PRTNR_NO FROM TB_OGBS_MM_OG_IZ X INNER JOIN TB_OGBS_MM_PRTNR_IZ Y ON Y.BASE_YM = X.BASE_YM AND Y.OG_TP_CD = 'W02' AND Y.OG_ID = X.OG_ID AND Y.DTA_DL_YN = 'N' WHERE X.BASE_YM = #{mngtYm} AND X.BLD_CD = #{strWareNo} AND X.DTA_DL_YN = 'N' ) )/T1.BFSVC_CSMB_DDLV_UNIT_ACC_N) * T1.BFSVC_CSMB_DDLV_UNIT_QTY), 0) END AS FXN_DDLV_UNIT_QTY , '' AS APLC_PD_CD , '' AS APLC_SAP_CD , NULL AS APLC_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL T1 WHERE 1 = 1 AND T1.BFSVC_CSMB_DDLV_OJ_CD = '3' AND T1.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND T1.BFSVC_CSMB_DDLV_TP_CD = '1' AND T1.MNGT_YM = #{mngtYm}
UNION ALL SELECT X.SORT_ODR , X.BFSVC_CSMB_DDLV_TP_CD , '미신청' AS REQ_YN , #{strWareNo} AS STR_WARE_NO , '' AS FXN_PD_CD , '' AS FXN_SAP_CD , NULL AS FXN_DDLV_UNIT_QTY , X.CSMB_PD_CD , (SELECT Y.SAP_MAT_CD FROM TB_PDBS_PD_BAS Y WHERE Y.PD_CD = X.CSMB_PD_CD ) AS APLC_SAP_CD , 0 AS APLC_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL X WHERE X.BFSVC_CSMB_DDLV_OJ_CD = '3' AND X.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND X.BFSVC_CSMB_DDLV_TP_CD = '2' AND X.MNGT_YM = #{mngtYm}
ORDER BY BFSVC_CSMB_DDLV_TP_CD, SORT_ODR

mapper.insert:
MERGE INTO TB_SVST_BIZ_CL_BAS T1 USING (SELECT #{mngtYm} AS MNGT_YM , #{bizStrtdt} AS BIZ_STRTDT , #{bizStrtHh} || '00' AS BIZ_STRT_HH , #{bizEnddt} AS BIZ_ENDDT , #{bizEndHh} || '00' AS BIZ_END_HH FROM DUAL ) T2 ON (T1.MNGT_YM = T2.MNGT_YM AND T1.CL_MNGT_DV_CD = '4' ) WHEN MATCHED THEN UPDATE SET T1.BIZ_STRTDT = T2.BIZ_STRTDT , T1.BIZ_STRT_HH = T2.BIZ_STRT_HH , T1.BIZ_ENDDT = T2.BIZ_ENDDT , T1.BIZ_END_HH = T2.BIZ_END_HH
WHEN NOT MATCHED THEN INSERT ( T1.MNGT_YM , T1.CL_MNGT_DV_CD , T1.BIZ_STRTDT , T1.BIZ_STRT_HH , T1.BIZ_ENDDT , T1.BIZ_END_HH , T1.DTA_DL_YN
) VALUES ( T2.MNGT_YM , '4' , T2.BIZ_STRTDT , T2.BIZ_STRT_HH , T2.BIZ_ENDDT , T2.BIZ_END_HH , 'N'
)

MERGE INTO TB_SVST_BFSVC_CSMB_DDLV_IZ T1 USING (SELECT #{mngtYm} AS MNGT_YM , #{bfsvcCsmbDdlvOjCd} AS BFSVC_CSMB_DDLV_OJ_CD , #{strWareNo} AS STR_WARE_NO , #{csmbPdCd} AS CSMB_PD_CD , #{sapMatCd} AS SAP_MAT_CD , TO_NUMBER(#{bfsvcCsmbDdlvQty}) AS BFSVC_CSMB_DDLV_QTY , #{bfsvcCsmbDdlvStatCd} AS BFSVC_CSMB_DDLV_STAT_CD FROM DUAL ) T2 ON ( T1.MNGT_YM = T2.MNGT_YM AND T1.BFSVC_CSMB_DDLV_OJ_CD = T2.BFSVC_CSMB_DDLV_OJ_CD AND T1.STR_WARE_NO = T2.STR_WARE_NO AND T1.CSMB_PD_CD = T2.CSMB_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.SAP_MAT_CD = T2.SAP_MAT_CD , T1.BFSVC_CSMB_DDLV_QTY = T2.BFSVC_CSMB_DDLV_QTY , T1.BFSVC_CSMB_DDLV_STAT_CD = T2.BFSVC_CSMB_DDLV_STAT_CD
WHEN NOT MATCHED THEN INSERT ( MNGT_YM , BFSVC_CSMB_DDLV_OJ_CD , STR_WARE_NO , CSMB_PD_CD , SAP_MAT_CD , BFSVC_CSMB_DDLV_QTY , BFSVC_CSMB_DDLV_STAT_CD , DTA_DL_YN
) VALUES ( T2.MNGT_YM , T2.BFSVC_CSMB_DDLV_OJ_CD , T2.STR_WARE_NO , T2.CSMB_PD_CD , T2.SAP_MAT_CD , T2.BFSVC_CSMB_DDLV_QTY , T2.BFSVC_CSMB_DDLV_STAT_CD , 'N'
)

mapper.update:
UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET OSTR_AK_NO = #{ostrAkNo}
     , OSTR_AK_SN = #{ostrAkSn}

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO = #{strWareNo}
   AND CSMB_PD_CD = #{csmbPdCd}

UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET BFSVC_CSMB_DDLV_STAT_CD = '30'

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = '3'
   AND STR_WARE_NO = #{strWareNo}
