mapper.select:
SELECT A.BLD_CD , A.BLD_NM , A.RSPP_PRTNR_NO , NVL(C.VST_CST_N, 0) AS VST_CST_N FROM TB_OGBS_BLD_BAS A INNER JOIN (SELECT X.OG_TP_CD , X.BLD_CD FROM TB_OGBS_MM_OG_IZ X WHERE X.BASE_YM = #{mngtYm} AND X.BLD_CD IS NOT NULL AND X.OG_TP_CD = 'W02' AND X.CLO_YN = 'N' AND X.DTA_DL_YN = 'N' ) B ON A.OG_TP_CD = B.OG_TP_CD AND A.BLD_CD = B.BLD_CD LEFT OUTER JOIN (SELECT C.BLD_CD , COUNT(1) AS VST_CST_N FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ A , TB_OGBS_MM_PRTNR_IZ B , TB_OGBS_MM_OG_IZ C WHERE A.ASN_OJ_YM = B.BASE_YM AND A.CNFM_PSIC_PRTNR_OG_TP_CD = B.OG_TP_CD AND A.CNFM_PSIC_PRTNR_NO = B.PRTNR_NO AND B.BASE_YM = C.BASE_YM AND B.OG_TP_CD = C.OG_TP_CD AND B.OG_ID = C.OG_ID AND A.DTA_DL_YN = 'N' AND A.ASN_OJ_YM = #{mngtYm} GROUP BY C.BLD_CD ) C ON A.BLD_CD = C.BLD_CD WHERE A.DTA_DL_YN = 'N'
AND A.BLD_CD IN (
#{bldCd}
)
GROUP BY A.BLD_CD, A.BLD_NM, A.RSPP_PRTNR_NO, C.VST_CST_N

mapper.insert:
MERGE INTO TB_SVST_BIZ_CL_BAS T1 USING (SELECT #{mngtYm} AS MNGT_YM , #{bizStrtdt} AS BIZ_STRTDT , #{bizStrtHh} || '00' AS BIZ_STRT_HH , #{bizEnddt} AS BIZ_ENDDT , #{bizEndHh} || '00' AS BIZ_END_HH FROM DUAL ) T2 ON (T1.MNGT_YM = T2.MNGT_YM AND T1.CL_MNGT_DV_CD = '4' ) WHEN MATCHED THEN UPDATE SET T1.BIZ_STRTDT = T2.BIZ_STRTDT , T1.BIZ_STRT_HH = T2.BIZ_STRT_HH , T1.BIZ_ENDDT = T2.BIZ_ENDDT , T1.BIZ_END_HH = T2.BIZ_END_HH
WHEN NOT MATCHED THEN INSERT ( T1.MNGT_YM , T1.CL_MNGT_DV_CD , T1.BIZ_STRTDT , T1.BIZ_STRT_HH , T1.BIZ_ENDDT , T1.BIZ_END_HH , T1.DTA_DL_YN
) VALUES ( T2.MNGT_YM , '4' , T2.BIZ_STRTDT , T2.BIZ_STRT_HH , T2.BIZ_ENDDT , T2.BIZ_END_HH , 'N'
)

MERGE INTO TB_SVST_BFSVC_CSMB_DDLV_IZ T1 USING ( SELECT #{item.mngtYm} AS MNGT_YM , #{item.bfsvcCsmbDdlvOjCd} AS BFSVC_CSMB_DDLV_OJ_CD , #{item.strWareNo} AS STR_WARE_NO , #{item.csmbPdCd} AS CSMB_PD_CD , #{item.sapMatCd} AS SAP_MAT_CD , TO_NUMBER(#{item.bfsvcCsmbDdlvQty}) AS BFSVC_CSMB_DDLV_QTY , #{item.bfsvcCsmbDdlvStatCd} AS BFSVC_CSMB_DDLV_STAT_CD FROM DUAL
) T2 ON ( T1.MNGT_YM = T2.MNGT_YM AND T1.BFSVC_CSMB_DDLV_OJ_CD = T2.BFSVC_CSMB_DDLV_OJ_CD AND T1.STR_WARE_NO = T2.STR_WARE_NO AND T1.CSMB_PD_CD = T2.CSMB_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.SAP_MAT_CD = T2.SAP_MAT_CD , T1.BFSVC_CSMB_DDLV_QTY = T2.BFSVC_CSMB_DDLV_QTY , T1.BFSVC_CSMB_DDLV_STAT_CD = T2.BFSVC_CSMB_DDLV_STAT_CD
WHEN NOT MATCHED THEN INSERT ( MNGT_YM , BFSVC_CSMB_DDLV_OJ_CD , STR_WARE_NO , CSMB_PD_CD , SAP_MAT_CD , BFSVC_CSMB_DDLV_QTY , BFSVC_CSMB_DDLV_STAT_CD , DTA_DL_YN
) VALUES ( T2.MNGT_YM , T2.BFSVC_CSMB_DDLV_OJ_CD , T2.STR_WARE_NO , T2.CSMB_PD_CD , T2.SAP_MAT_CD , T2.BFSVC_CSMB_DDLV_QTY , T2.BFSVC_CSMB_DDLV_STAT_CD , 'N'
)

mapper.update:
UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET OSTR_AK_NO = #{ostrAkNo}
     , OSTR_AK_SN = #{ostrAkSn}

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO = #{strWareNo}
   AND CSMB_PD_CD = #{csmbPdCd}

UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET BFSVC_CSMB_DDLV_STAT_CD = '30'

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO = #{strWareNo}
   AND CSMB_PD_CD = #{csmbPdCd}
