mapper.insert:
MERGE INTO TB_SVST_BIZ_CL_BAS TB_TGT /* 업무마감기본 */ USING ( SELECT #{mngtYm} AS MNGT_YM /* 관리년월 */ , #{bizStrtdt} AS BIZ_STRTDT /* 업무시작일자 */ , #{bizStrtHh} || '00' AS BIZ_STRT_HH /* 업무시작시간 */ , #{bizEnddt} AS BIZ_ENDDT /* 업무종료일자 */ , #{bizEndHh} || '00' AS BIZ_END_HH /* 업무종료시간 */ FROM DUAL ) TB_SRC ON ( TB_TGT.MNGT_YM = TB_SRC.MNGT_YM AND TB_TGT.CL_MNGT_DV_CD = '4' /* 빌딩BS소모품신청 */ ) WHEN MATCHED THEN UPDATE SET TB_TGT.BIZ_STRTDT = TB_SRC.BIZ_STRTDT , TB_TGT.BIZ_STRT_HH = TB_SRC.BIZ_STRT_HH , TB_TGT.BIZ_ENDDT = TB_SRC.BIZ_ENDDT , TB_TGT.BIZ_END_HH = TB_SRC.BIZ_END_HH
WHEN NOT MATCHED THEN INSERT ( TB_TGT.MNGT_YM /* 관리년월 */ , TB_TGT.CL_MNGT_DV_CD /* 마감관리구분코드 */ , TB_TGT.BIZ_STRTDT /* 업무시작일자 */ , TB_TGT.BIZ_STRT_HH /* 업무시작시간 */ , TB_TGT.BIZ_ENDDT /* 업무종료일자 */ , TB_TGT.BIZ_END_HH /* 업무종료시간 */ , TB_TGT.DTA_DL_YN /* 데이터삭제여부 */
) VALUES ( TB_SRC.MNGT_YM , '4' , TB_SRC.BIZ_STRTDT , TB_SRC.BIZ_STRT_HH , TB_SRC.BIZ_ENDDT , TB_SRC.BIZ_END_HH , 'N'
)

mapper.select:
SELECT T1.BFSVC_CSMB_DDLV_STAT_CD /* BS소모품배부상태코드 */ , T1.REQ_YN /* 신청여부 */ , T1.BLD_CD /* 빌딩코드 */ , T1.BLD_NM /* 빌딩명 */ , T1.RSPP_PRTNR_NO /* 책임자파트너번호 */ , T1.VST_CST_N /* 방문계정수 */ , ${pivotColumns} /* PIVOT 컬럼 */ FROM ( SELECT /*+ ORDERED USE_HASH(D1 D2) */ D6.BFSVC_CSMB_DDLV_STAT_CD , CASE WHEN D6.BFSVC_CSMB_DDLV_STAT_CD IS NULL THEN ( SELECT MLNG_CNTN FROM T_CMZ_MLNG_D WHERE MLNG_ID = 'MSG_TXT_NAPC' /* 미신청 */ AND TENANT_ID = 'TNT_BASE' AND LNG_ID = #{session.langId} ) ELSE F_CMZ_CD_NM('TNT_WELLS', 'BFSVC_CSMB_DDLV_STAT_CD', D6.BFSVC_CSMB_DDLV_STAT_CD, #{session.langId}) END AS REQ_YN , D5.BLD_CD , D5.BLD_NM , D5.RSPP_PRTNR_NO , COUNT(1) AS VST_CST_N FROM TB_SVPD_MCBY_CST_SV_OJ_IZ D1 /* 월별고객서비스대상내역 */ INNER JOIN TB_SVPD_CST_SV_EXCN_IZ D2 /* 고객서비스수행내역 */ ON D2.CNTR_NO = D1.CNTR_NO AND D2.CNTR_SN = D1.CNTR_SN INNER JOIN TB_OGBS_MM_PRTNR_IZ D3 /* 월파트너내역 */ ON D3.BASE_YM = D1.MNGT_YM AND D3.OG_TP_CD = D2.MNGT_PRTNR_OG_TP_CD AND D3.PRTNR_NO = D2.MNGT_PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ D4 /* 월조직내역 */ ON D4.BASE_YM = D3.BASE_YM AND D4.OG_TP_CD = D3.OG_TP_CD AND D4.OG_ID = D3.OG_ID INNER JOIN TB_OGBS_BLD_BAS D5 /* 빌딩기본 */ ON D5.OG_TP_CD = D4.OG_TP_CD AND D5.BLD_CD = D4.BLD_CD LEFT OUTER JOIN ( SELECT STR_WARE_NO , MAX(BFSVC_CSMB_DDLV_STAT_CD) AS BFSVC_CSMB_DDLV_STAT_CD FROM TB_SVST_BFSVC_CSMB_DDLV_IZ /* BS소모품배부내역 */ WHERE DTA_DL_YN = 'N' AND BFSVC_CSMB_DDLV_OJ_CD = '3' AND MNGT_YM = #{mngtYm} GROUP BY STR_WARE_NO ) D6 ON D6.STR_WARE_NO = D5.BLD_CD WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.MNGT_PRTNR_OG_TP_CD = 'W02' AND D2.CNTR_DTL_STAT_CD NOT IN ('401','402') AND D3.DTA_DL_YN = 'N' AND D3.OG_TP_CD = 'W02' AND D4.DTA_DL_YN = 'N' AND D4.OG_TP_CD = 'W02' AND D5.DTA_DL_YN = 'N' AND D5.OG_TP_CD = 'W02' AND D1.MNGT_YM = #{mngtYm} AND D3.BASE_YM = #{mngtYm} AND D4.BASE_YM = #{mngtYm}
AND D5.BLD_CD IN
#{item}
GROUP BY D6.BFSVC_CSMB_DDLV_STAT_CD, D5.BLD_CD, D5.BLD_NM, D5.RSPP_PRTNR_NO ) T1 LEFT OUTER JOIN ( SELECT * FROM ( SELECT NVL(S2.STR_WARE_NO, S1.STR_WARE_NO) AS STR_WARE_NO , TO_CHAR(TO_NUMBER(NVL(S2.SAP_MAT_CD, S1.SAP_MAT_CD))) AS SAP_MAT_CD , NVL(S2.BFSVC_CSMB_DDLV_QTY, S1.BFSVC_CSMB_DDLV_UNIT_QTY) AS BFSVC_CSMB_DDLV_UNIT_QTY FROM ( SELECT D4.BLD_CD AS STR_WARE_NO , D2.CSMB_PD_CD , D3.SAP_MAT_CD , CASE WHEN D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '2' THEN NVL(D2.BFSVC_CSMB_DDLV_UNIT_QTY, 0) WHEN D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '1' AND D2.BFSVC_CSMB_DDLV_UNIT_ACC_N <> 0 THEN NVL( CEIL( SUM(D5.CNT) / D2.BFSVC_CSMB_DDLV_UNIT_ACC_N * D2.BFSVC_CSMB_DDLV_UNIT_QTY), 0) ELSE 0 END AS BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_OGBS_MM_OG_IZ D1 /* 월조직내역 */ INNER JOIN TB_SVST_BFSVC_CSMB_BASE_DTL D2 /* BS소모품기준상세 */ ON D2.MNGT_YM = D1.BASE_YM INNER JOIN TB_PDBS_PD_BAS D3 /* 상품기본 */ ON D3.PD_CD = D2.CSMB_PD_CD INNER JOIN TB_OGBS_BLD_BAS D4 /* 빌딩기본 */ ON D4.OG_TP_CD = D1.OG_TP_CD AND D4.BLD_CD = D1.BLD_CD LEFT OUTER JOIN ( SELECT /*+ ORDERED USE_HASH(S1 S2) */ O3.BLD_CD , S3.COPN_DV_CD , S4.PD_PRP_VAL20 , COUNT(1) AS CNT FROM TB_SVPD_CST_SV_EXCN_IZ S1 INNER JOIN TB_SVPD_MCBY_CST_SV_OJ_IZ S2 ON S2.CNTR_NO = S1.CNTR_NO AND S2.CNTR_SN = S1.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS S3 ON S3.CNTR_NO = S1.CNTR_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S4 ON S4.PD_CD = S2.PDCT_PD_CD INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON O1.BASE_YM = S2.MNGT_YM AND O1.OG_TP_CD = S1.MNGT_PRTNR_OG_TP_CD AND O1.PRTNR_NO = S1.MNGT_PRTNR_NO INNER JOIN TB_OGBS_MM_OG_IZ O2 ON O2.BASE_YM = O1.BASE_YM AND O2.OG_TP_CD = O1.OG_TP_CD AND O2.OG_ID = O1.OG_ID INNER JOIN TB_OGBS_BLD_BAS O3 ON O3.OG_TP_CD = O2.OG_TP_CD AND O3.BLD_CD = O2.BLD_CD WHERE S1.DTA_DL_YN = 'N' AND S1.MNGT_PRTNR_OG_TP_CD = 'W02' AND S1.CNTR_DTL_STAT_CD NOT IN ('401', '402') AND S2.DTA_DL_YN = 'N' AND S3.DTA_DL_YN = 'N' AND S4.DTA_DL_YN = 'N' AND S4.PD_EXTS_PRP_GRP_CD = 'PART' AND O1.DTA_DL_YN = 'N' AND O1.OG_TP_CD = 'W02' AND O2.DTA_DL_YN = 'N' AND O2.OG_TP_CD = 'W02' AND O3.DTA_DL_YN = 'N' AND O3.OG_TP_CD = 'W02' AND S2.MNGT_YM = #{mngtYm} AND O1.BASE_YM = #{mngtYm} AND O2.BASE_YM = #{mngtYm} GROUP BY O3.BLD_CD, S3.COPN_DV_CD, S4.PD_PRP_VAL20 ) D5 ON D5.BLD_CD = D1.BLD_CD AND D5.COPN_DV_CD = NVL(D2.BFSVC_CSMB_DDLV_OJ_ACC_TP_CD, D5.COPN_DV_CD) AND D5.PD_PRP_VAL20 = NVL(D2.BFSVC_CSMB_DDLV_OJ_PD_GRP_CD, D5.PD_PRP_VAL20) WHERE D1.DTA_DL_YN = 'N' AND D1.OG_TP_CD = 'W02' AND D2.DTA_DL_YN = 'N' AND D2.BFSVC_CSMB_DDLV_OJ_CD = '3' AND D2.BFSVC_CSMB_DDLV_TP_CD = '1' AND D2.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND D3.DTA_DL_YN = 'N' AND D3.PD_TP_CD = 'M' AND D4.DTA_DL_YN = 'N' AND D4.OG_TP_CD = 'W02' AND D1.BASE_YM = #{mngtYm} AND D2.MNGT_YM = #{mngtYm} GROUP BY D4.BLD_CD, D2.CSMB_PD_CD, D3.SAP_MAT_CD, D2.BFSVC_CSMB_DDLV_CMPT_BASE_CD , NVL(D2.BFSVC_CSMB_DDLV_UNIT_QTY, 0), D2.BFSVC_CSMB_DDLV_UNIT_ACC_N, D2.BFSVC_CSMB_DDLV_UNIT_QTY UNION ALL SELECT /*+ USE_HASH(D1 D2 D3) */ D3.BLD_CD AS STR_WARE_NO , D1.CSMB_PD_CD , D4.SAP_MAT_CD , 0 AS BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL D1 /* BS소모품기준상세 */ INNER JOIN TB_OGBS_MM_OG_IZ D2 /* 월조직내역 */ ON D2.BASE_YM = D1.MNGT_YM INNER JOIN TB_OGBS_BLD_BAS D3 /* 빌딩기본 */ ON D3.OG_TP_CD = D2.OG_TP_CD AND D3.BLD_CD = D2.BLD_CD INNER JOIN TB_PDBS_PD_BAS D4 /* 상품기본 */ ON D4.PD_CD = D1.CSMB_PD_CD WHERE D1.DTA_DL_YN = 'N' AND D1.BFSVC_CSMB_DDLV_OJ_CD = '3' AND D1.BFSVC_CSMB_DDLV_TP_CD = '2' AND D1.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND D2.DTA_DL_YN = 'N' AND D2.OG_TP_CD = 'W02' AND D3.DTA_DL_YN = 'N' AND D3.OG_TP_CD = 'W02' AND D4.DTA_DL_YN = 'N' AND D4.PD_TP_CD = 'M' AND D1.MNGT_YM = #{mngtYm} AND D2.BASE_YM = #{mngtYm} ) S1 LEFT OUTER JOIN ( SELECT STR_WARE_NO , CSMB_PD_CD , SAP_MAT_CD , NVL(BFSVC_CSMB_DDLV_QTY, 0) AS BFSVC_CSMB_DDLV_QTY FROM TB_SVST_BFSVC_CSMB_DDLV_IZ /* BS소모품배부내역 */ WHERE DTA_DL_YN = 'N' AND BFSVC_CSMB_DDLV_OJ_CD = '3' AND MNGT_YM = #{mngtYm} ) S2 ON S2.STR_WARE_NO = S1.STR_WARE_NO AND S2.CSMB_PD_CD = S1.CSMB_PD_CD ) DDLV PIVOT ( SUM(BFSVC_CSMB_DDLV_UNIT_QTY) FOR SAP_MAT_CD IN (${pivotInStr}) ) ) T2 ON T2.STR_WARE_NO = T1.BLD_CD ORDER BY T1.BLD_NM

mapper.insert:
MERGE INTO TB_SVST_BFSVC_CSMB_DDLV_IZ TB_TGT /* BS소모품배부내역 */ USING ( SELECT #{mngtYm} AS MNGT_YM , #{bfsvcCsmbDdlvOjCd} AS BFSVC_CSMB_DDLV_OJ_CD , #{strWareNo} AS STR_WARE_NO , #{csmbPdCd} AS CSMB_PD_CD , LPAD(#{sapMatCd}, 18, '0') AS SAP_MAT_CD , #{bfsvcCsmbDdlvQty} AS BFSVC_CSMB_DDLV_QTY , #{bfsvcCsmbDdlvStatCd} AS BFSVC_CSMB_DDLV_STAT_CD FROM DUAL ) TB_SRC ON ( TB_TGT.MNGT_YM = TB_SRC.MNGT_YM AND TB_TGT.BFSVC_CSMB_DDLV_OJ_CD = TB_SRC.BFSVC_CSMB_DDLV_OJ_CD AND TB_TGT.STR_WARE_NO = TB_SRC.STR_WARE_NO AND TB_TGT.CSMB_PD_CD = TB_SRC.CSMB_PD_CD ) WHEN MATCHED THEN UPDATE SET TB_TGT.SAP_MAT_CD = TB_SRC.SAP_MAT_CD , TB_TGT.BFSVC_CSMB_DDLV_QTY = TB_SRC.BFSVC_CSMB_DDLV_QTY , TB_TGT.BFSVC_CSMB_DDLV_STAT_CD = TB_SRC.BFSVC_CSMB_DDLV_STAT_CD
WHEN NOT MATCHED THEN INSERT ( TB_TGT.MNGT_YM , TB_TGT.BFSVC_CSMB_DDLV_OJ_CD , TB_TGT.STR_WARE_NO , TB_TGT.CSMB_PD_CD , TB_TGT.SAP_MAT_CD , TB_TGT.BFSVC_CSMB_DDLV_QTY , TB_TGT.BFSVC_CSMB_DDLV_STAT_CD , TB_TGT.DTA_DL_YN
) VALUES ( TB_SRC.MNGT_YM , TB_SRC.BFSVC_CSMB_DDLV_OJ_CD , TB_SRC.STR_WARE_NO , TB_SRC.CSMB_PD_CD , TB_SRC.SAP_MAT_CD , TB_SRC.BFSVC_CSMB_DDLV_QTY , TB_SRC.BFSVC_CSMB_DDLV_STAT_CD , 'N'
)

mapper.update:
UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET OSTR_AK_NO = #{ostrAkNo}
     , OSTR_AK_SN = #{ostrAkSn}

 WHERE MNGT_YM               = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO           = #{strWareNo}
   AND CSMB_PD_CD            = #{csmbPdCd}

UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET BFSVC_CSMB_DDLV_STAT_CD = '30'

 WHERE BFSVC_CSMB_DDLV_OJ_CD = '3'
   AND MNGT_YM               = #{mngtYm}
   AND STR_WARE_NO           = #{strWareNo}
