AND D1.CNTR_SN = D3.CNTR_SN INNER JOIN TB_SSCT_CNTR_DTL D4 /* 계약상세 */ ON D4.CNTR_NO = D3.CNTR_NO AND D4.CNTR_SN = D3.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS D5 /* 계약기본 - 멤버십 */ ON D5.CNTR_NO = D4.CNTR_NO LEFT OUTER JOIN TB_CUBS_CST_BAS D6 /* 고객기본 */ ON D6.CST_NO = D5.CNTR_CST_NO AND D6.DTA_DL_YN = 'N' INNER JOIN TB_PDBS_PD_BAS D7 /* 상품기본 */ ON D1.ITM_PD_CD = D7.PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D8 /* 상품각사속성상세 */ ON D7.PD_CD = D8.PD_CD AND D8.PD_EXTS_PRP_GRP_CD = 'PART' LEFT OUTER JOIN TB_OGBS_OG_BAS D9 /* 조직기본 */ ON D9.OG_TP_CD = D1.OG_TP_CD AND D9.OG_ID = D1.DGR3_LEVL_OG_ID AND D9.DTA_DL_YN = 'N' WHERE 1 = 1 AND D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D4.DTA_DL_YN = 'N' AND D5.DTA_DL_YN = 'N' AND D7.DTA_DL_YN = 'N' AND D8.DTA_DL_YN = 'N' AND D8.PD_PRP_VAL19 = '4' AND NOT( SUBSTR(D1.SV_BIZ_DCLSF_CD, 0, 2) = '32' AND SUBSTR(D1.FNL_VST_FSH_DT, 1, 6) = SUBSTR(D3.IST_DT, 1, 6)) AND D1.SV_BIZ_HCLSF_CD = '6' AND D1.FNL_ITM_GD_CD = 'R' AND (SELECT /*+ no_unnest leading(T1) use_nl(T1 T2 D1) */ (CASE WHEN COUNT(1) > 0 THEN 'Y' ELSE 'N' END) FROM TB_SVST_SV_WK_OSTR_IZ D1 INNER JOIN TB_PDBS_PD_BAS T1 ON D1.ITM_PD_CD = T1.PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL T2 ON T1.PD_CD = T2.PD_CD AND T2.PD_EXTS_PRP_GRP_CD = 'PART' AND T2.DTA_DL_YN = 'N' WHERE T1.PD_CD = D1.ITM_PD_CD AND T1.PD_NM LIKE '%'|| '웰스팜' ||'%' ) = 'Y' AND ( D1.FNL_SELL_TP_CD <> '4' AND D1.CNTR_NO NOT LIKE 'W8888%') AND ( D1.RTNGD_PROCS_TP_CD <> '90' OR D1.RTNGD_PROCS_TP_CD IS NULL) AND D1.FNL_VST_FSH_DT BETWEEN #{inqrYm} || '01' AND #{inqrYm}|| '99' AND ( D1.RTNGD_PROCS_TP_CD IN ('70', '71', '72', '73') OR D1.RTNGD_PROCS_TP_CD IS NULL OR (D1.RTNGD_PROCS_TP_CD IN ('10', '20', '21', '80', '81', '82', '90') AND SUBSTR(D1.OSTR_DT, 1, 6) > SUBSTR(D1.FNL_VST_FSH_DT, 1, 6) AND SUBSTR(D1.OSTR_DT, 1, 6) NOT LIKE #{inqrYm})) AND D1.USE_QTY <> 0 AND D4.SELL_TP_DTL_CD <> '22' ) T1 WHERE 1 = 1 ) T2 LEFT OUTER JOIN TB_SSCT_RTAS_STAT_IZ X1 /* 렌탈자산상태내역 */ ON X1.CNTR_NO = T2.CNTR_NO AND X1.CNTR_SN = T2.KIWI_LCICSQ WHERE 1 = 1 AND NOT EXISTS (SELECT 1 FROM TB_IFIN_SAP_RTAS_STAT_CHR_ETXT H1 /* SAP렌탈자산상태변경수신전문 */ WHERE 1 = 1 AND H1.UPD_ACT_DT < #{inqrYm}||'00' AND H1.RENTAL_ASSET_STAT = '0015' AND H1.ORDER_NO = X1.SAP_ASSET_NO) AND (CASE WHEN T2.RENTAL_ASSET_STAT = '0018' THEN (SELECT COUNT (1) FROM TB_IFIN_SAP_RTAS_STAT_CHR_ETXT H2 WHERE 1 = 1 AND H2.UPD_ACT_DT < #{inqrYm}||'00' AND H2.RENTAL_ASSET_STAT IN('0015', '0018') AND T2.RENTAL_ASSET_STAT = '0018' AND H2.ORDER_NO = X1.SAP_ASSET_NO) ELSE 0 END ) = 0 ) T3 WHERE 1 = 1
