mapper.select:
SELECT D2.SAP_MAT_CD AS SAP_CD /* SAP코드 */ , D1.ITM_PD_CD /* 품목코드 */ , D2.PD_ABBR_NM AS ITM_PD_NM /* 품목명 */ , D1.RMK_CN /* 비고 */ , SUBSTR(D1.FST_RGST_DTM, 1, 8) AS FST_RGST_DT /* 등록일자 */ , D4.DEPT_NM /* 소속 */ , D4.USR_NM /* 등록자 */ , D1.MNGT_YM /* 관리년월 */ , D1.CMPT_EXCD_SN /* 산출제외일련번호 */ , D1.ITM_KND_CD /* 품목종류코드 */ FROM TB_SVST_CMPT_EXCD_ITM_IZ D1 /* 산출제외품목 */ INNER JOIN TB_PDBS_PD_BAS D2 /* 상품기본 */ ON D2.PD_CD = D1.ITM_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D3 /* 상품각사속성상세 */ ON D3.PD_CD = D2.PD_CD INNER JOIN T_CMP_USR_ACO_M D4 /* 사용자계정기본 */ ON D4.USR_ID = D1.FST_RGST_USR_ID WHERE D1.DTA_DL_YN = 'N' AND D2.PD_TP_CD = 'M' AND D2.DTA_DL_YN = 'N' AND D3.PD_EXTS_PRP_GRP_CD = 'PART' AND D3.DTA_DL_YN = 'N' AND D3.PD_PRP_VAL19 IN ('5', '6') /* 필터, A/S자재 */ AND D1.MNGT_YM = #{inqrYm}
AND D3.PD_PRP_VAL19 = #{itmKndCd}
AND D1.ITM_PD_CD IN
#{itmPdCd}
AND D2.SAP_MAT_CD >= LPAD(#{strtSapCd}, 18, '0')
AND D2.SAP_MAT_CD < LPAD(#{endSapCd}, 18, '0')
ORDER BY D1.ITM_PD_CD, D1.CMPT_EXCD_SN

SELECT 'Y'
  FROM TB_SVST_CMPT_EXCD_ITM_IZ
 WHERE DTA_DL_YN = 'N'
   AND MNGT_YM   = #{mngtYm}
   AND ITM_PD_CD = #{itmPdCd}
   AND ROWNUM    = 1

mapper.insert:
INSERT INTO TB_SVST_CMPT_EXCD_ITM_IZ   /* 산출제외품목 */
     (
       MNGT_YM        /* 관리년월 */
     , ITM_PD_CD      /* 품목상품코드 */
     , CMPT_EXCD_SN   /* 산출제외일련번호 */
     , ITM_KND_CD     /* 품목종류코드 */
     , RMK_CN         /* 비고내용 */
     , DTA_DL_YN      /* 데이터삭제여부 */

     )
VALUES
     (
       #{mngtYm}
     , #{itmPdCd}
     , ( SELECT NVL(MAX(CMPT_EXCD_SN), 0) + 1
           FROM TB_SVST_CMPT_EXCD_ITM_IZ
          WHERE MNGT_YM   = #{mngtYm}
            AND ITM_PD_CD = #{itmPdCd} )
     , #{itmKndCd}
     , #{rmkCn}
     , 'N'

     )

INSERT INTO TB_SVST_CMPT_EXCD_ITM_IZ /* 산출제외품목 */ ( MNGT_YM /* 관리년월 */ , ITM_PD_CD /* 품목상품코드 */ , CMPT_EXCD_SN /* 산출제외일련번호 */ , ITM_KND_CD /* 품목종류코드 */ , RMK_CN /* 비고내용 */ , DTA_DL_YN /* 데이터삭제여부 */
) SELECT #{inqrYm} , D1.ITM_PD_CD , ( SELECT NVL(MAX(CMPT_EXCD_SN), 0) + 1 FROM TB_SVST_CMPT_EXCD_ITM_IZ WHERE MNGT_YM = #{inqrYm} AND ITM_PD_CD = D1.ITM_PD_CD ) , ITM_KND_CD , RMK_CN , DTA_DL_YN
FROM TB_SVST_CMPT_EXCD_ITM_IZ D1 WHERE D1.DTA_DL_YN = 'N' AND D1.MNGT_YM = TO_CHAR(ADD_MONTHS(TO_DATE(#{inqrYm}, 'YYYYMM'), -1), 'YYYYMM')
