mapper.select:
SELECT T1.OG_TP_CD , T1.EGER_PRTNR_NO , T1.TOOL_PD_CD , T1.DSB_SN , T1.TOOL_QTY , T1.PYMDTS , T2.OG_CD , T2.OG_NM , T2.HGR_OG_NM , T2.PRTNR_NO , T2.PRTNR_KNM , T3.SVPD_NM_ABBR1 , T3.SVPD_SAP_CD , TO_NUMBER(MAX(T1.TOOL_QTY) OVER()) AS MAX_TOOL_QTY FROM (SELECT A.OG_TP_CD , A.EGER_PRTNR_NO , A.TOOL_PD_CD , (SELECT MAX(X.DSB_SN) FROM TB_SVST_EGER_TOOL_DSB_IZ X WHERE X.OG_TP_CD = A.OG_TP_CD AND X.EGER_PRTNR_NO = A.EGER_PRTNR_NO AND X.TOOL_PD_CD = A.TOOL_PD_CD AND X.DTA_DL_YN = 'N' ) DSB_SN , (SELECT SUM(Y.TOOL_QTY) FROM TB_SVST_EGER_TOOL_DSB_IZ Y WHERE Y.OG_TP_CD = A.OG_TP_CD AND Y.EGER_PRTNR_NO = A.EGER_PRTNR_NO AND Y.TOOL_PD_CD = A.TOOL_PD_CD AND Y.DTA_DL_YN = 'N' ) AS TOOL_QTY , LISTAGG(A.PYMDT, ',') WITHIN GROUP(ORDER BY A.PYMDT) AS PYMDTS FROM TB_SVST_EGER_TOOL_DSB_IZ A WHERE A.DTA_DL_YN = 'N' GROUP BY A.OG_TP_CD, A.EGER_PRTNR_NO, A.TOOL_PD_CD, A.TOOL_QTY ) T1 , (SELECT A.BASE_YM , A.OG_ID , A.OG_TP_CD , A.OG_CD , B.OG_NM , B.PRTNR_KNM , B.PRTNR_NO , (SELECT X.OG_NM FROM TB_OGBS_MM_OG_IZ X WHERE X.BASE_YM = A.BASE_YM AND X.OG_ID = A.HGR_OG_ID AND X.OG_TP_CD = A.OG_TP_CD AND X.DTA_DL_YN = 'N' ) HGR_OG_NM FROM TB_OGBS_MM_OG_IZ A , TB_OGBS_MM_PRTNR_IZ B WHERE A.OG_TP_CD = B.OG_TP_CD AND A.BASE_YM = B.BASE_YM AND A.OG_TP_CD = B.OG_TP_CD AND A.OG_ID = B.OG_ID AND A.BASE_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND A.OG_TP_CD = 'W06' AND (A.OG_LEVL_DV_CD = '2' OR A.OG_LEVL_DV_CD = '3') AND A.DTA_DL_YN = 'N' AND B.DTA_DL_YN = 'N' ) T2 , () T3 WHERE T1.OG_TP_CD = T2.OG_TP_CD AND T1.EGER_PRTNR_NO = T2.PRTNR_NO AND T1.TOOL_PD_CD = T3.SVPD_PD_CD
AND T1.PYMDTS LIKE #{pymdt} || '%'
AND T2.OG_CD = #{ogCd}
AND T2.PRTNR_NO = #{egerPrtnrNo}
AND T2.PRTNR_KNM LIKE #{prtnrKnm} || '%'
AND T1.TOOL_PD_CD >= #{toolPdCdStrt}
AND T1.TOOL_PD_CD <= #{toolPdCdEnd}
AND T3.SVPD_SAP_CD >= #{sapMatCdStrt}
AND T3.SVPD_SAP_CD >= #{sapMatCdEnd}
ORDER BY T1.PYMDTS DESC, T1.DSB_SN DESC

mapper.update:
UPDATE TB_SVST_EGER_TOOL_DSB_IZ T1
   SET T1.DTA_DL_YN = 'Y'

 WHERE T1.OG_TP_CD = #{ogTpCd}
   AND T1.EGER_PRTNR_NO = #{egerPrtnrNo}
   AND T1.TOOL_PD_CD = #{toolPdCd}
   AND T1.DSB_SN = (SELECT MAX(A.DSB_SN)
                      FROM TB_SVST_EGER_TOOL_DSB_IZ A
                     WHERE A.OG_TP_CD = T1.OG_TP_CD
                       AND A.EGER_PRTNR_NO = T1.EGER_PRTNR_NO
                       AND A.TOOL_PD_CD = T1.TOOL_PD_CD)
