mapper.update:
UPDATE TB_SVST_ITM_OSTR_IZ
   SET DTA_DL_YN = 'Y'


   AND ITM_OSTR_NO = #{itmOstrNo}
   AND OSTR_WARE_NO = #{ostrWareNo}
   AND OSTR_SN = #{ostrSn}

mapper.insert:
INSERT INTO TB_SVST_ITM_OSTR_IZ (  /* 품목출고내역 */
             ITM_OSTR_NO              /* 품목출고번호 */
           , OSTR_SN                  /* 출고일련번호 */
           , OSTR_TP_CD               /* 출고유형코드 */
           , OSTR_WARE_NO             /* 출고창고번호 */
           , OSTR_DT                  /* 출고일자 */
           , ITM_KND_CD               /* 품목종류코드 */
           , ITM_PD_CD                /* 품목상품코드 */
           , OSTR_WARE_DV_CD          /* 출고창고구분코드 */
           , MNGT_UNIT_CD             /* 관리단위코드 */
           , BOX_UNIT_QTY             /* 박스단위수량 */
           , ITM_GD_CD                /* 품목등급코드 */
           , OSTR_QTY                 /* 출고수량 */
           , WARE_MNGT_PRTNR_NO       /* 창고관리파트너번호 */
           , WARE_MNGT_PRTNR_OG_TP_CD /* 창고관리파트너조직유형코드 */
           , OSTR_RSON_CD             /* 출고사유코드 */
           , STR_AK_WARE_DV_CD        /* 입고요청창고구분코드*/
           , STR_WARE_NO              /* 입고창고번호 */
           , DTA_DL_YN                /* 데이터삭제여부 */
           , RMK_CN                   /* 비고내용 */
           )
      SELECT #{itmOstrNo}
           , ${ostrSn}
           , #{ostrTpCd}
           , #{ostrWareNo}
           , #{ostrDt}
           , #{itmKndCd}
           , #{itmPdCd}
           , '1'
           , #{mngtUnitCd}
           , 0
           , #{itmGdCd}
           , ${ostrQty}
           , #{wareMngtPrtnrNo}
           , (SELECT T1.OG_TP_CD
                FROM TB_SVST_MCBY_WARE_IZ T1
              WHERE 1 = 1
                AND T1.APY_YM = SUBSTR(#{ostrDt}, 1, 6)
                AND T1.WARE_NO = #{ostrWareNo}
                AND T1.WARE_MNGT_PRTNR_NO = #{wareMngtPrtnrNo})
           , #{ostrRsonCd}
           , '2'
           , #{bilDept}
           , 'N'
           , #{rmkCn}

        FROM DUAL
