mapper.select:
SELECT DISTINCT D1.HGR_WARE_NO             AS WARE_NO
     , ( SELECT WARE_NM
           FROM TB_SVST_MCBY_WARE_IZ
          WHERE APY_YM  = D1.APY_YM
            AND WARE_NO = D1.HGR_WARE_NO ) AS WARE_NM
  FROM TB_SVST_MCBY_WARE_IZ D1   /* 월별창고내역 */
 INNER JOIN
     (
       SELECT DISTINCT STR_WARE_NO   /* 입고창고번호 */
         FROM TB_SVST_ITM_QOM_ASN_IZ   /* 품목물량배정내역 */
        WHERE DTA_DL_YN      = 'N'
          AND ASN_OJ_YM      = #{asnOjYm}
          AND ASN_TN_N       = #{cnt}
          AND OSTR_WARE_NO   = #{ostrWareNo}
          AND WARE_DV_CD     = #{wareDvCd}
          AND WARE_DTL_DV_CD = #{wareDtlDvCd}
     ) D2
    ON D2.STR_WARE_NO = D1.WARE_NO
 WHERE D1.DTA_DL_YN   = 'N'
   AND D1.WARE_USE_YN = 'Y'
   AND D1.APY_YM      = #{apyYm}
 ORDER BY WARE_NM

SELECT CASE WHEN T.LGST_OSTR_AK_NO IS NOT NULL THEN 'Y' ELSE 'N' END AS LGST_TRS_YN /* 물류전송여부 */ , T.WARE_NM /* 창고명 */ , T.SAP_MAT_CD /* SAP코드 */ , T.ITM_PD_CD /* 품목코드 */ , T.PD_ABBR_NM /* 품목명 */ , T.MNGT_UNIT /* 관리단위 */ , T.MNGT_UNIT_NM /* 관리단위명 */ , T.MAT_GD_CD /* 등급 */ , T.BOX_UNIT_QTY /* 박스단위수량 */ , T.CRTL_STOC_QTY /* 재고수량 */ , T.USE_QTY /* 소요수량 */ , T.CNFM_QTY /* 물량배정확정 수량 */ , T.CNFM_BOX_QTY /* 물량배정확정 BOX */ , CASE WHEN T.ITM_PD_CD IN ('WM07100419', 'WM07100420') AND T.MCBY_ACU_OSTR_QTY > 0 AND T.FILTER_BOX_QTY > 0 AND TRUNC( T.MCBY_ACU_OSTR_QTY / T.FILTER_BOX_QTY, 2) - FLOOR(T.MCBY_ACU_OSTR_QTY / T.FILTER_BOX_QTY) > 0.8 THEN ROUND(T.MCBY_ACU_OSTR_BOX_QTY) * 30 ELSE T.MCBY_ACU_OSTR_QTY END AS MCBY_ACU_OSTR_QTY /* 물량배정출고 누계 */ , CASE WHEN T.ITM_PD_CD IN ('WM07100419', 'WM07100420') AND T.MCBY_ACU_OSTR_QTY > 0 AND T.FILTER_BOX_QTY > 0 AND TRUNC( T.MCBY_ACU_OSTR_QTY / T.FILTER_BOX_QTY, 2) - FLOOR(T.MCBY_ACU_OSTR_QTY / T.FILTER_BOX_QTY) > 0.8 THEN ROUND(T.MCBY_ACU_OSTR_BOX_QTY) ELSE T.MCBY_ACU_OSTR_BOX_QTY END AS MCBY_ACU_OSTR_BOX_QTY /* 물량배정출고 BOX */ , T.FILTER_BOX_QTY /* 필터박스수량 */ , T.OUT_QTY /* 출고수량 */ , T.ITM_QOM_ASN_NO /* 품목물량배정번호 */ , T.ASN_OJ_YM /* 배정대상년월 */ , T.OSTR_WARE_NO /* 출고창고번호 */ , T.STR_WARE_NO /* 입고창고번호 */ , T.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , T.OG_TP_CD /* 조직유형코드 */ , T.ITM_KND_CD /* 품목종류코드 */ , T.ASN_TN_N /* 배정회차수 */ , T.WARE_DV_CD /* 창고구분코드 */ , T.OSTR_AK_NO /* 출고요청번호 */ , T.OSTR_AK_SN /* 출고요청일련번호 */ , T.OSTR_DT /* 출고일자 */ , T.RMK_CN /* 비고 */ , T.OSTR_WARE_DV_CD /* 출고창고구분코드 */ , T.OSTR_PRTNR_NO /* 출고창고파트너번호 */ , T.OSTR_PRTNR_OG_TP_CD /* 출고창고파트너조직유형코드 */ FROM ( SELECT D1.ITM_PD_CD , D3.PD_ABBR_NM , D4.PD_PRP_VAL05 AS MNGT_UNIT , F_CMZ_CD_NM('TNT_WELLS', 'MNGT_UNIT_CD', D4.PD_PRP_VAL05, #{session.langId}) AS MNGT_UNIT_NM , D1.MAT_GD_CD , NVL(D1.BOX_QTY, 0) AS BOX_UNIT_QTY , D1.CNFM_QTY , CASE WHEN NVL(TO_NUMBER(D4.PD_PRP_VAL12), 0) > 0 AND NVL(D1.CNFM_QTY, 0) > 0 THEN ROUND( D1.CNFM_QTY / TO_NUMBER(D4.PD_PRP_VAL12), 2 ) ELSE 0 END AS CNFM_BOX_QTY , NVL(D5.OSTR_AK_QTY, NVL(D1.CNFM_QTY, 0) - NVL(D1.MCBY_ACU_OSTR_QTY, 0)) AS OUT_QTY , NVL(D1.MCBY_ACU_OSTR_QTY, 0) AS MCBY_ACU_OSTR_QTY , CASE WHEN NVL(TO_NUMBER(D4.PD_PRP_VAL12), 0) > 0 AND NVL(D1.MCBY_ACU_OSTR_QTY, 0) > 0 THEN ROUND( D1.MCBY_ACU_OSTR_QTY / TO_NUMBER(D4.PD_PRP_VAL12), 2 ) ELSE 0 END AS MCBY_ACU_OSTR_BOX_QTY , D1.ASN_OJ_YM , D1.OSTR_WARE_NO , D1.STR_WARE_NO , D1.ITM_QOM_ASN_NO , D1.ASN_TN_N , D2.WARE_MNGT_PRTNR_NO , D2.OG_TP_CD , D4.PD_PRP_VAL19 AS ITM_KND_CD , D3.SAP_MAT_CD , D1.CRTL_STOC_QTY , NVL(D1.THWK_EXP_QTY, 0) AS USE_QTY , NVL(TO_NUMBER(D4.PD_PRP_VAL12), 0) AS FILTER_BOX_QTY , D2.WARE_NM , D2.WARE_DV_CD , D5.OSTR_AK_NO , D5.OSTR_AK_SN , D5.STR_HOP_DT AS OSTR_DT , D5.RMK_CN , D6.WARE_DV_CD AS OSTR_WARE_DV_CD , D6.WARE_MNGT_PRTNR_NO AS OSTR_PRTNR_NO , D6.OG_TP_CD AS OSTR_PRTNR_OG_TP_CD , D7.LGST_OSTR_AK_NO FROM TB_SVST_ITM_QOM_ASN_IZ D1 /* 품목물량배정내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ D2 /* 월별창고내역 */ ON D2.WARE_NO = D1.STR_WARE_NO INNER JOIN TB_PDBS_PD_BAS D3 /* 상품기본 */ ON D3.PD_CD = D1.ITM_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D4 /* 상품각사속성상세 */ ON D4.PD_CD = D3.PD_CD LEFT OUTER JOIN TB_SVST_ITM_OSTR_AK_IZ D5 /* 품목출고요청내역 */ ON D5.ITM_QOM_ASN_NO = D1.ITM_QOM_ASN_NO AND D5.DTA_DL_YN = 'N' INNER JOIN TB_SVST_MCBY_WARE_IZ D6 /* 월별창고내역 */ ON D6.WARE_NO = D1.OSTR_WARE_NO LEFT OUTER JOIN TB_IFIN_OSTR_AK_DTL_SEND_ETXT D7 /* 출고요청상세송신전문 */ ON D7.REL_NO = D5.OSTR_AK_NO AND D7.REL_SN = D5.OSTR_AK_SN AND D7.DTA_DL_YN = 'N' WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.WARE_USE_YN = 'Y' AND D3.DTA_DL_YN = 'N' AND D3.PD_TP_CD = 'M' AND D4.DTA_DL_YN = 'N' AND D4.PD_EXTS_PRP_GRP_CD = 'PART' AND D4.PD_PRP_VAL19 IN ('4', '5', '6') AND D6.DTA_DL_YN = 'N' AND D1.ASN_OJ_YM = #{asnOjYm} AND D1.ASN_TN_N = #{cnt} AND D1.OSTR_WARE_NO = #{ostrWareNo} AND D1.WARE_DV_CD = #{wareDvCd} AND D1.WARE_DTL_DV_CD = #{wareDtlDvCd} AND D2.HGR_WARE_NO = #{strWareNo} AND D2.APY_YM = #{apyYm} AND D6.APY_YM = #{apyYm}
AND D4.PD_PRP_VAL19 = #{itmKndCd}
AND D1.ITM_PD_CD = #{itmPdCd}
AND D1.ITM_PD_CD IN
#{itmPdCd}
AND D3.SAP_MAT_CD >= LPAD(#{strtSapCd}, 18, '0')
AND D3.SAP_MAT_CD <= LPAD(#{endSapCd}, 18, '0')
AND D3.SAP_MAT_CD IS NOT NULL
) T ORDER BY T.WARE_NM, T.ITM_PD_CD

mapper.insert:
MERGE INTO TB_SVST_ITM_OSTR_AK_IZ TB_TGT /* 품목출고요청내역 */ USING ( SELECT #{ostrAkNo} AS OSTR_AK_NO /* 출고요청번호 */ , #{ostrAkSn} AS OSTR_AK_SN /* 출고요청일련번호 */ , ( SELECT NVL(MAX(OSTR_AK_SN), 0) + 1 FROM TB_SVST_ITM_OSTR_AK_IZ WHERE OSTR_AK_NO = #{ostrAkNo} ) AS NEW_OSTR_AK_SN /* 신규출고요청일련번호 */ , #{strWareNo} AS STR_WRAE_NO /* 입고창고번호 */ , #{ostrWareNo} AS OSTR_WARE_NO /* 출고대상창고번호 */ , #{ostrDt} AS OSTR_DT /* 출고요청등록일자 */ , #{wareDvCd} AS WARE_DV_CD /* 출고요청창고구분코드 */ , #{wareMngtPrtnrNo} AS WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , #{ogTpCd} AS OG_TP_CD /* 창고관리파트너조직유형코드 */ , #{itmPdCd} AS ITM_PD_CD /* 품목상품코드 */ , #{mngtUnit} AS MNGT_UNIT /* 관리단위코드 */ , #{boxUnitQty} AS BOX_UNIT_QTY /* 박스단위수량 */ , #{matGdCd} AS MAT_GD_CD /* 품목등급코드 */ , #{outQty} AS OSTR_AK_QTY /* 출고요청수량 */ , #{outQty} AS OSTR_CNFM_QTY /* 출고확정수량 */ , #{rmkCn} AS RMK_CN /* 비고내용 */ , #{itmQomAsnNo} AS ITM_QOM_ASN_NO /* 품목물량배정번호 */ , #{ostrWareDvCd} AS OSTR_OJ_WARE_DV_CD /* 출고대상창고구분코드 */ , #{ostrPrtnrNo} AS OSTR_WARE_MNGT_PRTNR_NO /* 출고창고관리파트너번호 */ , #{ostrPrtnrOgTpCd} AS OSTR_WARE_MNGT_PRTNR_OG_TP_CD /* 출고창고관리파트너조직유형코드 */ FROM DUAL ) TB_SRC ON ( TB_TGT.OSTR_AK_NO = TB_SRC.OSTR_AK_NO AND TB_TGT.OSTR_AK_SN = TB_SRC.OSTR_AK_SN AND TB_TGT.DTA_DL_YN = 'N' ) WHEN MATCHED THEN UPDATE SET TB_TGT.OSTR_AK_QTY = TB_SRC.OSTR_AK_QTY , TB_TGT.OSTR_CNFM_QTY = TB_SRC.OSTR_CNFM_QTY , TB_TGT.STR_HOP_DT = TB_SRC.OSTR_DT , TB_TGT.RMK_CN = TB_SRC.RMK_CN
WHEN NOT MATCHED THEN INSERT ( TB_TGT.OSTR_AK_NO /* 출고요청번호 */ , TB_TGT.OSTR_AK_SN /* 출고요청일련번호 */ , TB_TGT.OSTR_AK_TP_CD /* 출고요청유형코드 */ , TB_TGT.STR_OJ_WARE_NO /* 입고창고번호 */ , TB_TGT.OSTR_OJ_WARE_NO /* 출고대상창고번호 */ , TB_TGT.OSTR_AK_RGST_DT /* 출고요청등록일자 */ , TB_TGT.STR_HOP_DT /* 입고희망일자 */ , TB_TGT.OSTR_AK_WARE_DV_CD /* 출고요청창고구분코드 */ , TB_TGT.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , TB_TGT.WARE_MNGT_PRTNR_OG_TP_CD /* 창고관리파트너조직유형코드 */ , TB_TGT.OSTR_OJ_WARE_DV_CD /* 출고대상창고구분코드 */ , TB_TGT.OSTR_WARE_MNGT_PRTNR_NO /* 출고창고관리파트너번호 */ , TB_TGT.OSTR_WARE_MNGT_PRTNR_OG_TP_CD /* 출고창고관리파트너조직유형코드 */ , TB_TGT.ITM_PD_CD /* 품목상품코드 */ , TB_TGT.MNGT_UNIT_CD /* 관리단위코드 */ , TB_TGT.BOX_UNIT_QTY /* 박스단위수량 */ , TB_TGT.ITM_GD_CD /* 품목등급코드 */ , TB_TGT.OSTR_AK_QTY /* 출고요청수량 */ , TB_TGT.OSTR_CNFM_QTY /* 출고확정수량 */ , TB_TGT.ITM_QOM_ASN_NO /* 품목물량배정번호 */ , TB_TGT.RMK_CN /* 비고내용 */ , TB_TGT.DTA_DL_YN /* 데이터삭제여부 */
) VALUES ( TB_SRC.OSTR_AK_NO /* 출고요청번호 */ , TB_SRC.NEW_OSTR_AK_SN /* 출고요청일련번호 */ , '360' /* 출고요청유형코드 */ , TB_SRC.STR_WRAE_NO /* 입고창고번호 */ , TB_SRC.OSTR_WARE_NO /* 출고대상창고번호 */ , TO_CHAR(SYSDATE, 'YYYYMMDD') /* 출고요청등록일자 */ , TB_SRC.OSTR_DT /* 입고희망일자 */ , TB_SRC.WARE_DV_CD /* 출고요청창고구분코드 */ , TB_SRC.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , TB_SRC.OG_TP_CD /* 창고관리파트너조직유형코드 */ , TB_SRC.OSTR_OJ_WARE_DV_CD /* 출고대상창고구분코드 */ , TB_SRC.OSTR_WARE_MNGT_PRTNR_NO /* 출고창고관리파트너번호 */ , TB_SRC.OSTR_WARE_MNGT_PRTNR_OG_TP_CD /* 출고창고관리파트너조직유형코드 */ , TB_SRC.ITM_PD_CD /* 품목상품코드 */ , TB_SRC.MNGT_UNIT /* 관리단위코드 */ , TB_SRC.BOX_UNIT_QTY /* 박스단위수량 */ , TB_SRC.MAT_GD_CD /* 품목등급코드 */ , TB_SRC.OSTR_AK_QTY /* 출고요청수량 */ , TB_SRC.OSTR_CNFM_QTY /* 출고확정수량 */ , TB_SRC.ITM_QOM_ASN_NO /* 품목물량배정번호 */ , TB_SRC.RMK_CN /* 비고내용 */ , 'N' /* 데이터삭제여부 */
)
