mapper.select:


SELECT DISTINCT D1.HGR_WARE_NO             AS WARE_NO
     , ( SELECT WARE_NM
           FROM TB_SVST_MCBY_WARE_IZ
          WHERE APY_YM  = D1.APY_YM
            AND WARE_NO = D1.HGR_WARE_NO ) AS WARE_NM


SELECT D1.WARE_NO
     , D1.WARE_NM


  FROM TB_SVST_MCBY_WARE_IZ D1   /* 월별창고내역 */
 INNER JOIN
     (
       SELECT DISTINCT STR_WARE_NO   /* 입고창고번호 */
         FROM TB_SVST_ITM_QOM_ASN_IZ   /* 품목물량배정내역 */
        WHERE DTA_DL_YN      = 'N'
          AND ASN_OJ_YM      = #{asnOjYm}
          AND ASN_TN_N       = #{cnt}
          AND OSTR_WARE_NO   = #{ostrWareNo}
          AND WARE_DV_CD     = #{wareDvCd}
          AND WARE_DTL_DV_CD = #{wareDtlDvCd}
     ) D2
    ON D2.STR_WARE_NO = D1.WARE_NO
 WHERE D1.DTA_DL_YN   = 'N'
   AND D1.APY_YM      = #{apyYm}

   AND D1.HGR_WARE_NO = #{hgrStrWareNo}

 ORDER BY WARE_NM

SELECT CASE WHEN T.ITM_PD_CD IN ('WM07104866', 'WM07100391', 'WM07100392', 'WM07100279', 'WM07100287', 'WM07100289', 'WM07100403', 'WM07100466', 'WM07100469') THEN 'N' WHEN T.OUT_QTY > 0 AND (T.HGR_CRTL_STOC_QTY - T.TOT_OUT_QTY) < 5 THEN 'Y' ELSE 'N' END AS CHK /* Row 체크여부 */ , T.WARE_NM /* 입고창고 */ , T.SAP_MAT_CD /* SAP코드 */ , T.ITM_PD_CD /* 품목코드 */ , T.PD_ABBR_NM /* 품목명 */ , T.PART_USE_QTY /* 필터소요량 */ , T.UNDER_20PER /* 배정수량 대비 재고수량 부족 */ , T.HGR_CRTL_STOC_QTY /* 센터재고 */ , T.HGR_CRTL_STOC_QTY - T.TOT_OUT_QTY AS TOT_OUT_QTY /* 총출고 */ , T.MNGT_UNIT /* 관리단위 */ , T.MNGT_UNIT_NM /* 관리단위명 */ , T.MAT_GD_CD /* 등급 */ , T.BOX_UNIT_QTY /* 박스단위수량 */ , T.CRTL_STOC_QTY /* 재고수량 */ , T.USE_QTY /* 소요수량 */ , T.CNFM_QTY /* 물량배정확정 수량 */ , T.CNFM_BOX_QTY /* 물량배정확정 BOX */ , T.ACL_OSTR_QTY /* 물량배정출고 누계 */ , T.ACL_OSTR_BOX_QTY /* 물량배정출고 BOX */ , T.FILTER_BOX_QTY /* 필터박스수량 */ , T.OUT_QTY /* 출고수량 */ , T.OUT_BOX_QTY /* 출고수량 BOX */ , T.ITM_QOM_ASN_NO /* 품목물량배정번호 */ , T.ASN_OJ_YM /* 배정대상년월 */ , T.OSTR_WARE_NO /* 출고창고번호 */ , T.STR_WARE_NO /* 입고창고번호 */ , T.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , T.OG_TP_CD /* 조직유형코드 */ , T.ITM_KND_CD /* 품목종류코드 */ , T.ASN_TN_N /* 배정회차수 */ , T.WARE_DV_CD /* 창고구분코드 */ FROM ( SELECT D1.ITM_PD_CD , D4.PD_ABBR_NM , D5.PD_PRP_VAL05 AS MNGT_UNIT , F_CMZ_CD_NM('TNT_WELLS', 'MNGT_UNIT_CD', D5.PD_PRP_VAL05, #{session.langId}) AS MNGT_UNIT_NM , D1.MAT_GD_CD , NVL(D1.BOX_UNIT_QTY, 0) AS BOX_UNIT_QTY , D1.CNFM_QTY , CASE WHEN NVL(TO_NUMBER(D5.PD_PRP_VAL12), 0) > 0 AND NVL(D1.CNFM_QTY, 0) > 0 THEN ROUND( D1.CNFM_QTY / TO_NUMBER(D5.PD_PRP_VAL12), 2 ) ELSE 0 END AS CNFM_BOX_QTY , NVL(D1.CNFM_QTY, 0) - NVL(D1.ACL_OSTR_QTY, 0) AS OUT_QTY , CASE WHEN NVL(TO_NUMBER(D5.PD_PRP_VAL12), 0) > 0 AND NVL(D1.CNFM_QTY, 0) - NVL(D1.ACL_OSTR_QTY, 0) > 0 THEN ROUND( ( D1.CNFM_QTY - NVL(D1.ACL_OSTR_QTY, 0) ) / TO_NUMBER(D5.PD_PRP_VAL12), 2 ) ELSE 0 END AS OUT_BOX_QTY , NVL(D1.ACL_OSTR_QTY, 0) AS ACL_OSTR_QTY , CASE WHEN NVL(TO_NUMBER(D5.PD_PRP_VAL12), 0) > 0 AND NVL(D1.ACL_OSTR_QTY, 0) > 0 THEN ROUND( D1.ACL_OSTR_QTY / TO_NUMBER(D5.PD_PRP_VAL12), 2 ) ELSE 0 END AS ACL_OSTR_BOX_QTY , D1.ASN_OJ_YM , D1.OSTR_WARE_NO , D1.STR_WARE_NO , D1.ITM_QOM_ASN_NO , D1.ASN_TN_N , D2.WARE_MNGT_PRTNR_NO , D2.OG_TP_CD , D5.PD_PRP_VAL19 AS ITM_KND_CD , D4.SAP_MAT_CD , D1.CRTL_STOC_QTY , NVL(D1.WO_ASN_QOM_CT, 0) - NVL(D1.CRTL_STOC_QTY, 0) AS USE_QTY , NVL(TO_NUMBER(D5.PD_PRP_VAL12), 0) AS FILTER_BOX_QTY , D2.WARE_NM , D2.WARE_DV_CD , D3.PART_USE_QTY , ( SELECT NVL(PITM_STOC_A_GD_QTY, 0) - NVL(MMT_STOC_A_GD_QTY, 0) FROM TB_SVST_CST_SV_ITM_STOC_IZ WHERE ITM_PD_CD = D1.ITM_PD_CD AND WARE_NO = D2.HGR_WARE_NO AND DTA_DL_YN = 'N' ) AS HGR_CRTL_STOC_QTY , ( SELECT NVL(SUM(S1.CNFM_QTY), 0) - NVL(SUM(S1.ACL_OSTR_QTY), 0) FROM TB_SVST_ITM_QOM_ASN_IZ S1 INNER JOIN TB_SVST_MCBY_WARE_IZ S2 ON S2.WARE_NO = S1.STR_WARE_NO WHERE S1.ITM_PD_CD = D1.ITM_PD_CD AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S1.ASN_OJ_YM = #{asnOjYm} AND S1.ASN_TN_N = #{cnt} AND S1.OSTR_WARE_NO = #{ostrWareNo} AND S2.APY_YM = #{apyYm} AND S2.HGR_WARE_NO = #{hgrStrWareNo} ) AS TOT_OUT_QTY , GREATEST( ( SELECT NVL(SUM(S1.WO_ASN_QOM_CT), 0) * 0.2 FROM TB_SVST_ITM_QOM_ASN_IZ S1 INNER JOIN TB_SVST_MCBY_WARE_IZ S2 ON S2.WARE_NO = S1.STR_WARE_NO WHERE S1.ITM_PD_CD = D1.ITM_PD_CD AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' AND S1.ASN_OJ_YM = #{asnOjYm} AND S1.ASN_TN_N = #{cnt} AND S1.OSTR_WARE_NO = #{ostrWareNo} AND S2.APY_YM = #{apyYm} AND S2.HGR_WARE_NO = #{hgrStrWareNo} ) - ( SELECT NVL(S1.PITM_STOC_A_GD_QTY, 0) FROM TB_SVST_CST_SV_ITM_STOC_IZ S1 INNER JOIN TB_SVST_MCBY_WARE_IZ S2 ON S2.HGR_WARE_NO = S1.WARE_NO WHERE S1.ITM_PD_CD = D1.ITM_PD_CD AND S2.WARE_NO = D1.STR_WARE_NO AND S2.APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND S1.DTA_DL_YN = 'N' AND S2.DTA_DL_YN = 'N' ), 0) AS UNDER_20PER FROM TB_SVST_ITM_QOM_ASN_IZ D1 /* 품목물량배정내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ D2 /* 월별창고내역 */ ON D2.WARE_NO = D1.STR_WARE_NO LEFT OUTER JOIN ( SELECT PART_PD_CD , SUM(PART_USE_QTY) AS PART_USE_QTY FROM TB_SVPD_CST_SV_RGBSPR_IZ /* 고객서비스정기BS주기내역 */ WHERE DTA_DL_YN = 'N' AND ITM_KND_CD = '5' AND VST_DUEDT LIKE #{asnOjYm} || '%' GROUP BY PART_PD_CD ) D3 ON D3.PART_PD_CD = D1.ITM_PD_CD INNER JOIN TB_PDBS_PD_BAS D4 /* 상품기본 */ ON D4.PD_CD = D1.ITM_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D5 /* 상품각사속성상세 */ ON D5.PD_CD = D4.PD_CD WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.WARE_USE_YN = 'Y' AND D4.DTA_DL_YN = 'N' AND D4.PD_TP_CD = 'M' AND D5.DTA_DL_YN = 'N' AND D5.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.ASN_OJ_YM = #{asnOjYm} AND D1.ASN_TN_N = #{cnt} AND D1.OSTR_WARE_NO = #{ostrWareNo} AND D1.STR_WARE_NO = #{strWareNo} AND D2.APY_YM = #{apyYm} AND D2.HGR_WARE_NO = #{hgrStrWareNo}
AND D5.PD_PRP_VAL19 = #{itmKndCd}
AND D1.ITM_PD_CD = #{itmPdCd}
AND D1.ITM_PD_CD IN
#{itmPdCd}
AND D4.SAP_MAT_CD >= LPAD(#{strtSapCd}, 18, '0')
AND D4.SAP_MAT_CD < LPAD(#{endSapCd}, 18, '0')
AND D4.SAP_MAT_CD IS NOT NULL
) T
AND T.HGR_CRTL_STOC_QTY - T.TOT_OUT_QTY = #{totOutQty}
ORDER BY T.WARE_NM, T.ITM_PD_CD

mapper.insert:
INSERT INTO TB_SVST_ITM_OSTR_AK_IZ /* 품목출고요청내역 */ ( OSTR_AK_NO /* 출고요청번호 */ , OSTR_AK_SN /* 출고요청일련번호 */ , OSTR_AK_TP_CD /* 출고요청유형코드 */ , STR_OJ_WARE_NO /* 입고창고번호 */ , OSTR_OJ_WARE_NO /* 출고대상창고번호 */ , OSTR_AK_RGST_DT /* 출고요청등록일자 */ , OSTR_AK_WARE_DV_CD /* 출고요청창고구분코드 */ , WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , WARE_MNGT_PRTNR_OG_TP_CD /* 창고관리파트너조직유형코드 */ , OSTR_OJ_WARE_DV_CD /* 출고대상창고구분코드 */ , ITM_PD_CD /* 품목상품코드 */ , MNGT_UNIT_CD /* 관리단위코드 */ , BOX_UNIT_QTY /* 박스단위수량 */ , ITM_GD_CD /* 품목등급코드 */ , OSTR_AK_QTY /* 출고요청수량 */ , OSTR_CNFM_QTY /* 출고확정수량 */ , RMK_CN /* 비고내용 */ , DTA_DL_YN /* 데이터삭제여부 */
) VALUES ( #{ostrAkNo} /* 출고요청번호 */ , ( SELECT NVL(MAX(OSTR_AK_SN), 0) + 1 FROM TB_SVST_ITM_OSTR_AK_IZ WHERE OSTR_AK_NO = #{ostrAkNo} ) /* 출고요청일련번호 */ , '360' /* 출고요청유형코드 */ , #{strWareNo} /* 입고창고번호 */ , #{ostrWareNo} /* 출고대상창고번호 */ , #{ostrDt} /* 출고요청등록일자 */ , #{wareDvCd} /* 출고요청창고구분코드 */ , #{wareMngtPrtnrNo} /* 창고관리파트너번호 */ , #{ogTpCd} /* 창고관리파트너조직유형코드 */ , '1' /* 출고대상창고구분코드 */ , #{itmPdCd} /* 품목상품코드 */ , #{mngtUnit} /* 관리단위코드 */ , #{boxUnitQty} /* 박스단위수량 */ , #{matGdCd} /* 품목등급코드 */ , #{outQty} /* 출고요청수량 */ , #{outQty} /* 출고확정수량 */ , #{rmkCn} /* 비고내용 */ , 'N' /* 데이터삭제여부 */
)
