mapper.select:
WITH PD_INF AS ( SELECT T1.PD_CD , T1.PD_NM , T2.PD_PRP_VAL19 AS ITM_KND_CD , T1.SAP_MAT_CD , T2.PD_PRP_VAL15 AS ORDNY_HV_MAT_YN FROM WSMDBS.TB_PDBS_PD_BAS T1 /*상품기본*/ LEFT OUTER JOIN WSMDBS.TB_PDBS_PD_ECOM_PRP_DTL T2 /*상품각사속성상세*/ ON T1.PD_CD = T2.PD_CD AND T2.PD_EXTS_PRP_GRP_CD IN ('PART') AND T2.PD_PRP_VAL19 = '4' WHERE 1=1
AND T2.PD_CD = #{pdCd}
/*상품확장속성그룹코드 CMN 공통, AFS AS, PRC 가격, STLM 결제, CNTR 계약, EXCH 교환/반품, ETC 기타, SL 매출, GO 발주, SPP 배송, ANA 분석, FEE 영업수수료, HIST 이력, SCHD 일정관리, COCN 전집, LRNN 학습/외국어, LV 학습단계, PART AS부품, FINC 재무, PDCT 판매제품*/ ) , OG_INF AS ( SELECT (CASE WHEN OG_ID IN ('OGO198500002398') THEN 'OGO198500002391' /* 천안 -> 청주서비스센터 */ WHEN OG_ID IN ('OGO201300002811') THEN 'OGO198500002394' /* 순천 -> 광주서비스센터 */ WHEN OG_ID IN ('OGO198500002390', 'OGO201700003690') THEN 'OGO201600002963' /* 창원/진주 -> */ WHEN OG_ID IN ('OGO198500002383') THEN 'OGO198500002397' /* 성수 -> 하남서비스센터 */ ELSE OG_ID END) AS OG_ID , (CASE WHEN OG_ID IN ('OGO198500002398') THEN '청주서비스센터' /* 천안 -> 청주서비스센터 */ WHEN OG_ID IN ('OGO201300002811') THEN '광주서비스센터' /* 순천 -> 광주서비스센터 */ WHEN OG_ID IN ('OGO198500002390', 'OGO201700003690') THEN '김해서비스센터' /* 창원/진주 -> */ WHEN OG_ID IN ('OGO198500002383') THEN '하남서비스센터' /* 성수 -> 하남서비스센터 */ ELSE OG_NM END) AS OG_NM FROM TB_OGBS_OG_BAS ) , STOCK_INF AS ( SELECT TB_SVST_MCBY_WARE_IZ.APY_YM , TB_SVST_MCBY_WARE_IZ.WARE_NO , (CASE WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7121%' THEN '71213' WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7132%' THEN '71321' ELSE OG_INF.OG_ID END) AS OG_ID , (CASE WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7121%' THEN '물류운영2팀' WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7132%' THEN '물류운영3팀' ELSE OG_INF.OG_NM END) AS OG_NM , WARE_ICHR_NO , WARE_DV_CD , HGR_WARE_NO FROM TB_SVST_MCBY_WARE_IZ INNER JOIN OG_INF ON TB_SVST_MCBY_WARE_IZ.OG_ID = OG_INF.OG_ID WHERE APY_YM = SUBSTR(#{baseDt},1,6) AND WARE_ICHR_NO = '000' AND WARE_USE_YN = 'Y' ) , M_STOC AS ( SELECT S1.WARE_NO WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , S1.BTD_STOC_A_GD_QTY , S1.BTD_STOC_B_GD_QTY , S1.BTD_STOC_E_GD_QTY , S1.BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_MCITM_STOC_IZ S1 /* 월별품목재고내역 ST122 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.WARE_NO = STOCK_INF.WARE_NO AND S1.BASE_YM = STOCK_INF.APY_YM ) , ITM_STR AS ( SELECT S1.STR_WARE_NO WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , S1.STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_STR_IZ S1 /* 품목입고내역 ST141 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.STR_WARE_NO = STOCK_INF.WARE_NO AND S1.STR_RGST_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt} WHERE 1=1
AND S1.ITM_GD_CD = #{pdGdCd}
) , WK_OSTR1 AS ( SELECT S1.WK_WARE_NO WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , S1.USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_SV_WK_OSTR_IZ S1 /* 서비스작업출고내역 ST163 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.WK_WARE_NO = STOCK_INF.WARE_NO AND S1.FNL_VST_FSH_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt} WHERE 1=1 AND S1.SV_BIZ_HCLSF_CD = '6'
AND S1.FNL_ITM_GD_CD = #{pdGdCd}
) , ITM_GD1 AS ( SELECT S1.WARE_NO , S1.PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , S1.CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_GD_CTR_IZ S1 /* 품목등급조정내역 ST173 */ INNER JOIN PD_INF ON S1.PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.WARE_NO = STOCK_INF.WARE_NO AND S1.STAT_CTR_APY_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt} WHERE 1=1
AND S1.AFCT_ITM_GD_CD = #{pdGdCd}
) , ITM_OSTR AS ( SELECT S1.OSTR_WARE_NO WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , S1.OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_OSTR_IZ S1 /* 품목출고내역 ST161 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.OSTR_WARE_NO = STOCK_INF.WARE_NO AND S1.OSTR_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt}
WHERE S1.ITM_GD_CD = #{pdGdCd}
) , WK_OSTR2 AS ( SELECT S1.WK_WARE_NO AS WARE_NO , S1.ITM_PD_CD AS PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , S1.USE_QTY AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_SV_WK_OSTR_IZ S1 /* 서비스작업출고내역 ST163 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.WK_WARE_NO = STOCK_INF.WARE_NO AND S1.FNL_VST_FSH_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt} WHERE S1.SV_BIZ_HCLSF_CD = '6'
AND S1.FNL_ITM_GD_CD = #{pdGdCd}
) , ITM_GD2 AS ( SELECT S1.WARE_NO , S1.PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , S1.CTR_QTY AS CTR_QTY2 FROM TB_SVST_ITM_GD_CTR_IZ S1 /* 품목등급조정내역 ST173 */ INNER JOIN PD_INF ON S1.PD_CD = PD_INF.PD_CD INNER JOIN STOCK_INF ON S1.WARE_NO = STOCK_INF.WARE_NO AND S1.STAT_CTR_APY_DT BETWEEN STOCK_INF.APY_YM||'01' AND #{baseDt} WHERE 1=1
AND S1.AFCT_ITM_GD_CD = #{pdGdCd}
) , SV_INF AS ( SELECT T_DATE.TEMP_DATE AS VST_CNFMDT , V_MAIN.OG_ID , V_MAIN.VST_CNFMDT , V_MAIN.PD_CD , NVL(VST_CNFMDT_QTY,0) VST_CNFMDT_QTY , RN /* , DENSE_RANK() OVER(PARTITION BY V_MAIN.OG_ID ORDER BY TEMP_DATE) RN */ FROM ( SELECT TEMP_DATE , DENSE_RANK() OVER(ORDER BY TEMP_DATE) RN FROM ( SELECT TO_CHAR((TO_DATE(#{baseDt}, 'YYYYMMDD') + LEVEL-1), 'YYYYMMDD') AS TEMP_DATE FROM DUAL CONNECT BY LEVEL <= 14 ) ) T_DATE LEFT OUTER JOIN ( SELECT T1.SV_CNR_OG_ID AS OG_ID /* 센터 OG_ID */ , T1.VST_CNFMDT , PD_INF.PD_CD , SUM(DECODE(T1.SV_CNR_OG_ID, '', 0, 1)) AS VST_CNFMDT_QTY /* 작업수량 */ FROM TB_SVPD_CST_SVAS_IST_ASN_IZ T1 /* 고객서비스AS설치배정이력 AC221 */ LEFT OUTER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ T2 /* 고객서비스AS설치대상내역 AC211 */ ON T1.CST_SV_ASN_NO = T2.CST_SV_ASN_NO AND T2.SV_BIZ_DCLSF_CD NOT LIKE '13%' INNER JOIN PD_INF ON T2.PD_CD = PD_INF.PD_CD WHERE T2.MTR_STAT_CD IN ('1', '2') AND T1.WK_PRGS_STAT_CD IN ('00', '10') AND T2.SV_BIZ_HCLSF_CD = '1' AND T1.VST_CNFMDT BETWEEN TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD'),'YYYYMMDD') AND TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')+14, 'YYYYMMDD') AND T1.SV_CNR_OG_ID NOT IN ('OGO198500002399', '71507', '71369', 'OGO198500002385', '71353', '71362', '71448', 'OGO201700003699', '71354', 'OGO201800003425', 'OGO198500002393', '71314', '71351', 'OGO198500002383', 'OGO198500002386')
AND T2.PD_CD = #{pdCd}
GROUP BY T1.SV_CNR_OG_ID , T1.VST_CNFMDT , PD_INF.PD_CD ) V_MAIN ON V_MAIN.VST_CNFMDT = T_DATE.TEMP_DATE ) , E_STOCK_INF AS ( SELECT TB_SVST_MCBY_WARE_IZ.APY_YM , TB_SVST_MCBY_WARE_IZ.WARE_NO , (CASE WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7121%' THEN '71213' WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7132%' THEN '71321' ELSE OG_INF.OG_ID END) AS OG_ID , (CASE WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7121%' THEN '물류운영2팀' WHEN TB_SVST_MCBY_WARE_IZ.OG_ID LIKE '@7132%' THEN '물류운영3팀' ELSE OG_INF.OG_NM END) AS OG_NM , WARE_ICHR_NO , WARE_DV_CD , HGR_WARE_NO FROM TB_SVST_MCBY_WARE_IZ INNER JOIN OG_INF ON TB_SVST_MCBY_WARE_IZ.OG_ID = OG_INF.OG_ID WHERE APY_YM = SUBSTR(#{baseDt},1,6) AND WARE_ICHR_NO != '000' AND WARE_USE_YN = 'Y' ) , E_M_STOC AS (SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , S1.BTD_STOC_A_GD_QTY , S1.BTD_STOC_B_GD_QTY , S1.BTD_STOC_E_GD_QTY , S1.BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_MCITM_STOC_IZ S1 /* 월별품목재고내역 ST122 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.WARE_NO = E_STOCK_INF.WARE_NO AND S1.BASE_YM = E_STOCK_INF.APY_YM ) , E_ITM_STR AS ( SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , S1.STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_STR_IZ S1 /* 품목입고내역 ST141 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.STR_WARE_NO = E_STOCK_INF.WARE_NO AND S1.STR_RGST_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD')) WHERE 1=1
AND S1.ITM_GD_CD = #{pdGdCd}
) , E_WK_OSTR1 AS ( SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , S1.USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_SV_WK_OSTR_IZ S1 /* 서비스작업출고내역 ST163 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.WK_WARE_NO = E_STOCK_INF.WARE_NO AND S1.FNL_VST_FSH_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD')) WHERE 1=1 AND S1.SV_BIZ_HCLSF_CD = '6'
AND S1.FNL_ITM_GD_CD = #{pdGdCd}
) , E_ITM_GD1 AS ( SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , S1.CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_GD_CTR_IZ S1 /* 품목등급조정내역 ST173 */ INNER JOIN PD_INF ON S1.PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.WARE_NO = E_STOCK_INF.WARE_NO AND S1.STAT_CTR_APY_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD')) WHERE 1=1
AND S1.AFCT_ITM_GD_CD = #{pdGdCd}
) , E_ITM_OSTR AS ( SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.ITM_PD_CD PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , S1.OSTR_QTY , 0 AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_ITM_OSTR_IZ S1 /* 품목출고내역 ST161 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.OSTR_WARE_NO = E_STOCK_INF.WARE_NO AND S1.OSTR_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD'))
WHERE S1.ITM_GD_CD = #{pdGdCd}
) , E_WK_OSTR2 AS (SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.ITM_PD_CD AS PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , S1.USE_QTY AS USE_QTY2 , 0 AS CTR_QTY2 FROM TB_SVST_SV_WK_OSTR_IZ S1 /* 서비스작업출고내역 ST163 */ INNER JOIN PD_INF ON S1.ITM_PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.WK_WARE_NO = E_STOCK_INF.WARE_NO AND S1.FNL_VST_FSH_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD')) WHERE S1.SV_BIZ_HCLSF_CD = '6'
AND S1.FNL_ITM_GD_CD = #{pdGdCd}
) , E_ITM_GD2 AS (SELECT E_STOCK_INF.HGR_WARE_NO HGR_WARE_NO , S1.PD_CD , PD_INF.ITM_KND_CD , 0 AS BTD_STOC_A_GD_QTY , 0 AS BTD_STOC_B_GD_QTY , 0 AS BTD_STOC_E_GD_QTY , 0 AS BTD_STOC_R_GD_QTY , 0 AS STR_QTY , 0 AS USE_QTY , 0 AS CTR_QTY , 0 AS OSTR_QTY , 0 AS USE_QTY2 , S1.CTR_QTY AS CTR_QTY2 FROM TB_SVST_ITM_GD_CTR_IZ S1 /* 품목등급조정내역 ST173 */ INNER JOIN PD_INF ON S1.PD_CD = PD_INF.PD_CD INNER JOIN E_STOCK_INF ON S1.WARE_NO = E_STOCK_INF.WARE_NO AND S1.STAT_CTR_APY_DT BETWEEN DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', E_STOCK_INF.APY_YM||'01') AND DECODE(SUBSTR(#{baseDt},7,2),'01',E_STOCK_INF.APY_YM||'00', TO_CHAR(TO_DATE(#{baseDt},'YYYYMMDD')-1,'YYYYMMDD')) WHERE 1=1
AND S1.AFCT_ITM_GD_CD = #{pdGdCd}
