SELECT OG_ID , OG_NM , PD_CD , PD_NM , 0 AS PAJU_QTY , PRV_MNG , ENG_QTY , STOCK_TOTAL , NVL(STOCK_TOTAL - INSTALLDATE1, 0) AS STOCKDATE1 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2, 0) AS STOCKDATE2 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3, 0) AS STOCKDATE3 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4, 0) AS STOCKDATE4 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5, 0) AS STOCKDATE5 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6, 0) AS STOCKDATE6 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7, 0) AS STOCKDATE7 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8, 0) AS STOCKDATE8 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9, 0) AS STOCKDATE9 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10, 0) AS STOCKDATE10 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11, 0) AS STOCKDATE11 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11 - INSTALLDATE12, 0) AS STOCKDATE12 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11 - INSTALLDATE12 - INSTALLDATE13, 0) AS STOCKDATE13 , NVL(STOCK_TOTAL - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - I