GROUP BY AGRG.OG_ID , AGRG.OG_NM , AGRG.PD_CD , PD_INF.PD_NM , AGRG.ITM_KND_CD , AGRG.WARE_NO /* SELECT 1 :: 서비스센터 / 상품별 집계 */ ) AGRG2 /* 엔지니어 재고 구하기 */ , (SELECT WARE_NO , SUM(STOC_QTY) AS ENG_QTY , PD_CD FROM ( (SELECT STOC_TYP , (CASE WHEN HGR_WARE_NO = '200609' THEN '200013' WHEN HGR_WARE_NO = '200467' THEN '200127' WHEN HGR_WARE_NO IN ('200007', '200008', '200926') THEN '200371' ELSE HGR_WARE_NO END ) AS WARE_NO , PD_CD , NVL(STOC_QTY,0) AS STOC_QTY FROM ( SELECT 'PRV_ENG' AS STOC_TYP /* 기초재고 */ /* SELECT 1 */ , HGR_WARE_NO , PD_CD , '000' AS STR_TP_CD , (CASE WHEN #{pdGdCd} = 'A' THEN BTD_STOC_A_GD_QTY + STR_QTY - OSTR_QTY + USE_QTY - USE_QTY2 WHEN #{pdGdCd} = 'B' THEN BTD_STOC_B_GD_QTY + STR_QTY + USE_QTY - CTR_QTY - OSTR_QTY - USE_QTY2 - CTR_QTY2 WHEN #{pdGdCd} = 'E' THEN BTD_STOC_E_GD_QTY + STR_QTY + USE_QTY - CTR_QTY - OSTR_QTY - USE_QTY2 - CTR_QTY2 WHEN #{pdGdCd} = 'R' THEN BTD_STOC_R_GD_QTY + STR_QTY + USE_QTY - CTR_QTY - OSTR_QTY - USE_QTY2 - CTR_QTY2 ELSE BTD_STOC_A_GD_QTY+ BTD_STOC_B_GD_QTY + BTD_STOC_E_GD_QTY + BTD_STOC_R_GD_QTY + STR_QTY + USE_QTY - OSTR_QTY - USE_QTY2 END) AS STOC_QTY FROM ( SELECT HGR_WARE_NO , PD_CD , NVL(SUM(BTD_STOC_A_GD_QTY), 0) BTD_STOC_A_GD_QTY , NVL(SUM(BTD_STOC_B_GD_QTY), 0) BTD_STOC_B_GD_QTY , NVL(SUM(BTD_STOC_E_GD_QTY), 0) BTD_STOC_E_GD_QTY , NVL(SUM(BTD_STOC_R_GD_QTY), 0) BTD_STOC_R_GD_QTY , NVL(SUM(STR_QTY), 0) STR_QTY , NVL(SUM(USE_QTY), 0) USE_QTY , NVL(SUM(CTR_QTY), 0) CTR_QTY , NVL(SUM(OSTR_QTY), 0) OSTR_QTY , NVL(SUM(USE_QTY2), 0) USE_QTY2 , NVL(SUM(CTR_QTY2), 0) CTR_QTY2 FROM ( SELECT * FROM E_M_STOC
UNION ALL
SELECT * FROM E_ITM_STR
UNION ALL
SELECT * FROM E_WK_OSTR1
UNION ALL
SELECT * FROM E_ITM_GD1
UNION ALL
SELECT * FROM E_ITM_OSTR
UNION ALL
SELECT * FROM E_WK_OSTR2
UNION ALL
SELECT * FROM E_ITM_GD2 ) T GROUP BY HGR_WARE_NO , PD_CD ) ) T2 )
UNION ALL
