)
SELECT PD_INF.PD_CD , PD_INF.PD_NM , 0 AS PAJU_QTY , 0 AS CENTER_QTY , 0 AS ENG_QTY , NVL(EOT_QTY,0) AS EOT_QTY /* 기말재고 */ , NVL(STOC_QTY, 0) AS STOC_QTY , NVL(EOT_QTY - INSTALLDATE1, 0) AS STOCKDATE1 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2, 0) AS STOCKDATE2 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3, 0) AS STOCKDATE3 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4, 0) AS STOCKDATE4 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5, 0) AS STOCKDATE5 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6, 0) AS STOCKDATE6 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7, 0) AS STOCKDATE7 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8, 0) AS STOCKDATE8 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9, 0) AS STOCKDATE9 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10, 0) AS STOCKDATE10 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11, 0) AS STOCKDATE11 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11 - INSTALLDATE12, 0) AS STOCKDATE12 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11 - INSTALLDATE12 - INSTALLDATE13, 0) AS STOCKDATE13 , NVL(EOT_QTY - INSTALLDATE1 - INSTALLDATE2 - INSTALLDATE3 - INSTALLDATE4 - INSTALLDATE5 - INSTALLDATE6 - INSTALLDATE7 - INSTALLDATE8 - INSTALLDATE9 - INSTALLDATE10 - INSTALLDATE11 - INSTALLDATE12 - INSTALLDATE13 - INSTALLDATE14, 0) AS STOCKDATE14 , NVL(INSTALLDATE1,0) AS INSTALLDATE1 , NVL(INSTALLDATE2,0) AS INSTALLDATE2 , NVL(INSTALLDATE3,0) AS INSTALLDATE3 , NVL(INSTALLDATE4,0) AS INSTALLDATE4 , NVL(INSTALLDATE5,0) AS INSTALLDATE5 , NVL(INSTALLDATE6,0) AS INSTALLDATE6 , NVL(INSTALLDATE7,0) AS INSTALLDATE7 , NVL(INSTALLDATE8,0) AS INSTALLDATE8 , NVL(INSTALLDATE9,0) AS INSTALLDATE9 , NVL(INSTALLDATE10,0) AS INSTALLDATE10 , NVL(INSTALLDATE11,0) AS INSTALLDATE11 , NVL(INSTALLDATE12,0) AS INSTALLDATE12 , NVL(INSTALLDATE13,0) AS INSTALLDATE13 , NVL(INSTALLDATE14,0) AS INSTALLDATE14 , NVL((INSTALLDATE1+INSTALLDATE2+INSTALLDATE3+INSTALLDATE4+INSTALLDATE5+INSTALLDATE6+INSTALLDATE7+INSTALLDATE8+INSTALLDATE9+INSTALLDATE10+INSTALLDATE11+INSTALLDATE12+INSTALLDATE13+INSTALLDATE14), 0) IST_TOTAL FROM ( SELECT V2.PD_CD , NVL(V1.STOC_QTY, 0) AS STOC_QTY , NVL(V1.BTD_QTY, 0) AS BTD_QTY /* 기초 */ , NVL(NVL(V1.BTD_QTY,0) + NVL(QTY_110,0) + NVL(QTY_121,0) + NVL(QTY_122,0 )+ NVL(QTY_123,0)+NVL(QTY_161,0)+NVL(QTY_162,0)+NVL(QTY_190,0) + NVL(QTY_221,0) + NVL(QTY_222,0) + NVL(QTY_223,0) + NVL(QTY_261,0)+NVL(QTY_262,0) + NVL(QTY_211,0) + NVL(QTY_212,0) + NVL(QTY_999,0) + NVL(QTY_217,0) + NVL(QTY_290,0),0) AS EOT_QTY /*기말 */ , SUM(CASE WHEN RN = '1' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE1 , SUM(CASE WHEN RN = '2' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE2 , SUM(CASE WHEN RN = '3' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE3 , SUM(CASE WHEN RN = '4' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE4 , SUM(CASE WHEN RN = '5' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE5 , SUM(CASE WHEN RN = '6' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE6 , SUM(CASE WHEN RN = '7' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE7 , SUM(CASE WHEN RN = '8' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE8 , SUM(CASE WHEN RN = '9' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE9 , SUM(CASE WHEN RN = '10' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE10 , SUM(CASE WHEN RN = '11' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE11 , SUM(CASE WHEN RN = '12' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE12 , SUM(CASE WHEN RN = '13' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE13 , SUM(CASE WHEN RN = '14' THEN NVL(CNFM_QTY,0) ELSE 0 END) AS INSTALLDATE14 FROM ( SELECT AGRG.WARE_NO , AGRG.PD_CD , SUM(AGRG.STOC_QTY) AS STOC_QTY , DECODE(STR_TP_CD, '110', SUM(STOC_QTY)) AS QTY_110 , DECODE(STR_TP_CD, '121', SUM(STOC_QTY)) AS QTY_121 , DECODE(STR_TP_CD, '122', SUM(STOC_QTY)) AS QTY_122 , DECODE(STR_TP_CD, '123', SUM(STOC_QTY)) AS QTY_123 , DECODE(STR_TP_CD, '161', SUM(STOC_QTY)) AS QTY_161 , DECODE(STR_TP_CD, '162', SUM(STOC_QTY)) AS QTY_162 , DECODE(STR_TP_CD, '190', SUM(STOC_QTY)) AS QTY_190 , DECODE(STR_TP_CD, '211', SUM(STOC_QTY)) AS QTY_211 , DECODE(STR_TP_CD, '212', SUM(STOC_QTY)) AS QTY_212 , DECODE(STR_TP_CD, '217', SUM(STOC_QTY)) AS QTY_217 , DECODE(STR_TP_CD, '221', SUM(STOC_QTY)) AS QTY_221 , DECODE(STR_TP_CD, '222', SUM(STOC_QTY)) AS QTY_222 , DECODE(STR_TP_CD, '223', SUM(STOC_QTY)) AS QTY_223 , DECODE(STR_TP_CD, '261', SUM(STOC_QTY)) AS QTY_261 , DECODE(STR_TP_CD, '262', SUM(STOC_QTY)) AS QTY_262 , DECODE(STR_TP_CD, '299', SUM(STOC_QTY)) AS QTY_299 , DECODE(STR_TP_CD, '999', SUM(STOC_QTY)) AS QTY_999 , DECODE(STR_TP_CD, '290', SUM(STOC_QTY)) AS QTY_290 , BTD.BTD_QTY /* 기초재고 */ FROM AGRG INNER JOIN BTD ON AGRG.PD_CD = BTD.ITM_PD_CD AND AGRG.WARE_NO = BTD.WARE_NO WHERE 1=1
AND AGRG.WARE_NO = #{svCnr}
GROUP BY AGRG.WARE_NO , AGRG.PD_CD , AGRG.STR_TP_CD , BTD.BTD_QTY ) V1 /* 설치배정현황 */ , ( SELECT T_DATE.TEMP_DATE , T1.PD_CD , T1.VST_CNFMDT , NVL(CNFM_QTY, 0) AS CNFM_QTY , RN FROM ( SELECT TEMP_DATE , DENSE_RANK() OVER(ORDER BY TEMP_DATE) RN FROM ( SELECT TO_CHAR((TO_DATE(#{baseDt}, 'YYYYMMDD') + LEVEL-1), 'YYYYMMDD') AS TEMP_DATE FROM DUAL CONNECT BY LEVEL <= 14 ) ) T_DATE LEFT OUTER JOIN ( SELECT S1.VST_CNFMDT , S2.PD_CD , SUM(DECODE(S1.SV_CNR_OG_ID, '', 0, 1)) AS CNFM_QTY FROM TB_SVPD_CST_SVAS_IST_ASN_IZ S1 /* AC221 고객서비스AS설치배정내역 */ LEFT OUTER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ S2 /* AC211 고객서비스AS설치대상내역 */ ON S1.CST_SV_ASN_NO = S2.CST_SV_ASN_NO WHERE S1.VST_CNFMDT BETWEEN #{baseDt} AND TO_CHAR((TO_DATE(#{baseDt}, 'YYYYMMDD')+30), 'YYYYMMDD') AND S2.MTR_STAT_CD IN ('1', '2') AND S1.WK_PRGS_STAT_CD IN ('00', '10') AND S2.SV_BIZ_HCLSF_CD = '1' AND S2.SV_BIZ_DCLSF_CD NOT LIKE '13%' AND S2.PD_CD IS NOT NULL
AND S1.SV_CNR_OG_ID = (CASE WHEN #{svCnr} = '201698' THEN '71357' WHEN #{svCnr} = '200001' THEN '71367' ELSE ( SELECT OG_ID FROM TB_SVST_MCBY_WARE_IZ WHERE APY_YM = #{baseDt} AND WARE_NO = #{svCnr} ) END)
AND S2.PD_GD_CD = #{pdGdCd}
GROUP BY S1.VST_CNFMDT , S2.PD_CD ) T1 ON T1.VST_CNFMDT = T_DATE.TEMP_DATE ) V2 WHERE V2.PD_CD = V1.PD_CD(+) GROUP BY V2.PD_CD , NVL(V1.STOC_QTY, 0) , NVL(V1.BTD_QTY, 0) /* 기초 */ , NVL(NVL(V1.BTD_QTY,0) + NVL(QTY_110,0) + NVL(QTY_121,0) + NVL(QTY_122,0 )+ NVL(QTY_123,0)+NVL(QTY_161,0)+NVL(QTY_162,0)+NVL(QTY_190,0) + NVL(QTY_221,0) + NVL(QTY_222,0) + NVL(QTY_223,0) + NVL(QTY_261,0)+NVL(QTY_262,0) + NVL(QTY_211,0) + NVL(QTY_212,0) + NVL(QTY_999,0) + NVL(QTY_217,0) + NVL(QTY_290,0),0) ) MAIN INNER JOIN PD_INF ON MAIN.PD_CD = PD_INF.PD_CD WHERE 1=1 /* AND PD_INF.ITM_KND_CD = '4' */ ORDER BY PD_INF.PD_CD

WITH WARE_INF AS ( SELECT APY_YM , WARE_DV_CD , WARE_NO , (CASE WHEN WARE_ICHR_NO != '000' THEN HGR_WARE_NO ELSE WARE_NO END) HGR_WARE_NO , WARE_ICHR_NO FROM TB_SVST_MCBY_WARE_IZ WHERE APY_YM = TO_CHAR(SYSDATE,'YYYYMM') AND WARE_DV_CD = '2' ) , PD_INF AS ( SELECT T1.PD_CD , T1.PD_NM , T2.PD_PRP_VAL19 AS ITM_KND_CD , T1.SAP_MAT_CD , T2.PD_PRP_VAL15 AS ORDNY_HV_MAT_YN FROM WSMDBS.TB_PDBS_PD_BAS T1 /*상품기본*/ INNER JOIN WSMDBS.TB_PDBS_PD_ECOM_PRP_DTL T2 /*상품각사속성상세*/ ON T1.PD_CD = T2.PD_CD AND T2.PD_EXTS_PRP_GRP_CD IN ('PART') AND T2.PD_PRP_VAL19 = '4' ) SELECT ITM_PD_CD , SUM(CASE WHEN WARE_DIV = 'PAJU' THEN QTY ELSE 0 END) QTY_PAJU_SUM , SUM(CASE WHEN WARE_DIV = 'CENTER' THEN QTY ELSE 0 END) QTY_CENTER_SUM , SUM(CASE WHEN WARE_DIV = 'ENG' THEN QTY ELSE 0 END) QTY_ENG_SUM FROM ( SELECT 'BASE' AS DIV , O1.ITM_PD_CD , NVL(O1.PITM_STOC_A_GD_QTY,0) AS QTY , O1.WARE_NO , (CASE WHEN O1.WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_MCITM_STOC_IZ O1 /* ST122 월별품목재고내역 */ LEFT OUTER JOIN WARE_INF ON O1.WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O1.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O1.BASE_YM = TO_CHAR(ADD_MONTHS(SYSDATE,-1),'YYYYMM')
UNION ALL
/* 재고실사입고 */ SELECT 'STR' AS DIV , O2.ITM_PD_CD , NVL(O2.STOC_ACINSP_STR_A_GD_QTY, 0) AS QTY , O2.WARE_NO , (CASE WHEN O2.WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_MCITM_STOC_IZ O2 /* ST122 월별품목재고내역 */ LEFT OUTER JOIN WARE_INF ON O2.WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O2.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O2.BASE_YM = TO_CHAR(SYSDATE,'YYYYMM')
UNION ALL
/* 이동입고 */ SELECT 'STR' AS DIV , O3.ITM_PD_CD , NVL(O3.STR_QTY, 0) AS QTY , O3.STR_WARE_NO AS WARE_NO , (CASE WHEN O3.STR_WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_ITM_STR_IZ O3 /* ST141 품목입고내역 */ LEFT OUTER JOIN WARE_INF ON O3.STR_WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O3.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O3.STR_QTY > 0 AND STR_RGST_DT BETWEEN TO_CHAR(SYSDATE,'YYYYMM')||'01' AND TO_CHAR(SYSDATE,'YYYYMM')||'31'
AND O3.ITM_GD_CD = #{pdGdCd}
UNION ALL
/* 외부반품입고 */ SELECT /*+ INDEX(O4 IX_SVST_SV_WK_OSTR_IZ_01) */ 'STR' AS DIV , O4.ITM_PD_CD , NVL(O4.USE_QTY,0) AS QTY , O4.WK_WARE_NO AS WARE_NO , (CASE WHEN O4.WK_WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_SV_WK_OSTR_IZ O4 /* ST163 서비스작업출고내역 */ LEFT OUTER JOIN WARE_INF ON O4.WK_WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O4.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O4.SV_BIZ_HCLSF_CD = '6'
AND O4.FNL_ITM_GD_CD = #{pdGdCd}
AND O4.USE_QTY > 0 AND O4.FNL_VST_FSH_DT BETWEEN TO_CHAR(SYSDATE,'YYYYMM')||'01' AND TO_CHAR(SYSDATE,'YYYYMM')||'31'
UNION ALL
/* 등급조정입고 */ SELECT 'STR' AS DIV , O5.PD_CD AS ITM_PD_CD , NVL(CTR_QTY,0) AS QTY , O5.WARE_NO , (CASE WHEN O5.WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_ITM_GD_CTR_IZ O5 /* ST173 품목등급조정내역 */ INNER JOIN WARE_INF ON O5.WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O5.PD_CD = PD_INF.PD_CD WHERE 1=1 AND O5.CTR_QTY > 0
AND O5.AFCT_ITM_GD_CD = #{pdGdCd}
AND O5.CTR_WK_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
UNION ALL
/* 재고실사출고 */ SELECT 'OSTR' AS DIV , O6.ITM_PD_CD , NVL(-STOC_ACINSP_OSTR_A_GD_QTY,0) AS QTY , O6.WARE_NO , (CASE WHEN O6.WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_MCITM_STOC_IZ O6 /* ST122 월별품목재고내역 */ LEFT OUTER JOIN WARE_INF ON O6.WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O6.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O6.STOC_ACINSP_OSTR_A_GD_QTY > 0 AND O6.BASE_YM = TO_CHAR(SYSDATE,'YYYYMM')
UNION ALL
/* 이동출고 */ SELECT 'OSTR' AS DIV , O7.ITM_PD_CD , NVL(-OSTR_QTY,0) AS QTY , O7.OSTR_WARE_NO AS WARE_NO , (CASE WHEN O7.OSTR_WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_ITM_OSTR_IZ O7 /* ST161 품목출고내역 */ LEFT OUTER JOIN WARE_INF ON O7.OSTR_WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O7.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND OSTR_QTY > 0 AND O7.OSTR_DT BETWEEN TO_CHAR(SYSDATE,'YYYYMM')||'01' AND TO_CHAR(SYSDATE,'YYYYMM')||'31'
AND ITM_GD_CD = #{pdGdCd}
UNION ALL
/* 작업(입고)출고 */ SELECT /*+ INDEX(O8 IX_SVST_SV_WK_OSTR_IZ_01) */ 'OSTR' AS DIV , O8.ITM_PD_CD , NVL(-O8.USE_QTY,0) AS QTY , O8.WK_WARE_NO AS WARE_NO , (CASE WHEN O8.WK_WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_SV_WK_OSTR_IZ O8 /* ST163 */ LEFT OUTER JOIN WARE_INF ON O8.WK_WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O8.ITM_PD_CD = PD_INF.PD_CD WHERE 1=1 AND O8.SV_BIZ_HCLSF_CD IN ('1','2','3') AND O8.USE_QTY > 0
AND O8.FNL_ITM_GD_CD = #{pdGdCd}
AND O8.FNL_VST_FSH_DT BETWEEN TO_CHAR(SYSDATE,'YYYYMM')||'01' AND TO_CHAR(SYSDATE,'YYYYMM')||'31'
UNION ALL
/* 등급조정 출고 */ SELECT 'OSTR' AS DIV , O9.PD_CD AS ITM_PD_CD , NVL(-O9.CTR_QTY,0) AS QTY , O9.WARE_NO , (CASE WHEN O9.WARE_NO = '100002' THEN 'PAJU' WHEN WARE_INF.WARE_ICHR_NO = '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'CENTER' WHEN WARE_INF.WARE_ICHR_NO != '000' AND WARE_INF.WARE_DV_CD = '2' THEN 'ENG' END ) AS WARE_DIV FROM TB_SVST_ITM_GD_CTR_IZ O9 /* ST173 품목등급조정내역 */ INNER JOIN WARE_INF ON O9.WARE_NO = WARE_INF.WARE_NO INNER JOIN PD_INF ON O9.PD_CD = PD_INF.PD_CD WHERE 1=1
AND O9.BFCT_ITM_GD_CD = #{pdGdCd}
AND O9.CTR_QTY > 0 AND PD_INF.ITM_KND_CD = '6' AND O9.STAT_CTR_APY_DT BETWEEN TO_CHAR(SYSDATE,'YYYYMM')||'01' AND TO_CHAR(SYSDATE,'YYYYMM')||'31' ) GROUP BY ITM_PD_CD
