mapper.select:
SELECT T1.SAP_CD /* SAP코드 */ , T1.SAP_GRP /* 자재그룹 */ , T1.ITM_PD_CD /* 품목상품코드 */ , T1.ITM_PD_NM /* 품목상품명 */ , T1.ITM_PD_ABBR1 /* 품목상품약어명 */ , T1.ITM_KND /* 품목종류코드 */ , T1.ITM_KND_NM /* 품목종류 */ , T1.DEL_UNT /* 출고단위코드 */ , T1.DEL_UNT_NM /* 출고단위 */ , T1.IMG_URL /* 이미지URL */ , T1.APLD_FR /* 사용시작일 */ , T1.APLD_TO /* 사용중지일 */ , T1.BOX_QTY /* 박스수량 */ , T1.LEAD_TIME /* 리드타임 */ , NVL(T2.CENTER_QTY, 0) AS CENTER_QTY /* 센터A등급 재고 */ , NVL(T2.MY_CENTER_QTY, 0) AS MY_CENTER_QTY /* 조직창고 수량 */ , NVL(T2.INDI_STCK_QTY, 0) AS INDI_STCK_QTY /* 개인창고 수량 */ , 0 AS LGST_QTY /* 물류재고 */ , NVL(T2.L_QTY, 0) AS L_QTY FROM ( SELECT S1.SVPD_SAP_CD AS SAP_CD /* SAP코드 */ , S1.SVPD_SAP_GRP AS SAP_GRP /* 자재그룹 */ , S1.SVPD_PD_CD AS ITM_PD_CD /* 품목상품코드 */ , S1.SVPD_NM_KOR AS ITM_PD_NM /* 품목상품명 */ , S1.SVPD_NM_ABBR1 AS ITM_PD_ABBR1 /* 품목상품약어명 */ , S1.SVPD_ITEM_KND AS ITM_KND /* 품목종류코드 */ , S1.SVPD_ITEM_KND_NM AS ITM_KND_NM /* 품목종류 */ , S1.SVPD_DEL_UNT AS DEL_UNT /* 출고단위코드 */ , S1.SVPD_DEL_UNT_NM AS DEL_UNT_NM /* 출고단위 */ , S1.SVPD_IMG_URL AS IMG_URL /* 이미지URL */ , S1.SVPD_APLD_FR AS APLD_FR /* 사용시작일 */ , S1.SVPD_APLD_TO AS APLD_TO /* 사용중지일 */ , S1.SVPD_BOX_QTY AS BOX_QTY /* 박스수량 */ , S1.SVPD_LT AS LEAD_TIME /* 리드타임 */ FROM ( ) S1
AND S1.SVPD_ITEM_KND = #{itmKndCd}
AND S1.SVPD_NM_KOR LIKE '%' || #{itmPdNm} || '%'
AND S1.SVPD_PD_CD LIKE #{itmPdCd}||'%'
) T1 /* 상품 */ LEFT OUTER JOIN ( SELECT D1.ITM_PD_CD , SUM(CASE WHEN D1.WARE_NO <> '100002' AND D2.WARE_DV_CD = '2' AND D2.WARE_DTL_DV_CD = '20' THEN D1.PITM_STOC_A_GD_QTY END) AS CENTER_QTY , SUM(CASE WHEN D1.WARE_NO = D3.HGR_WARE_NO THEN D1.PITM_STOC_A_GD_QTY END) AS MY_CENTER_QTY , SUM(CASE WHEN D2.HGR_WARE_NO = D3.HGR_WARE_NO THEN D1.PITM_STOC_A_GD_QTY END) AS INDI_STCK_QTY , SUM(D4.USE_QTY) AS L_QTY FROM TB_SVST_CST_SV_ITM_STOC_IZ D1 /* 고객서비스품목재고내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ D2 /* 월별창고내역 */ ON D2.WARE_NO = D1.WARE_NO LEFT OUTER JOIN ( SELECT MIN(HGR_WARE_NO) AS HGR_WARE_NO FROM TB_SVST_MCBY_WARE_IZ WHERE DTA_DL_YN = 'N' AND WARE_USE_YN = 'Y' AND APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND WARE_MNGT_PRTNR_NO = #{session.employeeIDNumber} AND HGR_WARE_NO NOT LIKE '1%' ) D3 ON 1 = 1 LEFT OUTER JOIN ( SELECT WK_WARE_NO , ITM_PD_CD , SUM(USE_QTY) AS USE_QTY FROM TB_SVST_SV_WK_OSTR_IZ /* 서비스작업출고내역 */ WHERE DTA_DL_YN = 'N' AND FNL_VST_FSH_DT LIKE TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM') || '%' AND ICHR_PRTNR_NO = #{session.employeeIDNumber} GROUP BY WK_WARE_NO, ITM_PD_CD ) D4 ON D4.WK_WARE_NO = D1.WARE_NO AND D4.ITM_PD_CD = D1.ITM_PD_CD WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.WARE_USE_YN = 'Y' AND D2.APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') GROUP BY D1.ITM_PD_CD ) T2 ON T2.ITM_PD_CD = T1.ITM_PD_CD ORDER BY T1.ITM_PD_CD
