mapper.update:
MERGE INTO TB_SVST_CST_SV_ITM_LCT_IZ T1 USING (
SELECT #{item.wareNo} AS WARE_NO /* 창고번호 */ , #{item.itmPdCd} AS ITM_PD_CD /* 품목코드 */ , #{item.itmLctAngleVal} AS ITM_LCT_ANGLE_VAL /* 앵글 */ , #{item.itmLctCofVal} AS ITM_LCT_COF_VAL /* 층수 */ , #{item.itmLctFlorNoVal} AS ITM_LCT_FLOR_NO_VAL /* 층번호 */ , #{item.itmLctMatGrpCd} AS ITM_LCT_MAT_GRP_CD /* 그룹 */ , #{item.itmKndCd} AS ITM_KND_CD /* 품목종류코드 */ FROM DUAL
) T2 ON ( T1.WARE_NO = T2.WARE_NO AND T1.ITM_PD_CD = T2.ITM_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.ITM_LCT_ANGLE_VAL = T2.ITM_LCT_ANGLE_VAL /* 앵글 */ , T1.ITM_LCT_COF_VAL = T2.ITM_LCT_COF_VAL /* 층수 */ , T1.ITM_LCT_FLOR_NO_VAL = T2.ITM_LCT_FLOR_NO_VAL /* 층번호 */ , T1.ITM_LCT_MAT_GRP_CD = T2.ITM_LCT_MAT_GRP_CD /* 그룹 */ , T1.ITM_KND_CD = T2.ITM_KND_CD /* 품목종류코드 */ , T1.DTA_DL_YN = 'N' /* 데이터삭제여부 */
WHEN NOT MATCHED THEN INSERT ( WARE_NO /* 창고번호 */ , ITM_PD_CD /* 품목상품코드 */ , ITM_LCT_ANGLE_VAL /* 앵글 */ , ITM_LCT_COF_VAL /* 층수 */ , ITM_LCT_FLOR_NO_VAL /* 층번호 */ , ITM_LCT_MAT_GRP_CD /* 그룹 */ , ITM_KND_CD /* 품목종류코드 */ , DTA_DL_YN /* 데이터삭제여부 */
) VALUES ( T2.WARE_NO /* 창고번호 */ , T2.ITM_PD_CD /* 품목상품코드 */ , T2.ITM_LCT_ANGLE_VAL /* 앵글 */ , T2.ITM_LCT_COF_VAL /* 층수 */ , T2.ITM_LCT_FLOR_NO_VAL /* 층번호 */ , T2.ITM_LCT_MAT_GRP_CD /* 그룹 */ , T2.ITM_KND_CD /* 품목종류코드 */ , 'N' /* 데이터삭제여부 */
)

mapper.select:
SELECT D1.PD_CD AS ITM_PD_CD , D1.PD_ABBR_NM AS PD_ABBR_NM , TO_CHAR(TO_NUMBER(D1.SAP_MAT_CD)) AS SAP_MAT_CD , D4.PD_PRP_VAL19 AS ITM_KND_CD , D5.WARE_NO , D5.WARE_NM , D5.STD_WARE_USE_YN , NVL(D3.PITM_STOC_A_GD_QTY, 0) AS PITM_STOC_A_GD_QTY , D2.WARE_TP_CD , TRIM(D2.ITM_LCT_ANGLE_VAL) AS ITM_LCT_ANGLE_VAL , D2.ITM_LCT_COF_VAL , D2.ITM_LCT_FLOR_NO_VAL , D2.ITM_LCT_MAT_GRP_CD , D2.WARE_TP_CD || TRIM(D2.ITM_LCT_ANGLE_VAL) || D2.ITM_LCT_COF_VAL || D2.ITM_LCT_FLOR_NO_VAL || D2.ITM_LCT_MAT_GRP_CD AS LOCATION_CD , TRIM( F_CMZ_CD_NM('TNT_WELLS', 'WARE_TP_CD', D2.WARE_TP_CD, #{session.langId}) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_ANGLE_CD', D2.ITM_LCT_ANGLE_VAL, #{session.langId}) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_COF_CD', D2.ITM_LCT_COF_VAL, #{session.langId}) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_FLOR_NO_CD', D2.ITM_LCT_FLOR_NO_VAL, #{session.langId}) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_MAT_GRP_CD', D2.ITM_LCT_MAT_GRP_CD, #{session.langId}) ) AS ITM_LCT_NM FROM TB_PDBS_PD_BAS D1 /* 상품기본 */ LEFT OUTER JOIN TB_SVST_CST_SV_ITM_STOC_IZ D3 /* 고객서비스품목재고내역 */ ON D3.ITM_PD_CD = D1.PD_CD AND D3.DTA_DL_YN = 'N'
AND D3.WARE_NO = #{wareNo}
INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D4 /* 상품각사속성상세 */ ON D4.PD_CD = D1.PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ D5 /* 월별창고내역 */ ON 1 = 1 LEFT OUTER JOIN TB_SVST_CST_SV_ITM_LCT_IZ D2 /* 고객서비스품목위치내역 */ ON D2.ITM_PD_CD = D1.PD_CD AND D2.DTA_DL_YN = 'N' AND D2.WARE_NO = CASE WHEN D5.STD_WARE_USE_YN = 'Y' THEN #{wareNo} ELSE '200012' END WHERE D1.DTA_DL_YN = 'N' AND D1.PD_TP_CD = 'M' AND D4.DTA_DL_YN = 'N' AND D4.PD_EXTS_PRP_GRP_CD = 'PART' AND D5.DTA_DL_YN = 'N' AND D5.APY_YM = TO_CHAR(SYSDATE, 'YYYYMM')
AND D1.PD_CD = #{itmPdCd}
AND D5.WARE_NO = #{wareNo}
ORDER BY ITM_PD_CD

mapper.update:
MERGE INTO TB_SVST_CST_SV_ITM_LCT_IZ T1 USING (
SELECT #{item.wareNo} AS WARE_NO , #{item.itmPdCd} AS ITM_PD_CD , #{item.wareTpCd} AS WARE_TP_CD , #{item.itmLctAngleVal} AS ITM_LCT_ANGLE_VAL , #{item.itmLctCofVal} AS ITM_LCT_COF_VAL , #{item.itmLctFlorNoVal} AS ITM_LCT_FLOR_NO_VAL , #{item.itmLctMatGrpCd} AS ITM_LCT_MAT_GRP_CD , #{item.itmKndCd} AS ITM_KND_CD FROM DUAL
) T2 ON ( T1.WARE_NO = T2.WARE_NO AND T1.ITM_PD_CD = T2.ITM_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.WARE_TP_CD = T2.WARE_TP_CD , T1.ITM_LCT_ANGLE_VAL = T2.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL = T2.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL = T2.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD = T2.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD = T2.ITM_KND_CD , T1.DTA_DL_YN = 'N'
WHEN NOT MATCHED THEN INSERT ( T1.WARE_NO , T1.ITM_PD_CD , T1.WARE_TP_CD , T1.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD , T1.DTA_DL_YN
) VALUES ( T2.WARE_NO , T2.ITM_PD_CD , T2.WARE_TP_CD , T2.ITM_LCT_ANGLE_VAL , T2.ITM_LCT_COF_VAL , T2.ITM_LCT_FLOR_NO_VAL , T2.ITM_LCT_MAT_GRP_CD , T2.ITM_KND_CD , 'N'
)

UPDATE TB_SVST_MCBY_WARE_IZ
   SET STD_WARE_USE_YN = #{stckStdGb}

 WHERE APY_YM  = TO_CHAR(SYSDATE, 'YYYYMM')

   AND WARE_NO = #{wareNo}
