mapper.select:
SELECT T1.SVPD_PD_CD AS ITM_PD_CD , T1.SVPD_NM_ABBR1 AS PD_ABBR_NM , T1.SVPD_SAP_CD AS SAP_MAT_CD , T1.SVPD_ITEM_KND AS ITM_KND_CD , T2.WARE_NO , T2.WARE_NM , T2.STD_WARE_USE_YN , T3.PITM_STOC_A_GD_QTY , TRIM(T4.ITM_LCT_ANGLE_VAL) AS ITM_LCT_ANGLE_VAL , T4.ITM_LCT_COF_VAL , T4.ITM_LCT_FLOR_NO_VAL , T4.ITM_LCT_MAT_GRP_CD , TRIM( F_CMZ_CD_NM('TNT_WELLS', 'LCT_ANGLE_CD', T4.ITM_LCT_ANGLE_VAL) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_COF_CD', T4.ITM_LCT_COF_VAL) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_FLOR_NO_CD', T4.ITM_LCT_FLOR_NO_VAL) || ' ' || F_CMZ_CD_NM('TNT_WELLS', 'LCT_MAT_GRP_CD', T4.ITM_LCT_MAT_GRP_CD) ) AS ITM_LCT_NM FROM () T1 INNER JOIN TB_SVST_MCBY_WARE_IZ T2 ON (T2.APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND T2.WARE_NO = #{wareNo}) LEFT OUTER JOIN TB_SVST_CST_SV_ITM_STOC_IZ T3 ON (T1.SVPD_PD_CD = T3.ITM_PD_CD AND T3.WARE_NO = #{wareNo} AND T3.DTA_DL_YN = 'N') LEFT OUTER JOIN TB_SVST_CST_SV_ITM_LCT_IZ T4 ON (T1.SVPD_PD_CD = T4.ITM_PD_CD AND T4.WARE_NO = #{wareNo} AND T4.DTA_DL_YN = 'N')
AND T1.SVPD_PD_CD = #{itmPdCd}
ORDER BY T1.SVPD_PD_CD

mapper.update:
MERGE INTO TB_SVST_CST_SV_ITM_LCT_IZ T1 USING ( SELECT #{item.wareNo} AS WARE_NO , #{item.itmPdCd} AS ITM_PD_CD , #{item.itmLctAngleVal} AS ITM_LCT_ANGLE_VAL , #{item.itmLctCofVal} AS ITM_LCT_COF_VAL , #{item.itmLctFlorNoVal} AS ITM_LCT_FLOR_NO_VAL , #{item.itmLctMatGrpCd} AS ITM_LCT_MAT_GRP_CD , #{item.itmKndCd} AS ITM_KND_CD FROM DUAL
) T2 ON ( T1.WARE_NO = T2.WARE_NO AND T1.ITM_PD_CD = T2.ITM_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.ITM_LCT_ANGLE_VAL = T2.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL = T2.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL = T2.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD = T2.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD = T2.ITM_KND_CD , T1.DTA_DL_YN = 'N'
WHEN NOT MATCHED THEN INSERT ( WARE_NO , ITM_PD_CD , ITM_LCT_ANGLE_VAL , ITM_LCT_COF_VAL , ITM_LCT_FLOR_NO_VAL , ITM_LCT_MAT_GRP_CD , ITM_KND_CD , DTA_DL_YN
)VALUES( T2.WARE_NO , T2.ITM_PD_CD , T2.ITM_LCT_ANGLE_VAL , T2.ITM_LCT_COF_VAL , T2.ITM_LCT_FLOR_NO_VAL , T2.ITM_LCT_MAT_GRP_CD , T2.ITM_KND_CD , 'N'
)
