mapper.update:
MERGE INTO TB_SVST_CST_SV_ITM_LCT_IZ T1 USING (
SELECT #{item.wareNo} AS WARE_NO , #{item.itmPdCd} AS ITM_PD_CD , #{item.itmLctAngleVal} AS ITM_LCT_ANGLE_VAL , #{item.itmLctCofVal} AS ITM_LCT_COF_VAL , #{item.itmLctFlorNoVal} AS ITM_LCT_FLOR_NO_VAL , #{item.itmLctMatGrpCd} AS ITM_LCT_MAT_GRP_CD , #{item.itmKndCd} AS ITM_KND_CD FROM DUAL
) T2 ON ( T1.WARE_NO = T2.WARE_NO AND T1.ITM_PD_CD = T2.ITM_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.ITM_LCT_ANGLE_VAL = T2.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL = T2.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL = T2.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD = T2.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD = T2.ITM_KND_CD , T1.DTA_DL_YN = 'N'
WHEN NOT MATCHED THEN INSERT ( WARE_NO , ITM_PD_CD , ITM_LCT_ANGLE_VAL , ITM_LCT_COF_VAL , ITM_LCT_FLOR_NO_VAL , ITM_LCT_MAT_GRP_CD , ITM_KND_CD , DTA_DL_YN
) VALUES ( T2.WARE_NO , T2.ITM_PD_CD , T2.ITM_LCT_ANGLE_VAL , T2.ITM_LCT_COF_VAL , T2.ITM_LCT_FLOR_NO_VAL , T2.ITM_LCT_MAT_GRP_CD , T2.ITM_KND_CD , 'N'
)

MERGE INTO TB_SVST_CST_SV_ITM_LCT_IZ T1 USING (
SELECT #{item.wareNo} AS WARE_NO , #{item.itmPdCd} AS ITM_PD_CD , #{item.wareTpCd} AS WARE_TP_CD , #{item.itmLctAngleVal} AS ITM_LCT_ANGLE_VAL , #{item.itmLctCofVal} AS ITM_LCT_COF_VAL , #{item.itmLctFlorNoVal} AS ITM_LCT_FLOR_NO_VAL , #{item.itmLctMatGrpCd} AS ITM_LCT_MAT_GRP_CD , #{item.itmKndCd} AS ITM_KND_CD FROM DUAL
) T2 ON ( T1.WARE_NO = T2.WARE_NO AND T1.ITM_PD_CD = T2.ITM_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.WARE_TP_CD = T2.WARE_TP_CD , T1.ITM_LCT_ANGLE_VAL = T2.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL = T2.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL = T2.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD = T2.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD = T2.ITM_KND_CD , T1.DTA_DL_YN = 'N'
WHEN NOT MATCHED THEN INSERT ( T1.WARE_NO , T1.ITM_PD_CD , T1.WARE_TP_CD , T1.ITM_LCT_ANGLE_VAL , T1.ITM_LCT_COF_VAL , T1.ITM_LCT_FLOR_NO_VAL , T1.ITM_LCT_MAT_GRP_CD , T1.ITM_KND_CD , T1.DTA_DL_YN
) VALUES ( T2.WARE_NO , T2.ITM_PD_CD , T2.WARE_TP_CD , T2.ITM_LCT_ANGLE_VAL , T2.ITM_LCT_COF_VAL , T2.ITM_LCT_FLOR_NO_VAL , T2.ITM_LCT_MAT_GRP_CD , T2.ITM_KND_CD , 'N'
)

UPDATE TB_SVST_MCBY_WARE_IZ
   SET STD_WARE_USE_YN = #{stckStdGb}

 WHERE APY_YM  = TO_CHAR(SYSDATE, 'YYYYMM')

   AND WARE_NO = #{wareNo}
