 O2.BASE_YM = S1.MNGT_YM AND O2.DTA_DL_YN = 'N' INNER JOIN TB_OGBS_BLD_BAS O3 ON O2.OG_TP_CD = O3.OG_TP_CD AND O2.BLD_CD = O3.BLD_CD AND O3.DTA_DL_YN = 'N' WHERE S1.DTA_DL_YN = 'N' AND S1.CNTR_DTL_STAT_CD NOT IN ('401','402') AND S1.MNGT_YM = #{mngtYm}
