mapper.select:
SELECT M1.BLD_NM , M1.BLD_CD , M1.PRTNR_NO , M1.OG_CD , M1.PRTNR_KNM , M1.VST_CST_N , NVL(M2.WRFR, 0) AS WRFR , NVL(M2.BDT_INDV, 0) AS BDT_INDV , NVL(M2.BDT_CRP, 0) AS BDT_CRP , NVL(M2.ARCLE_INDV, 0) AS ARCLE_INDV , NVL(M2.ARCLE_CRP, 0) AS ARCLE_CRP , NVL(M2.WTR_SFTNR, 0) AS WTR_SFTNR , NVL(M2.CFF_MCHN, 0) AS CFF_MCHN , NVL(M2.MSGCR, 0) AS MSGCR , NVL(M2.DRYR, 0) AS DRYR , NVL(M2.WASH, 0) AS WASH , NVL(M2.ARDRSSR, 0) AS ARDRSSR , NVL(M2.SSCLING, 0) AS SSCLING FROM (SELECT O1.PRTNR_NO , O1.PRTNR_KNM , O1.OG_CD , O3.BLD_CD , O3.BLD_NM , COUNT(1) VST_CST_N FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ S1 INNER JOIN TB_SVPD_CST_SV_EXCN_IZ S2 ON S1.CNTR_NO = S2.CNTR_NO AND S1.CNTR_SN = S2.CNTR_SN AND S2.REQD_DT IS NULL AND S2.CPS_DT IS NULL AND S2.CNTR_DTL_STAT_CD = '101' INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON S1.CNTC_ICHR_PRTNR_OG_TP_CD = O1.OG_TP_CD AND S1.CNTC_ICHR_PRTNR_NO = O1.PRTNR_NO AND O1.BASE_YM = #{mngtYm} INNER JOIN TB_OGBS_MM_OG_IZ O2 ON O1.OG_ID = O2.OG_ID AND O1.OG_TP_CD = O2.OG_TP_CD AND O2.BASE_YM = #{mngtYm} INNER JOIN TB_OGBS_BLD_BAS O3 ON O2.OG_TP_CD = O3.OG_TP_CD AND O2.BLD_CD = O3.BLD_CD WHERE 1 = 1 AND S1.SV_BIZ_DCLSF_CD != '2140' AND S1.ASN_OJ_YM = #{mngtYm}
AND O3.BLD_CD IN (
#{bldCd}
)
GROUP BY O1.PRTNR_NO, O1.PRTNR_KNM, O1.OG_CD, O3.BLD_CD, O3.BLD_NM ) M1 LEFT JOIN (SELECT S1.CNFM_PSIC_PRTNR_OG_TP_CD AS OG_TP_CD , S1.CNFM_PSIC_PRTNR_NO AS PRTNR_NO , SUM(CASE WHEN P1.PD_PRP_VAL20 = '1' AND S3.COPN_DV_CD = '1' THEN 1 ELSE 0 END) AS BDT_INDV , SUM(CASE WHEN P1.PD_PRP_VAL20 = '1' AND S3.COPN_DV_CD = '2' THEN 1 ELSE 0 END) AS BDT_CRP , SUM(CASE WHEN P1.PD_PRP_VAL20 = '2' THEN 1 ELSE 0 END) AS WRFR , SUM(CASE WHEN P1.PD_PRP_VAL20 = '3' AND S3.COPN_DV_CD = '1' THEN 1 ELSE 0 END) AS ARCLE_INDV , SUM(CASE WHEN P1.PD_PRP_VAL20 = '3' AND S3.COPN_DV_CD = '2' THEN 1 ELSE 0 END) AS ARCLE_CRP , SUM(CASE WHEN P1.PD_PRP_VAL20 = '4' THEN 1 ELSE 0 END) AS WTR_SFTNR , SUM(CASE WHEN P1.PD_PRP_VAL20 = '93' THEN 1 ELSE 0 END) AS MSGCR , SUM(CASE WHEN P1.PD_PRP_VAL20 = '96' THEN 1 ELSE 0 END) AS CFF_MCHN , SUM(CASE WHEN P1.PD_CD IN ('WM04100121' , 'WM04100152' , 'WM04100153') THEN 1 ELSE 0 END) AS DRYR /* 건조기 */ , SUM(CASE WHEN P1.PD_CD IN ('WM04100127' , 'WM04100155' , 'WM04100156') THEN 1 ELSE 0 END) AS WASH /* 세탁기 */ , SUM(CASE WHEN P1.PD_CD IN ('WM04100164', 'WM04100166', 'WM04100192', 'WM04100193', 'WM04100194', 'WM07101222') THEN 1 ELSE 0 END) AS ARDRSSR /* 에어드레서 */ , SUM(CASE WHEN P1.PD_CD IN ('WM04100200', 'WM04100201', 'WM04100202') THEN 1 ELSE 0 END) AS SSCLING /* 삼성청소기 */ , COUNT(1) CNT FROM TB_SVPD_CST_SV_BFSVC_ASN_IZ S1 INNER JOIN TB_SVPD_CST_SV_EXCN_IZ S2 ON S1.CNTR_NO = S2.CNTR_NO AND S1.CNTR_SN = S2.CNTR_SN AND S2.REQD_DT IS NULL AND S2.CPS_DT IS NULL AND S2.CNTR_DTL_STAT_CD = '101' INNER JOIN TB_SSCT_CNTR_BAS S3 ON S1.CNTR_NO = S3.CNTR_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL P1 ON S2.PDCT_PD_CD = P1.PD_CD AND P1.PD_EXTS_PRP_GRP_CD = 'PART' AND P1.PD_PRP_VAL20 != '11' WHERE 1 = 1 AND S1.SV_BIZ_DCLSF_CD != '2140' AND S1.ASN_OJ_YM = #{mngtYm} GROUP BY S1.CNFM_PSIC_PRTNR_OG_TP_CD, S1.CNFM_PSIC_PRTNR_NO ) M2 ON M1.PRTNR_NO = M2.PRTNR_NO WHERE 1 = 1 ORDER BY M1.BLD_NM

mapper.insert:
MERGE INTO TB_SVST_BIZ_CL_BAS T1 USING (SELECT #{mngtYm} AS MNGT_YM , #{bizStrtdt} AS BIZ_STRTDT , #{bizStrtHh} || '00' AS BIZ_STRT_HH , #{bizEnddt} AS BIZ_ENDDT , #{bizEndHh} || '00' AS BIZ_END_HH FROM DUAL ) T2 ON (T1.MNGT_YM = T2.MNGT_YM AND T1.CL_MNGT_DV_CD = '6' ) WHEN MATCHED THEN UPDATE SET T1.BIZ_STRTDT = T2.BIZ_STRTDT , T1.BIZ_STRT_HH = T2.BIZ_STRT_HH , T1.BIZ_ENDDT = T2.BIZ_ENDDT , T1.BIZ_END_HH = T2.BIZ_END_HH
WHEN NOT MATCHED THEN INSERT ( T1.MNGT_YM , T1.CL_MNGT_DV_CD , T1.BIZ_STRTDT , T1.BIZ_STRT_HH , T1.BIZ_ENDDT , T1.BIZ_END_HH , T1.DTA_DL_YN
) VALUES ( T2.MNGT_YM , '6' , T2.BIZ_STRTDT , T2.BIZ_STRT_HH , T2.BIZ_ENDDT , T2.BIZ_END_HH , 'N'
)

MERGE INTO TB_SVST_BFSVC_CSMB_DDLV_IZ T1 USING ( SELECT #{item.mngtYm} AS MNGT_YM , #{item.bfsvcCsmbDdlvOjCd} AS BFSVC_CSMB_DDLV_OJ_CD , #{item.strWareNo} AS STR_WARE_NO , #{item.csmbPdCd} AS CSMB_PD_CD , #{item.sapMatCd} AS SAP_MAT_CD , TO_NUMBER(#{item.bfsvcCsmbDdlvQty}) AS BFSVC_CSMB_DDLV_QTY , #{item.bfsvcCsmbDdlvStatCd} AS BFSVC_CSMB_DDLV_STAT_CD FROM DUAL
) T2 ON ( T1.MNGT_YM = T2.MNGT_YM AND T1.BFSVC_CSMB_DDLV_OJ_CD = T2.BFSVC_CSMB_DDLV_OJ_CD AND T1.STR_WARE_NO = T2.STR_WARE_NO AND T1.CSMB_PD_CD = T2.CSMB_PD_CD ) WHEN MATCHED THEN UPDATE SET T1.SAP_MAT_CD = T2.SAP_MAT_CD , T1.BFSVC_CSMB_DDLV_QTY = T2.BFSVC_CSMB_DDLV_QTY , T1.BFSVC_CSMB_DDLV_STAT_CD = T2.BFSVC_CSMB_DDLV_STAT_CD
WHEN NOT MATCHED THEN INSERT ( MNGT_YM , BFSVC_CSMB_DDLV_OJ_CD , STR_WARE_NO , CSMB_PD_CD , SAP_MAT_CD , BFSVC_CSMB_DDLV_QTY , BFSVC_CSMB_DDLV_STAT_CD , DTA_DL_YN
) VALUES ( T2.MNGT_YM , T2.BFSVC_CSMB_DDLV_OJ_CD , T2.STR_WARE_NO , T2.CSMB_PD_CD , T2.SAP_MAT_CD , T2.BFSVC_CSMB_DDLV_QTY , T2.BFSVC_CSMB_DDLV_STAT_CD , 'N'
)

mapper.update:
UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET OSTR_AK_NO = #{ostrAkNo}
     , OSTR_AK_SN = #{ostrAkSn}

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO = #{strWareNo}
   AND CSMB_PD_CD = #{csmbPdCd}

UPDATE TB_SVST_BFSVC_CSMB_DDLV_IZ
   SET BFSVC_CSMB_DDLV_STAT_CD = '30'

 WHERE 1 = 1
   AND MNGT_YM = #{mngtYm}
   AND BFSVC_CSMB_DDLV_OJ_CD = #{bfsvcCsmbDdlvOjCd}
   AND STR_WARE_NO = #{strWareNo}
   AND CSMB_PD_CD = #{csmbPdCd}
