mapper.select:
SELECT COUNT(1) AS CNT
FROM TB_SSSO_SPP_BZS_IVC_PROCS_IZ
WHERE 1=1
 AND CNTR_NO = #{cntrNo}
 AND CNTR_SN = #{cntrSn}
 AND SPP_IVC_NO = #{sppIvcNo}

mapper.insert:
INSERT INTO TB_SSSO_SPP_BZS_IVC_PROCS_IZ /* 배송업체송장처리내역 */
(
    SPP_BZS_PROCS_ID /* 배송업체처리ID */
  , CNTR_NO          /* 계약번호 */
  , CNTR_SN          /* 계약일련번호 */
  , SPP_IVC_NO       /* 송장번호 */
  , SPP_BZS_PD_ID    /* SR번호 */
  , SPP_PROCS_BZS_CD /* 배송업체(택배사) */

)
VALUES
(
    (SELECT NVL(MAX(TO_NUMBER(SPP_BZS_PROCS_ID)), 0) + 1 FROM TB_SSSO_SPP_BZS_IVC_PROCS_IZ)
  , #{cntrNo}
  , #{cntrSn}
  , #{sppIvcNo}
  , #{sppBzsPdId}
  , #{pcsvCompDv}

)
