mapper.select:
SELECT WARE_NO /* 창고번호 */ , WARE_NM || CASE WHEN #{hgrWareNo} IS NOT NULL THEN '[' || WARE_MNGT_PRTNR_NO || ']' END || CASE WHEN WARE_USE_YN <> 'Y' /* 미사용인 경우만 (미사용) 표기 */ THEN ( SELECT '(' || MLNG_CNTN || ')' FROM T_CMZ_MLNG_D WHERE TENANT_ID = 'TNT_BASE' AND MLNG_ID = 'MSG_TXT_NUSD' AND LNG_ID = #{session.langId} ) END AS WARE_NM /* 창고명 */ FROM TB_SVST_MCBY_WARE_IZ /* 월별창고내역 */ WHERE DTA_DL_YN = 'N' AND APY_YM = #{baseYm} AND WARE_DV_CD = #{wareDvCd}
AND WARE_DTL_DV_CD = '20'
AND WARE_DTL_DV_CD = '30'
AND HGR_WARE_NO = #{hgrWareNo}
ORDER BY WARE_USE_YN DESC, TO_NUMBER(SORT_DV_VAL), WARE_NM, WARE_NO
