mapper.select:
SELECT D1.STR_TP_CD /* 입고유형코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'STR_TP_CD', D1.STR_TP_CD, #{session.langId}) AS STR_TP_NM /* 입고유형명*/ , D1.STR_WARE_NO /* 입고창고번호 */ , D3.WARE_NM AS STR_WARE_NM /* 입고창고명*/ , D1.STR_RGST_DT /* 입고등록일자 */ , D1.DLVG_DLPNR_NO /* 납품거래처번호*/ , D1.ITM_STR_NO /* 품목입고번호 */ , MIN(D1.STR_SN) AS STR_SN /* 입고순번 */ , D1.OSTR_TP_CD /* 출고유형코드 */ , D1.OSTR_WARE_NO /* 출고창고번호 */ , D4.WARE_NM AS OSTR_WARE_NM /* 출고창고명 */ , D2.STR_HOP_DT /* 입고희망일자 */ , D1.OSTR_DT /* 출고일자 */ , D1.ITM_OSTR_NO /* 품목출고번호 */ , MIN(D1.OSTR_SN) AS OSTR_SN /* 출고순번 */ FROM TB_SVST_ITM_STR_IZ D1 /* 품목입고내역 */ INNER JOIN TB_SVST_ITM_OSTR_IZ D2 /* 품목출고내역 */ ON D2.ITM_OSTR_NO = D1.ITM_OSTR_NO AND D2.OSTR_SN = D1.OSTR_SN INNER JOIN TB_SVST_MCBY_WARE_IZ D3 /* 월별창고내역(입고) */ ON D3.APY_YM = SUBSTR(D1.STR_RGST_DT, 1, 6) AND D3.WARE_NO = D1.STR_WARE_NO INNER JOIN TB_SVST_MCBY_WARE_IZ D4 /* 월별창고내역(출고) */ ON D4.APY_YM = SUBSTR(D1.STR_RGST_DT, 1, 6) AND D4.WARE_NO = D1.OSTR_WARE_NO WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D3.DTA_DL_YN = 'N' AND D4.DTA_DL_YN = 'N' AND D1.STR_WARE_NO = #{strOjWareNo} AND D1.STR_RGST_DT BETWEEN #{stStrDt} AND #{edStrDt} AND D1.STR_CONF_DT IS NOT NULL
AND D4.WARE_DV_CD = #{wareDvCd}
AND D1.STR_TP_CD = #{strTpCd}
AND D1.STR_TP_CD IN ('121','122','123','162','161')
GROUP BY D1.STR_TP_CD, D1.STR_WARE_NO, D3.WARE_NM, D1.STR_RGST_DT, D1.ITM_STR_NO, D2.STR_HOP_DT , D1.OSTR_TP_CD, D1.OSTR_WARE_NO, D4.WARE_NM, D1.OSTR_DT, D1.ITM_OSTR_NO, D1.DLVG_DLPNR_NO ORDER BY D1.STR_TP_CD, D1.STR_WARE_NO, D1.STR_RGST_DT, D1.ITM_STR_NO

SELECT T1.STR_RGST_DT /* 입고일자 */ , T1.STR_TP_CD /* 입고유형코드 */ , T1.ITM_STR_NO /* 품목입고번호 */ , T1.ITM_PD_CD /* 품목상품코드 */ , T3.SVPD_NM_ABBR1 || ' 외' AS ITM_PD_NM /* 품목상품명 */ , T1.STR_SN /* 입고순번 */ , T1.STR_WARE_NO /* 입고창고번호 */ , T2.WARE_NM /* 창고번호 */ , T1.OSTR_WARE_NO /* 출고창고번호 */ , T4.WARE_NM AS OSTR_WARE_NM /* 출고창고명 */ , (SELECT S1.OSTR_SN FROM TB_SVST_ITM_OSTR_IZ S1 WHERE S1.OSTR_WARE_NO = T1.OSTR_WARE_NO AND S1.ITM_PD_CD = T1.ITM_PD_CD AND S1.ITM_STR_NO = T1.ITM_STR_NO AND S1.STR_SN = T1.STR_SN AND S1.DTA_DL_YN = 'N' ) AS OSTR_SN /* 출고순번 */ , (SELECT S1.STR_HOP_DT FROM TB_SVST_ITM_OSTR_AK_IZ S1 WHERE S1.OSTR_AK_NO = T1.OSTR_AK_NO AND S1.DTA_DL_YN = 'N' AND ROWNUM = 1 ) AS STR_HOP_DT /* 입고희망일자 */ FROM TB_SVST_ITM_STR_IZ T1 /* 품목입고내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ T2 /* 월별창고내역(입고) */ ON T1.STR_WARE_NO = T2.WARE_NO AND T2.DTA_DL_YN = 'N' INNER JOIN () T3 ON T3.SVPD_PD_CD = T1.ITM_PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T4 /* 월별창고내역(출고) */ ON T1.OSTR_WARE_NO = T4.WARE_NO AND T2.APY_YM = T4.APY_YM AND T4.DTA_DL_YN = 'N' WHERE 1 = 1 AND T1.STR_WARE_NO = #{strOjWareNo}
AND T4.WARE_DV_CD = #{wareDvCd}
AND T1.STR_TP_CD = #{strTpCd}
AND T1.STR_TP_CD IN ('121','122','123','162','161')
AND T2.APY_YM = SUBSTR(#{stStrDt}, 1, 6) AND T1.STR_RGST_DT BETWEEN #{stStrDt} AND #{edStrDt} AND T1.STR_CONF_DT IS NULL AND T1.STR_SN = (SELECT MIN(W1.STR_SN) FROM TB_SVST_ITM_STR_IZ W1 WHERE W1.ITM_STR_NO = T1.ITM_STR_NO AND W1.STR_CONF_DT IS NULL) AND T1.DTA_DL_YN = 'N' ORDER BY STR_TP_CD, ITM_STR_NO ,STR_SN

SELECT T1.ITM_OSTR_NO /* 품목출고번호 */ , T1.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , T1.OSTR_TP_CD /* 출고유형코드 */ , T1.OSTR_WARE_NO /* 출고창고번호 */ , T1.OSTR_DT /* 출고일자 */ , T1.STR_WARE_NO /* 입고창고번호 */ , T1.ITM_STR_NO /* 품목입고번호 */ , T1.ACB_DT /* 회계일자 */ , T1.EVID_DV_CD /* 증빙구분코드 */ , T1.STR_HOP_DT /* 입고희망일자 */ , T2.WARE_NM AS OSTR_WARE_NM /* 출고창고명 */ , T4.WARE_NM AS STR_WARE_NM /* 입고창고명 */ , T3.SVPD_NM_ABBR1 || ' 외' AS PD_NM /* 상품명 */ FROM TB_SVST_ITM_OSTR_IZ T1 /*품목출고내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ T2 /* 월별창고내역 */ ON T1.OSTR_WARE_NO = T2.WARE_NO AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' INNER JOIN () T3 ON T1.ITM_PD_CD = T3.SVPD_PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ T4 /* 월별창고내역 */ ON T1.STR_WARE_NO = T4.WARE_NO AND T4.DTA_DL_YN = 'N' WHERE T2.APY_YM BETWEEN SUBSTR(#{stOstrDt}, 1, 6) AND SUBSTR(#{edOstrDt}, 1, 6) AND T1.STR_WARE_NO = #{strWareNo} AND T1.STR_CONF_DT IS NULL AND T1.OSTR_DT BETWEEN #{stOstrDt} AND #{edOstrDt} AND T1.OSTR_TP_CD IN ('221','222','223','261','262') AND T1.OSTR_SN = (SELECT MIN(W1.OSTR_SN) FROM TB_SVST_ITM_OSTR_IZ W1 WHERE W1.ITM_STR_NO = T1.ITM_STR_NO AND W1.STR_CONF_DT IS NULL)
AND T1.OSTR_TP_CD = #{ostrTpCd}
GROUP BY T1.ITM_OSTR_NO , T1.OSTR_TP_CD , T1.OSTR_WARE_NO , T1.OSTR_DT , T1.STR_WARE_NO , T1.ACB_DT , T1.EVID_DV_CD , T1.WARE_MNGT_PRTNR_NO , T1.ITM_STR_NO , T1.STR_HOP_DT , T2.WARE_NM , T4.WARE_NM , T3.PD_NM ORDER BY T1.OSTR_TP_CD, T1.OSTR_WARE_NO, T1.OSTR_DT

mapper.update:
UPDATE TB_SVST_ITM_STR_IZ T1    /* 품목입고내역 */
   SET T1.STR_CONF_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
     , T1.STR_CONF_HH = TO_CHAR(SYSDATE,'HH24MISS')
     , T1.STR_CONF_PRTNR_NO = #{session.employeeIDNumber}
     , T1.STR_CONF_PRTNR_OG_TP_CD = ( SELECT OG_TP_CD
                                        FROM TB_SVST_MCBY_WARE_IZ
                                       WHERE APY_YM  = TO_CHAR(SYSDATE,'YYYYMM')
                                         AND WARE_NO = #{strWareNo} )

 WHERE T1.ITM_STR_NO = #{itmStrNo}
   AND T1.STR_SN = #{strSn}

UPDATE TB_SVST_ITM_OSTR_IZ T1 /* 품목출고내역 */
   SET STR_CONF_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
     , STR_CONF_HH = TO_CHAR(SYSDATE,'HH24MISS')
     , STR_CONF_PRTNR_NO = #{session.employeeIDNumber}
     , STR_CONF_PRTNR_OG_TP_CD = ( SELECT OG_TP_CD
                                     FROM TB_SVST_MCBY_WARE_IZ
                                    WHERE APY_YM  = TO_CHAR(SYSDATE,'YYYYMM')
                                      AND WARE_NO = #{strWareNo} )

 WHERE T1.ITM_STR_NO = #{itmStrNo}
   AND T1.STR_SN = #{strSn}

mapper.select:
SELECT COUNT(1) AS CNT /* 카운트*/
  FROM TB_SVST_MCBY_WARE_CL_IZ T1 /* 월별창고마감내역 */
 WHERE T1.APY_YM     = #{apyYm}
   AND T1.WARE_NO    = #{wareNo}
   AND T1.WARE_CL_YN = 'Y'
   AND T1.DTA_DL_YN  = 'N'
