mapper.select:
SELECT STR_IZ.STR_TP_CD /*입고유형코드*/ , STR_IZ.STR_WARE_NO /*입고창고번호*/ , STR_IZ.STR_SN /*입고일련번호*/ , STR_IZ.STR_RGST_DT /*입고등록일자*/ , STR_IZ.DLVG_DLPNR_NO /*납품거래처번호*/ , STR_IZ.ITM_STR_NO /*품목입고번호*/ , STR_IZ.OSTR_TP_CD /*출고유형코드*/ , STR_IZ.OSTR_WARE_NO /*출고창고번호*/ , STR_IZ.OSTR_DT /*출고일자*/ , STR_IZ.OSTR_SN /*출고일련번호*/ , STR_IZ.ITM_OSTR_NO /*품목출고번호*/ , WARE_IZ.WARE_NM /*창고명*/ FROM TB_SVST_ITM_STR_IZ STR_IZ --품목입고내역 INNER JOIN TB_SVST_ITM_OSTR_IZ OSTR_IZ --품목출고내역 ON OSTR_IZ.ITM_OSTR_NO = STR_IZ.ITM_OSTR_NO AND OSTR_IZ.OSTR_SN = STR_IZ.OSTR_SN LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ WARE_IZ --월별창고 ON STR_IZ.OSTR_WARE_NO = WARE_IZ.WARE_NO AND WARE_IZ.WARE_USE_YN = 'Y' AND WARE_IZ.APY_YM BETWEEN SUBSTR(#{stStrDt},1,6) AND SUBSTR(#{edStrDt},1,6) WHERE 1 = 1 AND STR_IZ.STR_WARE_NO = #{strOjWareNo} AND STR_IZ.STR_CONF_DT IS NOT NULL AND STR_IZ.STR_RGST_DT BETWEEN #{stStrDt} AND #{edStrDt}
AND WARE_IZ.WARE_DV_CD = #{wareDvCd}
AND STR_IZ.STR_TP_CD = #{strTpCd}
AND STR_IZ.STR_TP_CD IN ('121','122','123','162','161')
GROUP BY STR_IZ.STR_TP_CD ,STR_IZ.STR_WARE_NO ,STR_IZ.STR_SN ,STR_IZ.STR_RGST_DT ,STR_IZ.DLVG_DLPNR_NO ,STR_IZ.ITM_STR_NO ,STR_IZ.OSTR_TP_CD ,STR_IZ.OSTR_WARE_NO ,STR_IZ.OSTR_DT ,STR_IZ.OSTR_SN ,STR_IZ.ITM_OSTR_NO ,WARE_IZ.WARE_NM ORDER BY STR_IZ.STR_TP_CD, STR_IZ.STR_WARE_NO, STR_IZ.STR_RGST_DT, STR_IZ.ITM_STR_NO

SELECT T1.STR_RGST_DT /*입고일자*/ , T1.STR_TP_CD /*입고유형코드*/ , T1.ITM_STR_NO /*품목입고번호*/ , T1.ITM_PD_CD , T3.SVPD_NM_ABBR1 || ' 외' AS ITM_PD_NM , T1.STR_SN , T1.STR_WARE_NO /*입고창고번호*/ , T2.WARE_NM /*창고번호*/ , T1.OSTR_WARE_NO , T4.WARE_NM AS OSTR_WARE_NM , (SELECT S1.OSTR_SN FROM TB_SVST_ITM_OSTR_IZ S1 WHERE S1.OSTR_WARE_NO = T1.OSTR_WARE_NO AND S1.ITM_PD_CD = T1.ITM_PD_CD AND S1.ITM_STR_NO = T1.ITM_STR_NO AND S1.STR_SN = T1.STR_SN ) AS OSTR_SN , (SELECT S1.STR_HOP_DT FROM TB_SVST_ITM_OSTR_AK_IZ S1 WHERE S1.OSTR_AK_NO = T1.OSTR_AK_NO AND ROWNUM = 1 ) AS STR_HOP_DT FROM TB_SVST_ITM_STR_IZ T1 /* 품목입고내역 */ LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T2 ON T1.STR_WARE_NO = T2.WARE_NO INNER JOIN () T3 ON T3.SVPD_PD_CD = T1.ITM_PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T4 ON T1.OSTR_WARE_NO = T4.WARE_NO AND T2.APY_YM = T4.APY_YM WHERE 1 = 1 AND T1.STR_WARE_NO = #{strOjWareNo}
AND T2.WARE_DV_CD = #{wareDvCd}
AND T1.STR_TP_CD = #{strTpCd}
AND T1.STR_TP_CD IN ('121','122','123','162','161')
AND T2.APY_YM BETWEEN SUBSTR(#{stStrDt},1,6) AND SUBSTR(#{edStrDt},1,6) AND T1.STR_RGST_DT BETWEEN #{stStrDt} AND #{edStrDt} AND T1.STR_CONF_DT IS NULL AND T1.STR_SN = (SELECT MIN(W1.STR_SN) FROM TB_SVST_ITM_STR_IZ W1 WHERE W1.ITM_STR_NO = T1.ITM_STR_NO AND W1.STR_CONF_DT IS NULL) ORDER BY STR_TP_CD, ITM_STR_NO ,STR_SN

SELECT T1.ITM_OSTR_NO , T1.WARE_MNGT_PRTNR_NO , T1.OSTR_TP_CD , T1.OSTR_WARE_NO , T1.OSTR_DT , T1.STR_WARE_NO , T1.ITM_STR_NO , T1.ACB_DT , T1.EVID_DV_CD , T1.STR_HOP_DT , T2.WARE_NM AS OSTR_WARE_NM , T4.WARE_NM AS STR_WARE_NM , T3.SVPD_NM_ABBR1 || ' 외' AS PD_NM FROM TB_SVST_ITM_OSTR_IZ T1 INNER JOIN TB_SVST_MCBY_WARE_IZ T2 ON T1.OSTR_WARE_NO = T2.WARE_NO INNER JOIN () T3 ON T1.ITM_PD_CD = T3.SVPD_PD_CD INNER JOIN TB_SVST_MCBY_WARE_IZ T4 ON T1.STR_WARE_NO = T4.WARE_NO WHERE T2.APY_YM BETWEEN SUBSTR(#{stOstrDt}, 1, 6) AND SUBSTR(#{edOstrDt}, 1, 6) AND T1.STR_WARE_NO = #{strWareNo} AND T1.STR_CONF_DT IS NULL AND T1.OSTR_DT BETWEEN #{stOstrDt} AND #{edOstrDt} AND T1.OSTR_TP_CD IN ('221','222','223','261','262') AND T1.OSTR_SN = (SELECT MIN(W1.OSTR_SN) FROM TB_SVST_ITM_OSTR_IZ W1 WHERE W1.ITM_STR_NO = T1.ITM_STR_NO AND W1.STR_CONF_DT IS NULL)
AND T1.OSTR_TP_CD = #{ostrTpCd}
GROUP BY T1.ITM_OSTR_NO , T1.OSTR_TP_CD , T1.OSTR_WARE_NO , T1.OSTR_DT , T1.STR_WARE_NO , T1.ACB_DT , T1.EVID_DV_CD , T1.WARE_MNGT_PRTNR_NO , T1.ITM_STR_NO , T1.STR_HOP_DT , T2.WARE_NM , T4.WARE_NM , T3.PD_NM ORDER BY T1.OSTR_TP_CD, T1.OSTR_WARE_NO, T1.OSTR_DT

mapper.update:
UPDATE TB_SVST_ITM_STR_IZ T1
   SET T1.STR_CONF_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
     , T1.STR_CONF_HH = TO_CHAR(SYSDATE,'HH24MISS')
     , T1.STR_CONF_PRTNR_NO = #{session.employeeIDNumber}
     , T1.STR_CONF_PRTNR_OG_TP_CD = ( SELECT P1.OG_TP_CD
                                        FROM TB_OGBS_MM_PRTNR_IZ P1 /* 월파트너내역 */
                                       WHERE P1.BASE_YM = SUBSTR(#{ostrDt}, 1, 6)
                                         AND P1.PRTNR_NO = #{prtnrNo}
                                    ) /* 조직유형코드 가져오는 쿼리 */

 WHERE T1.ITM_STR_NO = #{itmStrNo}
   AND T1.STR_SN = #{strSn}

UPDATE TB_SVST_ITM_OSTR_IZ T1
   SET STR_CONF_DT = TO_CHAR(SYSDATE,'YYYYMMDD')
     , STR_CONF_HH = TO_CHAR(SYSDATE,'HH24MISS')
     , STR_CONF_PRTNR_NO = #{session.employeeIDNumber}
     , STR_CONF_PRTNR_OG_TP_CD = ( SELECT P1.OG_TP_CD
                                     FROM TB_OGBS_MM_PRTNR_IZ P1 /* 월파트너내역 */
                                    WHERE P1.BASE_YM = SUBSTR(#{ostrDt}, 1, 6)
                                      AND P1.PRTNR_NO = #{prtnrNo}
                                 ) /* 조직유형코드 가져오는 쿼리 */

 WHERE T1.ITM_STR_NO = #{itmStrNo}
   AND T1.STR_SN = #{strSn}

mapper.select:
SELECT count(1) AS CNT
  FROM TB_SVST_MCBY_WARE_CL_IZ T1
 WHERE T1.APY_YM = #{apyYm}
   AND T1.WARE_NO = #{wareNo}
   AND T1.WARE_CL_YN = 'Y'
   AND T1.DTA_DL_YN = 'N'
