MERGE INTO TB_SVST_BIZ_CL_BAS TB_TGT /* 업무마감기본 */ USING ( SELECT #{mngtYm} AS MNGT_YM /* 관리년월 */ , #{bizStrtdt} AS BIZ_STRTDT /* 업무시작일자 */ , #{bizStrtHh} || '00' AS BIZ_STRT_HH /* 업무시작시간 */ , #{bizEnddt} AS BIZ_ENDDT /* 업무종료일자 */ , #{bizEndHh} || '00' AS BIZ_END_HH /* 업무종료시간 */ FROM DUAL ) TB_SRC ON ( TB_TGT.MNGT_YM = TB_SRC.MNGT_YM AND TB_TGT.CL_MNGT_DV_CD = '5' /* 신입BS소모품신청 */ ) WHEN MATCHED THEN UPDATE SET TB_TGT.BIZ_STRTDT = TB_SRC.BIZ_STRTDT , TB_TGT.BIZ_STRT_HH = TB_SRC.BIZ_STRT_HH , TB_TGT.BIZ_ENDDT = TB_SRC.BIZ_ENDDT , TB_TGT.BIZ_END_HH = TB_SRC.BIZ_END_HH
