) VALUES ( TB_SRC.MNGT_YM , '5' , TB_SRC.BIZ_STRTDT , TB_SRC.BIZ_STRT_HH , TB_SRC.BIZ_ENDDT , TB_SRC.BIZ_END_HH , 'N'
)

mapper.select:
SELECT T1.BFSVC_CSMB_DDLV_STAT_CD /* BS소모품배부상태코드 */ , T1.REQ_YN /* 신청여부 */ , T1.BLD_CD /* 빌딩코드 */ , T1.BLD_NM /* 빌딩명 */ , T1.PRTNR_NM_NO /* 파트너명 */ , T1.PRTNR_NO /* 파트너번호 */ , ${pivotColumns} /* PIVOT 컬럼 */ FROM ( SELECT D3.BLD_CD , D3.BLD_NM , D1.PRTNR_KNM || '(' || D1.PRTNR_NO || ')' AS PRTNR_NM_NO , D1.PRTNR_NO , D4.BFSVC_CSMB_DDLV_STAT_CD , CASE WHEN D4.BFSVC_CSMB_DDLV_STAT_CD IS NULL THEN ( SELECT MLNG_CNTN FROM T_CMZ_MLNG_D WHERE MLNG_ID = 'MSG_TXT_NAPC' /* 미신청 */ AND TENANT_ID = 'TNT_BASE' AND LNG_ID = #{session.langId} ) ELSE F_CMZ_CD_NM('TNT_WELLS', 'BFSVC_CSMB_DDLV_STAT_CD', D4.BFSVC_CSMB_DDLV_STAT_CD, #{session.langId}) END AS REQ_YN FROM TB_OGBS_MM_PRTNR_IZ D1 /* 월파트너내역(신입) */ INNER JOIN TB_OGBS_MM_OG_IZ D2 /* 월조직내역 */ ON D2.BASE_YM = D1.BASE_YM AND D2.OG_TP_CD = D1.OG_TP_CD AND D2.OG_ID = D1.OG_ID INNER JOIN TB_OGBS_BLD_BAS D3 /* 빌딩기본 */ ON D3.OG_TP_CD = D2.OG_TP_CD AND D3.BLD_CD = D2.BLD_CD LEFT OUTER JOIN ( SELECT STR_WARE_NO , MAX(BFSVC_CSMB_DDLV_STAT_CD) AS BFSVC_CSMB_DDLV_STAT_CD FROM TB_SVST_BFSVC_CSMB_DDLV_IZ /* BS소모품배부내역 */ WHERE DTA_DL_YN = 'N' AND BFSVC_CSMB_DDLV_OJ_CD = '1' AND MNGT_YM = #{mngtYm} GROUP BY STR_WARE_NO ) D4 ON D4.STR_WARE_NO = D1.PRTNR_NO WHERE D1.DTA_DL_YN = 'N' AND D1.OG_TP_CD = 'W02' AND D2.DTA_DL_YN = 'N' AND D2.CLO_YN = 'N' AND D2.OG_TP_CD = 'W02' AND D3.DTA_DL_YN = 'N' AND D3.OG_TP_CD = 'W02' AND D1.BASE_YM = #{mngtYm} AND D2.BASE_YM = #{mngtYm} AND D1.CNTR_DT BETWEEN TO_CHAR(ADD_MONTHS(TO_DATE(#{mngtYm}, 'YYYYMM'), -1), 'YYYYMM') || '05' AND #{mngtYm} || '01' /* 전월 5일부터 현재월 1일까지 */
AND D3.BLD_CD IN
#{item}
) T1 LEFT OUTER JOIN ( SELECT * FROM ( SELECT NVL(S2.STR_WARE_NO, S1.STR_WARE_NO) AS STR_WARE_NO , TO_CHAR(TO_NUMBER(NVL(S2.SAP_MAT_CD, S1.SAP_MAT_CD))) AS SAP_MAT_CD , NVL(S2.BFSVC_CSMB_DDLV_QTY, S1.BFSVC_CSMB_DDLV_UNIT_QTY) AS BFSVC_CSMB_DDLV_UNIT_QTY FROM ( SELECT D2.PRTNR_NO AS STR_WARE_NO , D1.CSMB_PD_CD , D3.SAP_MAT_CD , CASE WHEN D1.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '2' THEN NVL(D1.BFSVC_CSMB_DDLV_UNIT_QTY, 0) WHEN D1.BFSVC_CSMB_DDLV_CMPT_BASE_CD = '1' AND D1.BFSVC_CSMB_DDLV_UNIT_ACC_N <> 0 THEN NVL( CEIL( SUM(D4.CNT) / D1.BFSVC_CSMB_DDLV_UNIT_ACC_N * D1.BFSVC_CSMB_DDLV_UNIT_QTY), 0) ELSE 0 END AS BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL D1 /* BS소모품기준상세 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ D2 /* 월파트너내역 */ ON D2.BASE_YM = D1.MNGT_YM INNER JOIN TB_PDBS_PD_BAS D3 /* 상품기본 */ ON D3.PD_CD = D1.CSMB_PD_CD LEFT OUTER JOIN ( SELECT /*+ ORDERED USE_HASH(S1 S2) */ O1.PRTNR_NO , S3.COPN_DV_CD , S4.PD_PRP_VAL20 , COUNT(1) AS CNT FROM TB_SVPD_CST_SV_EXCN_IZ S1 INNER JOIN TB_SVPD_MCBY_CST_SV_OJ_IZ S2 ON S2.CNTR_NO = S1.CNTR_NO AND S2.CNTR_SN = S1.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS S3 ON S3.CNTR_NO = S1.CNTR_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL S4 ON S4.PD_CD = S2.PDCT_PD_CD INNER JOIN TB_OGBS_MM_PRTNR_IZ O1 ON O1.BASE_YM = S2.MNGT_YM AND O1.OG_TP_CD = S1.MNGT_PRTNR_OG_TP_CD AND O1.PRTNR_NO = S1.MNGT_PRTNR_NO WHERE S1.DTA_DL_YN = 'N' AND S1.MNGT_PRTNR_OG_TP_CD = 'W02' AND S1.CNTR_DTL_STAT_CD NOT IN ('401', '402') AND S2.DTA_DL_YN = 'N' AND S3.DTA_DL_YN = 'N' AND S4.DTA_DL_YN = 'N' AND S4.PD_EXTS_PRP_GRP_CD = 'PART' AND O1.DTA_DL_YN = 'N' AND O1.OG_TP_CD = 'W02' AND S2.MNGT_YM = #{mngtYm} AND O1.BASE_YM = #{mngtYm} GROUP BY O1.PRTNR_NO, S3.COPN_DV_CD, S4.PD_PRP_VAL20 ) D4 ON D4.PRTNR_NO = D2.PRTNR_NO AND D4.COPN_DV_CD = NVL(D1.BFSVC_CSMB_DDLV_OJ_ACC_TP_CD, D4.COPN_DV_CD) AND D4.PD_PRP_VAL20 = NVL(D1.BFSVC_CSMB_DDLV_OJ_PD_GRP_CD, D4.PD_PRP_VAL20) WHERE D1.DTA_DL_YN = 'N' AND D1.BFSVC_CSMB_DDLV_OJ_CD = '1' AND D1.BFSVC_CSMB_DDLV_TP_CD = '1' AND D1.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND D2.DTA_DL_YN = 'N' AND D2.OG_TP_CD = 'W02' AND D3.DTA_DL_YN = 'N' AND D3.PD_TP_CD = 'M' AND D1.MNGT_YM = #{mngtYm} AND D2.BASE_YM = #{mngtYm} GROUP BY D2.PRTNR_NO, D1.CSMB_PD_CD, D3.SAP_MAT_CD, D1.BFSVC_CSMB_DDLV_CMPT_BASE_CD , NVL(D1.BFSVC_CSMB_DDLV_UNIT_QTY, 0), D1.BFSVC_CSMB_DDLV_UNIT_ACC_N, D1.BFSVC_CSMB_DDLV_UNIT_QTY UNION ALL SELECT D2.PRTNR_NO AS STR_WARE_NO , D1.CSMB_PD_CD , D3.SAP_MAT_CD , 0 AS BFSVC_CSMB_DDLV_UNIT_QTY FROM TB_SVST_BFSVC_CSMB_BASE_DTL D1 /* BS소모품기준상세 */ INNER JOIN TB_OGBS_MM_PRTNR_IZ D2 /* 월파트너내역 */ ON D2.BASE_YM = D1.MNGT_YM INNER JOIN TB_PDBS_PD_BAS D3 /* 상품기본 */ ON D3.PD_CD = D1.CSMB_PD_CD WHERE D1.DTA_DL_YN = 'N' AND D1.BFSVC_CSMB_DDLV_OJ_CD = '1' AND D1.BFSVC_CSMB_DDLV_TP_CD = '2' AND D1.BFSVC_CSMB_DDLV_ORT_YN = 'Y' AND D2.DTA_DL_YN = 'N' AND D2.OG_TP_CD = 'W02' AND D3.DTA_DL_YN = 'N' AND D3.PD_TP_CD = 'M' AND D1.MNGT_YM = #{mngtYm} AND D2.BASE_YM = #{mngtYm} ) S1 LEFT OUTER JOIN ( SELECT STR_WARE_NO , CSMB_PD_CD , SAP_MAT_CD , NVL(BFSVC_CSMB_DDLV_QTY, 0) AS BFSVC_CSMB_DDLV_QTY FROM TB_SVST_BFSVC_CSMB_DDLV_IZ /* BS소모품배부내역 */ WHERE DTA_DL_YN = 'N' AND BFSVC_CSMB_DDLV_OJ_CD = '1' AND MNGT_YM = #{mngtYm} ) S2 ON S2.STR_WARE_NO = S1.STR_WARE_NO AND S2.CSMB_PD_CD = S1.CSMB_PD_CD ) DDVL PIVOT ( SUM(BFSVC_CSMB_DDLV_UNIT_QTY) FOR SAP_MAT_CD IN (${pivotInStr}) ) ) T2 ON T2.STR_WARE_NO = T1.PRTNR_NO ORDER BY T1.BLD_NM, T1.PRTNR_NM_NO

