mapper.select:
SELECT J1.OSTR_AK_NO , J1.OSTR_AK_TP_CD , J1.OSTR_OJ_WARE_NO , J1.OSTR_OJ_WARE_NM , J1.STR_HOP_DT , J1.OVIV_TP_CD , J1.RECT_OSTR_DT , J1.ITM_PD_CD , J1.OSTR_AK_RGST_DT , J1.OSTR_DTRN_YN , J2.PD_NM , ' ' AS RMK_CN FROM (SELECT T1.OSTR_AK_NO , T1.OSTR_AK_TP_CD , T1.OSTR_OJ_WARE_NO , T2.WARE_NM AS OSTR_OJ_WARE_NM , T1.STR_HOP_DT , T1.OVIV_TP_CD , MAX(T1.RECT_OSTR_DT) AS RECT_OSTR_DT , MIN(T1.ITM_PD_CD) AS ITM_PD_CD , T1.OSTR_AK_RGST_DT , (CASE WHEN T1.OSTR_AK_RGST_DT > '20190700' THEN CASE WHEN COUNT(T1.OSTR_CNFM_QTY) - COUNT(T1.OSTR_AGG_QTY) = 0 THEN '출고완료' WHEN COUNT(T1.OSTR_CNFM_QTY) = COUNT(T1.OSTR_AGG_QTY) THEN '출고대기' ELSE '일부출고' END ELSE CASE WHEN COUNT(*) = COUNT(T1.RECT_OSTR_DT) THEN '출고완료' ELSE '' END END) AS OSTR_DTRN_YN FROM TB_SVST_ITM_OSTR_AK_IZ T1 INNER JOIN TB_SVST_MCBY_WARE_IZ T2 ON T2.APY_YM = SUBSTR(#{strHopDtStr}, 1,6) AND T1.OSTR_OJ_WARE_NO = T2.WARE_NO WHERE T1.STR_HOP_DT BETWEEN #{strHopDtStr} AND #{strHopDtEnd} AND T1.OSTR_OJ_WARE_NO = #{ostrOjWareNo}
AND T1.OSTR_AK_TP_CD = #{ostrAkTpCd}
AND T1.OSTR_AK_WARE_DV_CD = #{wareDvCd}
AND T1.ITM_PD_CD = #{itmKndCd}
AND T1.OSTR_OJ_WARE_NO = #{ostrOjWareNo}
AND T2.WARE_LOCARA_CD = #{wareLocaraCd}
GROUP BY T1.OSTR_AK_TP_CD , T1.OSTR_OJ_WARE_NO , T1.OSTR_AK_RGST_DT , T1.OSTR_AK_NO , T1.STR_HOP_DT , T2.WARE_NM , T1.OVIV_TP_CD HAVING (CASE WHEN T1.OSTR_AK_RGST_DT > '20190700' THEN CASE WHEN COUNT(T1.OSTR_CNFM_QTY) - COUNT(T1.OSTR_AGG_QTY) = 0 THEN '1' ELSE '0' END ELSE CASE WHEN COUNT(*) = COUNT(T1.RECT_OSTR_DT) THEN '1' ELSE '0' END END) = CASE WHEN #{ostrCnfm} = 'Y' THEN '1' ELSE '0' END )J1 INNER JOIN TB_PDBS_PD_BAS J2 ON J1.ITM_PD_CD = J2.PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL J3 ON J2.PD_CD = J3.PD_CD AND J3.PD_EXTS_PRP_GRP_CD = 'PART'
AND T4.PD_PRP_VAL19 = #{itmKndCd}
