mapper.select:
SELECT J1.OSTR_AK_NO , J1.OSTR_AK_TP_CD , J1.OSTR_OJ_WARE_NO , J1.STR_OJ_WARE_NO , (SELECT S1.WARE_NM FROM TB_SVST_MCBY_WARE_IZ S1 WHERE S1.APY_YM = SUBSTR(#{strHopDtStr}, 1,6) AND S1.WARE_NO = J1.OSTR_OJ_WARE_NO ) AS OSTR_OJ_WARE_NM , (SELECT S1.WARE_NM FROM TB_SVST_MCBY_WARE_IZ S1 WHERE S1.APY_YM = SUBSTR(#{strHopDtStr}, 1,6) AND S1.WARE_NO = J1.STR_OJ_WARE_NO ) AS STR_OJ_WARE_NM , J1.STR_HOP_DT , J1.OVIV_TP_CD , J1.RECT_OSTR_DT , J1.ITM_PD_CD , J1.OSTR_AK_RGST_DT , J1.OSTR_DTRN_YN , J2.SVPD_NM_ABBR1 || ' 외' AS PD_NM , (SELECT MIN(S1.OSTR_AK_SN) FROM TB_SVST_ITM_OSTR_AK_IZ S1 WHERE S1.OSTR_AK_NO = J1.OSTR_AK_NO AND S1.ITM_PD_CD = J1.ITM_PD_CD AND S1.DTA_DL_YN = 'N' ) AS OSTR_AK_SN , ' ' AS RMK_CN FROM (SELECT T1.OSTR_AK_NO , T1.OSTR_AK_TP_CD , T1.OSTR_OJ_WARE_NO , T1.STR_OJ_WARE_NO , T1.STR_HOP_DT , T1.OVIV_TP_CD , MAX(T1.RECT_OSTR_DT) AS RECT_OSTR_DT , MIN(T1.ITM_PD_CD) AS ITM_PD_CD , T1.OSTR_AK_RGST_DT , (CASE WHEN T1.OSTR_AK_RGST_DT > '20190700' THEN DECODE(COUNT(CASE WHEN T1.OSTR_CNFM_QTY - T1.OSTR_AGG_QTY = 0 THEN 1 END), COUNT(1),'출고완료', '0', '출고대기', '일부출고') ELSE DECODE(COUNT(1),COUNT(T1.RECT_OSTR_DT),'출고완료', '') END) AS OSTR_DTRN_YN FROM TB_SVST_ITM_OSTR_AK_IZ T1 INNER JOIN TB_SVST_MCBY_WARE_IZ T2 ON T1.OSTR_OJ_WARE_NO = T2.WARE_NO AND T2.APY_YM = SUBSTR(#{strHopDtStr}, 1,6) WHERE T1.STR_HOP_DT BETWEEN #{strHopDtStr} AND #{strHopDtEnd}
AND T1.RECT_OSTR_DT IS NOT NULL
AND T1.OSTR_AK_TP_CD = #{ostrAkTpCd}
AND T1.OSTR_AK_WARE_DV_CD = #{wareDvCd}
AND T1.OSTR_OJ_WARE_NO = #{ostrOjWareNo}
AND T2.WARE_LOCARA_CD = #{wareLocaraCd}
AND T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' GROUP BY T1.OSTR_AK_TP_CD , T1.OSTR_OJ_WARE_NO , T1.STR_OJ_WARE_NO , T1.OSTR_AK_RGST_DT , T1.OSTR_AK_NO , T1.STR_HOP_DT , T1.OVIV_TP_CD
HAVING (CASE WHEN T1.OSTR_AK_RGST_DT > '20190700' THEN CASE WHEN COUNT(T1.OSTR_CNFM_QTY) - COUNT(T1.OSTR_AGG_QTY) = 0 THEN '1' ELSE '0' END ELSE CASE WHEN COUNT(*) = COUNT(T1.RECT_OSTR_DT) THEN '1' ELSE '0' END END) = CASE WHEN #{ostrCnfm} = 'Y' THEN '1' ELSE '0' END
)J1 INNER JOIN () J2 ON J1.ITM_PD_CD = J2.SVPD_PD_CD
AND J2.SVPD_ITEM_KND = #{itmKndCd}
AND J1.OSTR_DTRN_YN != '출고완료'
ORDER BY J1.OSTR_AK_NO ASC

