UPDATE TB_SVST_ITM_OSTR_AK_IZ
   SET RECT_OSTR_DT = #{todayStr}
     , OSTR_AGG_QTY = NVL(OSTR_AGG_QTY,0) + #{outQty}
     , OSTR_CNFM_CD = 0
     , RMK_CN = #{rmkCn}

