mapper.select:
SELECT T1.OSTR_AK_NO , T1.OSTR_AK_TP_CD , T1.OSTR_OJ_WARE_NO , T1.STR_OJ_WARE_NO , ( SELECT WARE_NM FROM TB_SVST_MCBY_WARE_IZ WHERE WARE_NO = T1.STR_OJ_WARE_NO AND DTA_DL_YN = 'N' AND APY_YM = SUBSTR(#{strHopDtStr}, 1, 6) ) AS STR_OJ_WARE_NM , T1.STR_HOP_DT , T1.OVIV_TP_CD , T1.RECT_OSTR_DT , T1.ITM_PD_CD , T1.OSTR_AK_RGST_DT , T1.OSTR_DTRN_YN , T2.PD_NM || ' 외' AS PD_NM , T1.OSTR_AK_SN , ' ' AS RMK_CN FROM ( SELECT D1.OSTR_AK_NO /* 출고요청번호 */ , D1.OSTR_AK_TP_CD /* 출고요청유형 */ , D1.OSTR_OJ_WARE_NO /* 출고대상창고번호 */ , D1.STR_OJ_WARE_NO /* 입고대상창고번호 */ , D1.STR_HOP_DT /* 입고희망일자 */ , D1.OVIV_TP_CD /* 배차형태 */ , D1.OSTR_AK_RGST_DT , MIN(D1.OSTR_AK_SN) AS OSTR_AK_SN /* 출고요청일련번호 */ , MAX(D1.RECT_OSTR_DT) AS RECT_OSTR_DT /* 최근출고일자 */ , MIN(D1.ITM_PD_CD) AS ITM_PD_CD /* 품목상품코드 */ , CASE WHEN D1.OSTR_AK_RGST_DT > '20190700' THEN CASE COUNT(CASE WHEN D1.OSTR_CNFM_QTY = D1.OSTR_AGG_QTY THEN 1 END) WHEN COUNT(1) THEN '출고완료' WHEN 0 THEN '출고대기' ELSE '일부출고' END ELSE CASE WHEN COUNT(*) = COUNT(D1.RECT_OSTR_DT) THEN '출고완료' END END AS OSTR_DTRN_YN /* 출고확정여부 */ FROM TB_SVST_ITM_OSTR_AK_IZ D1 /* 품목출고요청내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ D2 /* 월별창고내역 */ ON D2.WARE_NO = D1.OSTR_OJ_WARE_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D3 /* 상품각사속성상세 */ ON D3.PD_CD = D1.ITM_PD_CD WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.WARE_USE_YN = 'Y' AND D3.DTA_DL_YN = 'N' AND D3.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.OSTR_OJ_WARE_NO = #{ostrOjWareNo} AND D1.STR_HOP_DT BETWEEN #{strHopDtStr} AND #{strHopDtEnd} AND D2.APY_YM = SUBSTR(#{strHopDtStr}, 1, 6) AND D2.WARE_DV_CD = #{wareDvCd}
AND D1.OSTR_AK_TP_CD = #{ostrAkTpCd}
AND D3.PD_PRP_VAL19 = #{itmKndCd}
GROUP BY D1.OSTR_AK_NO, D1.OSTR_AK_TP_CD, D1.OSTR_OJ_WARE_NO, D1.STR_OJ_WARE_NO, D1.STR_HOP_DT, D1.OVIV_TP_CD, D1.OSTR_AK_RGST_DT
HAVING CASE WHEN D1.OSTR_AK_RGST_DT > '20190700' THEN CASE COUNT(CASE WHEN D1.OSTR_CNFM_QTY = D1.OSTR_AGG_QTY THEN 1 END) WHEN COUNT(1) THEN '1' ELSE '0' END ELSE CASE WHEN COUNT(*) = COUNT(D1.RECT_OSTR_DT) THEN '1' ELSE '0' END END = '1' /* 출고확정 */
HAVING CASE WHEN D1.OSTR_AK_RGST_DT > '20190700' THEN CASE COUNT(CASE WHEN D1.OSTR_CNFM_QTY = D1.OSTR_AGG_QTY THEN 1 END) WHEN COUNT(1) THEN '1' ELSE '0' END ELSE CASE WHEN COUNT(*) = COUNT(D1.RECT_OSTR_DT) THEN '1' ELSE '0' END END = '0' /* 출고미확정 */
) T1 INNER JOIN TB_PDBS_PD_BAS T2 ON T2.PD_CD = T1.ITM_PD_CD WHERE T2.DTA_DL_YN = 'N' AND T2.PD_TP_CD = 'M' ORDER BY T1.OSTR_AK_TP_CD, T1.OSTR_OJ_WARE_NO, T1.STR_HOP_DT, T1.OSTR_AK_NO

mapper.update:
UPDATE TB_SVST_MCBY_WARE_IZ
   SET STD_WARE_USE_YN = #{stckStdGb}

 WHERE APY_YM  = #{apyYm}
   AND WARE_NO = #{wareNo}

mapper.select:
SELECT D1.WARE_MNGT_PRTNR_NO , D1.WARE_NM , D2.ITM_PD_CD , D1.WARE_NO , NVL(D2.PITM_STOC_A_GD_QTY, 0) - NVL(MMT_STOC_A_GD_QTY, 0) AS QTY FROM TB_SVST_MCBY_WARE_IZ D1 /* 월별창고내역 */ INNER JOIN TB_SVST_CST_SV_ITM_STOC_IZ D2 /* 고객서비스품목재고내역 */ ON D2.WARE_NO = D1.WARE_NO WHERE D1.DTA_DL_YN = 'N' AND D1.WARE_USE_YN = 'Y' AND D1.APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND D2.DTA_DL_YN = 'N' AND D1.WARE_DV_CD = #{wareDvCd} AND D2.ITM_PD_CD = #{itmPdCd} AND D1.HGR_WARE_NO = ( SELECT HGR_WARE_NO FROM TB_SVST_MCBY_WARE_IZ WHERE DTA_DL_YN = 'N' AND WARE_USE_YN = 'Y' AND APY_YM = TO_CHAR(SYSDATE, 'YYYYMM') AND WARE_NO = #{strOjWareNo}
AND WARE_DTL_DV_CD <> '20'
AND WARE_DTL_DV_CD <> '30' ) ORDER BY D1.WARE_NM

mapper.update:
UPDATE TB_SVST_ITM_OSTR_IZ   /* 품목출고내역 */
   SET DTA_DL_YN = 'Y'

 WHERE DTA_DL_YN   = 'N'
   AND ITM_OSTR_NO = #{itmOstrNo}
   AND OSTR_SN     = #{ostrSn}

UPDATE TB_SVST_ITM_STR_IZ   /* 품목입고내역 */
   SET DTA_DL_YN = 'Y'

 WHERE DTA_DL_YN  = 'N'
   AND ITM_STR_NO = #{itmStrNo}
   AND STR_SN     = #{strSn}

UPDATE TB_SVST_ITM_OSTR_AK_IZ   /* 품목출고요청내역 */
   SET RECT_OSTR_DT = NULL
     , OSTR_AGG_QTY = NVL(OSTR_AGG_QTY, 0) - #{outQty}

 WHERE OSTR_AK_NO = #{ostrAkNo}
   AND OSTR_AK_SN = #{ostrAkSn}

mapper.select:
SELECT NVL(MAX(OSTR_SN), 0) + 1 AS OSTR_SN
  FROM TB_SVST_ITM_OSTR_IZ
 WHERE ITM_OSTR_NO = #{itmOstrNo}

SELECT NVL(MAX(STR_SN), 0) + 1 AS STR_SN
  FROM TB_SVST_ITM_STR_IZ
 WHERE ITM_STR_NO = #{itmStrNo}

