mapper.select:
WITH PD_INF AS (/*상품정보*/ SELECT T1.PD_CD , T1.PD_NM , T1.PD_ABBR_NM , T1.SAP_MAT_CD , T1.RVPY_STRTDT , T1.RVPY_ENDDT , T2.PD_EXTS_PRP_GRP_CD , T2.PD_PRP_VAL01 , T2.PD_PRP_VAL02 , T2.PD_PRP_VAL19 , T2.PD_PRP_VAL21 , T2.PD_PRP_VAL32 FROM TB_PDBS_PD_BAS T1 /*상품기본*/ LEFT OUTER JOIN TB_PDBS_PD_ECOM_PRP_DTL T2 ON T1.PD_CD = T2.PD_CD AND T2.DTA_DL_YN ='N' AND T2.PD_EXTS_PRP_GRP_CD IN ('PART') WHERE T1.DTA_DL_YN ='N' ) ,ITM_OSTR_INF AS (/* 대상조회*/ SELECT T1.OSTR_TP_CD AS OSTR_TP_CD /* ST161_OUT_STCK_CD */ , T1.OSTR_WARE_NO AS OSTR_WARE_NO /* ST161_OUT_STCK_CD */ , T1.ITM_PD_CD AS ITM_PD_CD /* ST161_ITEM_CD */ , T1.ITM_OSTR_NO AS ITM_OSTR_NO , T1.OSTR_QTY AS QTY /* ST161_QTY */ , (CASE WHEN T3.WARE_ICHR_NO = '000' THEN T1.OSTR_WARE_NO ELSE SUBSTR(T1.OSTR_WARE_NO,1,1) || '99999' END ) AS WARE_NO /* STCK_CD */ FROM TB_SVST_ITM_OSTR_IZ T1 /*LC_STOCK_ST161TB */ LEFT OUTER JOIN PD_INF T2 /* LC_STOCK_ST101TB */ ON T1.ITM_PD_CD = T2.PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T3 /* LC_STOCK_ST102TB */ ON T1.OSTR_WARE_NO = T3.WARE_NO AND SUBSTR(T1.OSTR_WARE_NO, 1, 1) = T3.WARE_DV_CD AND SUBSTR(T1.OSTR_DT,1,6) = T3.APY_YM AND T3.DTA_DL_YN ='N' AND T3.WARE_USE_YN = 'Y' WHERE T1.DTA_DL_YN = 'N' AND T1.OSTR_QTY > 0
/* 검색조건 */ AND T1.OSTR_DT BETWEEN #{startDt} AND #{endDt}
AND T1.OSTR_TP_CD = #{ostrTpCd} /* 출고유형 */
AND T1.ITM_PD_CD = #{itmPdCd} /* 품목코드 */
AND T1.ITM_GD_CD = #{itmGdCd} /* 등급코드*/
)
, ALL_INF AS ( /* 출고유형 : 전체 */ SELECT T1.OSTR_TP_CD , T1.OSTR_WARE_NO , T1.ITM_PD_CD , T1.ITM_OSTR_NO , T1.QTY , T1.WARE_NO FROM ITM_OSTR_INF T1 UNION ALL SELECT '213' AS OSTR_TP_CD /* OUT_TYP */ , T1.WK_WARE_NO AS OSTR_WARE_NO /* ST163_WRK_STCK_CD */ , T1.ITM_PD_CD AS ITM_PD_CD /* ST163_ITEM_CD */ , T1.ITM_OSTR_NO AS ITM_OSTR_NO , T1.USE_QTY AS QTY /* ST163_QTY */ ,(CASE WHEN T3.WARE_ICHR_NO = '000' THEN T1.WK_WARE_NO ELSE SUBSTR(T1.WK_WARE_NO,1,1)||'99999' END) AS WARE_NO /* STCK_CD */ FROM TB_SVST_SV_WK_OSTR_IZ T1 /*LC_STOCK_ST163TB */ LEFT OUTER JOIN PD_INF T2 /* LC_STOCK_ST101TB */ ON T1.ITM_PD_CD = T2.PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T3 /* LC_STOCK_ST102TB */ ON T1.WK_WARE_NO = T3.WARE_NO AND SUBSTR(T1.WK_WARE_NO, 1, 1) = T3.WARE_DV_CD AND SUBSTR(T1.FNL_VST_FSH_DT,1,6) = T3.APY_YM AND T3.DTA_DL_YN ='N' AND T3.WARE_USE_YN = 'Y' WHERE T1.DTA_DL_YN = 'N' AND T1.USE_QTY > 0 AND T1.SV_BIZ_HCLSF_CD IN ('1','2','3')
/* 검색조건 */ AND T1.FNL_VST_FSH_DT BETWEEN #{startDt} AND #{endDt}
AND T1.ITM_PD_CD = #{itmPdCd} /* 품목코드 */
AND T1.FNL_ITM_GD_CD = #{itmGdCd} /* 등급코드 */
)
, WORK_OSTR_INF AS ( /* 출고유형 :작업출고 */ SELECT T1.OSTR_TP_CD , T1.OSTR_WARE_NO , T1.ITM_PD_CD , T1.ITM_OSTR_NO , T1.QTY , T1.WARE_NO FROM ITM_OSTR_INF T1 UNION ALL SELECT '213' AS OSTR_TP_CD /* OUT_TYP */ , T1.WK_WARE_NO AS OSTR_WARE_NO /* ST163_WRK_STCK_CD */ , T1.ITM_PD_CD AS ITM_PD_CD /* ST163_ITEM_CD */ , T1.ITM_OSTR_NO AS ITM_OSTR_NO , T1.USE_QTY AS QTY /* ST163_QTY */ ,(CASE WHEN T3.WARE_ICHR_NO = '000' THEN T1.WK_WARE_NO ELSE SUBSTR(T1.WK_WARE_NO,1,1)||'99999' END) AS WARE_NO /* STCK_CD */ FROM TB_SVST_SV_WK_OSTR_IZ T1 /*LC_STOCK_ST163TB */ INNER JOIN PD_INF T2 /* LC_STOCK_ST101TB */ ON T1.ITM_PD_CD LIKE T2.PD_CD||'%' LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ T3 /* LC_STOCK_ST102TB */ ON T1.WK_WARE_NO = T3.WARE_NO AND SUBSTR(T1.WK_WARE_NO, 1, 1) = T3.WARE_DV_CD AND SUBSTR(T1.FNL_VST_FSH_DT,1,6) = T3.APY_YM AND T3.DTA_DL_YN ='N' AND T3.WARE_USE_YN = 'Y' WHERE T1.DTA_DL_YN = 'N' AND T1.USE_QTY > 0 AND T1.SV_BIZ_HCLSF_CD IN ('1','2','3') AND T3.WARE_DV_CD ='2'
/* 검색조건 */ AND T1.FNL_VST_FSH_DT BETWEEN #{startDt} AND #{endDt}
AND T1.ITM_PD_CD = #{itmPdCd} /* 품목코드 */
AND T1.FNL_ITM_GD_CD = #{itmGdCd} /* 등급코드 */
)
/* 결과 조회 */ SELECT T1.ITM_PD_CD AS ITM_PD_CD , T2.SAP_MAT_CD AS SAP_MAT_CD , T2.PD_NM AS ITM_NM , T2.PD_ABBR_NM AS PD_ABBR_NM , T2.PD_PRP_VAL02 AS SVPD_MGT_TYP , F_CMZ_CD_NM('TNT_WELLS', 'MAT_MNGT_DV_CD', T2.PD_PRP_VAL02) AS SVPD_MGT_TYP_NM , ${wareNoFields} FROM ( SELECT ITM_PD_CD , WARE_NO , QTY
FROM ALL_INF T1 /* 출고유형 : 전체 */
FROM ITM_OSTR_INF T1 /* 출고유형 : 전체,작업출고가 아닌경우 */
FROM WORK_OSTR_INF T1 /* 출고유형 : 작업출고 */
/* 상품코드로 그룹핑후 재고수량 합 */ GROUP BY ITM_PD_CD , WARE_NO , QTY ) PIVOT ( SUM(QTY) FOR WARE_NO IN (${wareNoInStr}) ) T1 LEFT OUTER JOIN PD_INF T2 ON T1.ITM_PD_CD = T2.PD_CD WHERE 1=1
/* 검색조건 */
/* 품목코드 FROM~ 품목코드 TO */ AND T1.ITM_PD_CD BETWEEN #{itmCdFrom} AND #{itmCdTo}
/* SAP코드 FROM~ SAP코드 TO */ AND T2.SAP_MAT_CD BETWEEN #{sapMatCdFrom} AND #{sapMatCdTo}
/* 품목종류 */ AND T2.PD_PRP_VAL19 = #{itmKndCd}
/* 수불 일자 및 화면노출여부를 기준으로 사용여부 처리 */ AND (CASE WHEN TO_CHAR(SYSDATE,'YYYYMMDD') BETWEEN T2.RVPY_STRTDT AND T2.RVPY_ENDDT AND T2.PD_PRP_VAL32 ='Y' THEN 'Y' ELSE 'N' END) = #{useYn}
ORDER BY T1.ITM_PD_CD ASC
