mapper.select:
SELECT I1.OSTR_AK_NO , I1.OSTR_AK_TP_CD , I1.STR_OJ_WARE_NO AS STR_OJ_WARE_NO , W1.WARE_NM AS STR_OJ_WARE_NM , W1.WARE_MNGT_PRTNR_NO AS STR_OJ_STCK_MGR , I1.OSTR_AK_RGST_DT , I1.STR_HOP_DT , I1.OSTR_OJ_WARE_NO AS OSTR_OJ_WARE_NO , W2.WARE_NM AS OSTR_OJ_WARE_NM , W2.WARE_MNGT_PRTNR_NO AS OSTR_OJ_STCK_MGR , I1.RECT_OSTR_DT , NVL(I1.OVIV_TP_CD, '0') AS OVIV_TP_CD , P1.PD_PRP_VAL19 AS OSTR_ITM_NO FROM TB_SVST_ITM_OSTR_AK_IZ I1 INNER JOIN TB_SVST_MCBY_WARE_IZ W1 ON W1.APY_YM = SUBSTR(I1.STR_HOP_DT,1,6) AND W1.WARE_NO = I1.STR_OJ_WARE_NO AND W1.WARE_USE_YN = 'Y' INNER JOIN T_CMZ_CD_D C1 ON C1.CD_VLD_VAL = I1.OSTR_AK_TP_CD INNER JOIN TB_SVST_MCBY_WARE_IZ W2 ON W2.APY_YM = SUBSTR(I1.STR_HOP_DT,1,6) AND W2.WARE_NO = I1.OSTR_OJ_WARE_NO AND W2.WARE_USE_YN = 'Y' INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL P1 ON I1.ITM_PD_CD = P1.PD_CD AND P1.PD_EXTS_PRP_GRP_CD = 'PART' WHERE 1=1 AND C1.CD_ID = 'OSTR_AK_TP_CD'
AND I1.OSTR_AK_NO = #{ostrAkNo}
AND ROWNUM = 1
