mapper.select:
SELECT I1.OSTR_AK_NO /* 출고요청번호 */ , I1.OSTR_AK_TP_CD /* 출고요청유형코드 */ , I1.STR_OJ_WARE_NO AS STR_OJ_WARE_NO /* 입고대상창고번호 */ , W1.WARE_NM AS STR_OJ_WARE_NM /* 입고대상창고명 */ , W1.WARE_MNGT_PRTNR_NO AS STR_OJ_STCK_MGR /* 입고대상창고파트너번호 */ , I1.OSTR_AK_RGST_DT /* 출고요청등록일자 */ , I1.STR_HOP_DT /* 입고희망일자 */ , I1.OSTR_OJ_WARE_NO AS OSTR_OJ_WARE_NO /* 출고대상창고번호 */ , W2.WARE_NM AS OSTR_OJ_WARE_NM /* 출고대상창고명 */ , W2.WARE_MNGT_PRTNR_NO AS OSTR_OJ_STCK_MGR /* 출고대상창고파트너번호 */ , I1.RECT_OSTR_DT /* 최근출고일자 */ , I1.OVIV_TP_CD /* 배차유형코드 */ , P1.PD_PRP_VAL19 AS OSTR_ITM_NO /* 출고품목번호 */ FROM TB_SVST_ITM_OSTR_AK_IZ I1 /* 품목출고요청내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ W1 /* 월별창고내역 */ ON W1.APY_YM = SUBSTR(I1.STR_HOP_DT,1,6) AND W1.WARE_NO = I1.STR_OJ_WARE_NO AND W1.WARE_USE_YN = 'Y' INNER JOIN T_CMZ_CD_D C1 /* 코드상세 */ ON C1.CD_VLD_VAL = I1.OSTR_AK_TP_CD INNER JOIN TB_SVST_MCBY_WARE_IZ W2 /* 월별창고내역 */ ON W2.APY_YM = SUBSTR(I1.STR_HOP_DT,1,6) AND W2.WARE_NO = I1.OSTR_OJ_WARE_NO AND W2.WARE_USE_YN = 'Y' INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL P1 /* 상품각사속성상세 */ ON I1.ITM_PD_CD = P1.PD_CD AND P1.PD_EXTS_PRP_GRP_CD = 'PART' WHERE 1=1 AND C1.CD_ID = 'OSTR_AK_TP_CD'
AND I1.OSTR_AK_NO = #{ostrAkNo}
AND ROWNUM = 1

mapper.delete:
UPDATE TB_SVST_ITM_OSTR_AK_IZ   /* 품목출고요청내역 */
   SET DTA_DL_YN = 'Y' /* 데이터삭제여부 */
 WHERE 1 = 1
   AND OSTR_AK_NO = #{ostrAkNo}
   AND OSTR_AK_SN = #{ostrAkSn}

mapper.update:
UPDATE TB_SVST_ITM_OSTR_AK_IZ   /* 품목출고요청내역 */
   SET OSTR_AK_QTY = #{ostrAkQty}
     , OSTR_CNFM_QTY = #{ostrCnfmQty}
     , RMK_CN = #{rmkCn}
     , BOX_UNIT_QTY = #{boxUnitQty}

 WHERE OSTR_AK_NO = #{ostrAkNo}
   AND OSTR_AK_SN = #{ostrAkSn}

mapper.select:
SELECT T1.OSTR_AK_NO /* 출고요청번호 */ , T1.OSTR_AK_SN /* 출고요청순번 */ , T1.OSTR_AK_TP_CD /* 출고요청유형코드 */ , T1.OSTR_AK_RGST_DT /* 출고요청등록일자 */ , T1.OSTR_AK_WARE_DV_CD /* 출고요청창고구분코드 */ , T1.OSTR_OJ_WARE_DV_CD /* 출고대상창고구분코드 */ , T1.STR_HOP_DT AS OSTR_HOP_DT /* 출고희망일자 */ , T1.WARE_MNGT_PRTNR_NO /* 창고관리파트너번호 */ , T1.WARE_MNGT_PRTNR_OG_TP_CD /* 창고관리파트너조직유형코드 */ , 0 AS MPAC_SN /* 합포장일련번호 */ , 'WE' AS IOST_AK_DV_CD /* 입출고요청구분코드 */ , '' AS SAP_IOST_TP_CD /* SAP입출고유형코드 */ , (CASE WHEN T1.OVIV_TP_CD = '00' THEN '9' WHEN T1.OVIV_TP_CD = '11' THEN '7' ELSE '6' END ) AS LGST_SPP_MTHD_CD /* 물류배송방식코드 */ , (CASE WHEN A1.WARE_DV_CD = '2' AND T2.PD_PRP_VAL19 = '4' THEN 'WE01' WHEN A1.WARE_DV_CD = '2' AND T2.PD_PRP_VAL19 = '5' THEN 'WE03' WHEN A1.WARE_DV_CD = '2' AND T2.PD_PRP_VAL19 <> '4' AND T2.PD_PRP_VAL19 <> '5' THEN 'WE02' ELSE '' END ) AS LGST_WK_MTHD_CD /* 물류작업방식코드 */ , '' AS MDLV_PRTNR_NO /* 배달파트너번호 */ , T1.ITM_PD_CD /* 품목상품코드 */ , T1.OSTR_AK_QTY /* 출고요청수량 */ , 'A' AS ITM_GD_CD /* 물류품목등급코드 */ , T1.OSTR_OJ_WARE_NO /* 출고대상창고번호 */ , A1.WARE_NO AS SV_CNR_CD /* 서비스센터코드 */ , A1.WARE_NM AS SV_CNR_NM /* 서비스센터명 */ , A1.WARE_MNGT_PRTNR_NM AS SV_CNR_ICHR_PRTNR_NM /*서비스센터담당파트너명*/ , A1.LOCARA_TNO /* 지역번호 */ , A1.EXNO_ENCR /* 전화국번호암호화 */ , A1.IDV_TNO /* 개별전화번호 */ , TRIM(A1.ADR_NM) AS SV_CNR_ADR /* 서비스센터 주소 */ , T1.OVIV_TP_CD /* 배차유형코드 */ , '' AS HSMTRL_CLSF_CD /* 학습지분류코드 */ , '' AS HSMTRL_CLSF_NM /* 학습지분류명 */ , T1.RMK_CN /* 비고 */ FROM TB_SVST_ITM_OSTR_AK_IZ T1 /* 품목출고요청내역 */ INNER JOIN ( SELECT T2.APY_YM /* 적용년월 */ , T2.WARE_DV_CD /* 창고구분코드 */ , T2.WARE_DTL_DV_CD /* 창고상세구분코드 */ , T2.WARE_NO /* 창고번호 */ , T2.WARE_NM /* 창고명 */ , T3.PRTNR_KNM AS WARE_MNGT_PRTNR_NM /* 창고관리파트너명*/ , T5.RNADR || ' ' || T5.RDADR AS ADR_NM /* 주소 */ , T4.LOCARA_TNO /* 지역번호 */ , T4.EXNO_ENCR /* 전화국번호암호화 */ , T4.IDV_TNO /* 개별전화번호 */ FROM TB_SVST_MCBY_WARE_IZ T2 /* 월별창고내역 */ LEFT OUTER JOIN TB_OGBS_PRTNR_BAS T3 /* 파트너기본 */ ON T3.OG_TP_CD = T2.OG_TP_CD AND T3.PRTNR_NO = T2.WARE_MNGT_PRTNR_NO AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_OG_BAS T4 /* 조직내역 */ ON T4.OG_ID = T2.OG_ID AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS T5 /* 주소기본 */ ON T5.ADR_ID = T4.ADR_ID AND T5.DTA_DL_YN = 'N' WHERE T2.APY_YM = TO_CHAR(SYSDATE , 'YYYYMM') ) A1 ON A1.WARE_NO = T1.STR_OJ_WARE_NO INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL T2 /* 상품각사속성상세 */ ON T2.PD_CD = T1.ITM_PD_CD WHERE T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N'
AND T1.OSTR_AK_NO = #{ostrAkNo}
AND T1.OSTR_AK_NO IN
#{item}
AND T2.PD_EXTS_PRP_GRP_CD = 'PART'

