RTNR_NM /* 창고관리파트너명 */ , T5.RNADR || ' ' || T5.RDADR AS ADR_NM /* 주소 */ , T5.NEW_ADR_ZIP /* 우편번호 */ , T5.RNADR /* 빌딩기본주소 */ , T5.RDADR /* 빌딩상세주소 */ , T5.LTN_ADR /* 지번 */ , T4.LOCARA_TNO /* 요청창고 전화번호 앞 */ , T4.EXNO_ENCR /* 요청창고 전화번호 중간 */ , T4.IDV_TNO /* 요청창고 전화번호 끝 */ , T3.CRAL_LOCARA_TNO /*요청창고 담당매니저 휴대전화번호 앞자리*/ , T3.MEXNO_ENCR /*요청창고 담당매니저 휴대전화번호 중간*/ , T3.CRAL_IDV_TNO /*요청창고 담당매니저 휴대전화번호 끝자리*/ FROM TB_SVST_MCBY_WARE_IZ T2 /* 월별창고내역 */ LEFT OUTER JOIN TB_OGBS_PRTNR_BAS T3 /* 파트너기본 */ ON T3.OG_TP_CD = T2.OG_TP_CD AND T3.PRTNR_NO = T2.WARE_MNGT_PRTNR_NO AND T3.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_OG_BAS T4 /* 조직내역 */ ON T4.OG_ID = T2.OG_ID AND T4.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS T5 /* 주소기본 */ ON T5.ADR_ID = T4.ADR_ID AND T5.DTA_DL_YN = 'N' WHERE T2.APY_YM = TO_CHAR(SYSDATE , 'YYYYMM') ) A1 ON A1.WARE_NO = T1.STR_OJ_WARE_NO INNER JOIN TB_PDBS_PD_BAS T2 /* 상품기본 */ ON T1.ITM_PD_CD = T2.PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL T3 /* 상품각사속성상세 */ ON T3.PD_CD = T2.PD_CD WHERE T1.DTA_DL_YN = 'N' AND T2.DTA_DL_YN = 'N' AND T3.DTA_DL_YN = 'N' AND T3.PD_EXTS_PRP_GRP_CD = 'PART'
