mapper.select:
SELECT D1.WARE_NO
     , D1.WARE_NM
  FROM TB_SVST_MCBY_WARE_IZ D1   /* 월별창고내역 */
 INNER JOIN
     (
       SELECT DISTINCT STR_WARE_NO   /* 입고창고번호 */
         FROM TB_SVST_ITM_QOM_ASN_IZ   /* 품목물량배정내역 */
        WHERE DTA_DL_YN = 'N'
          AND ASN_OJ_YM = #{apyYm}
     ) D2
    ON D2.STR_WARE_NO = D1.WARE_NO
 WHERE D1.DTA_DL_YN      = 'N'
   AND D1.APY_YM         = #{apyYm}
   AND D1.WARE_DV_CD     = #{wareDvCd}
   AND D1.WARE_DTL_DV_CD = #{wareDtlDvCd}
 ORDER BY D1.WARE_USE_YN DESC, TO_NUMBER(D1.SORT_DV_VAL), D1.WARE_NO

SELECT 1
  FROM TB_SVST_ITM_QOM_ASN_IZ   /* 품목물량배정내역 */
 WHERE DTA_DL_YN = 'N'
   AND ASN_OJ_YM = #{asnOjYm}
   AND ASN_TN_N  = #{cnt}
   AND ROWNUM    = 1

SELECT D2.SAP_MAT_CD AS SAP_CD /* SAP코드 */ , D1.ITM_PD_CD /* 품목코드 */ , D2.PD_ABBR_NM AS ITM_PD_NM /* 품목이름 */ , D1.STR_WARE_NO AS WARE_NO /* 창고번호 */ , D1.WARE_MNGT_PRTNR_NO AS PRTNR_NO /* 파트너번호 */ , D6.PRTNR_KNM AS PRTNR_NM /* 파트너명 */ , D4.WARE_NM /* 창고명 */ , ( SELECT PITM_STOC_A_GD_QTY FROM TB_SVST_MCITM_STOC_IZ S1 /* 월별품목재고내역 */ INNER JOIN TB_SVST_MCBY_WARE_IZ S2 /* 월별창고내역 */ ON S2.APY_YM = S1.BASE_YM AND S2.HGR_WARE_NO = S1.WARE_NO WHERE S1.BASE_YM = D1.ASN_OJ_YM AND S1.ITM_PD_CD = D1.ITM_PD_CD AND S2.WARE_NO = D1.STR_WARE_NO ) AS CENTER_QTY /* 센터수량 */ , D1.GE_ASN_QOM_CT AS GE_QTY /* 일반수량 */ , D1.CRP_ASN_QOM_CT AS CRP_QTY /* 법인수량 */ , D1.WO_ASN_QOM_CT AS TOTAL_QTY /* 전체수량 */ , D1.ETN_WTCF_APY_QTY AS APY_QTY /* 가중치적용 */ , D1.MCBY_ACU_OSTR_QTY AS OSTR_QTY /* 누적출고 */ , D1.BFSVC_FSH_CT AS BS_QTY /* BS완료 */ , D1.CRTL_STOC_QTY AS STOC_QTY /* 현재재고 */ , D1.THWK_EXP_QTY AS THWK_QTY /* 금주예정 */ , D1.BORR_EXP_QTY AS BORR_QTY /* 차주예정 */ , D1.CNFM_QTY /* 확정수량 */ , D1.BOX_UNIT_QTY AS BOX_QTY /* 박스수량 */ , D1.BLD_CD /* 빌딩코드 */ , D5.BLD_NM /* 빌딩명 */ , D8.LOCARA_TNO /* 지역전화번호 */ , D8.EXNO_ENCR /* 전화국번호암호화 */ , D8.IDV_TNO /* 개별전화번호 */ , NVL(D7.NEW_ADR_ZIP, D7.OLD_ADR_ZIP) AS ADR_ZIP /* 우편번호 */ , D7.RNADR /* 주소1 */ , D7.RDADR /* 주소2 */ FROM TB_SVST_ITM_QOM_ASN_IZ D1 /* 품목물량배정내역 */ INNER JOIN TB_PDBS_PD_BAS D2 /* 상품기본 */ ON D2.PD_CD = D1.ITM_PD_CD INNER JOIN TB_PDBS_PD_ECOM_PRP_DTL D3 /* 상품각사속성상세 */ ON D3.PD_CD = D2.PD_CD LEFT OUTER JOIN TB_SVST_MCBY_WARE_IZ D4 /* 월별창고내역 */ ON D4.WARE_NO = D1.STR_WARE_NO AND D4.DTA_DL_YN = 'N' AND D4.APY_YM = #{apyYm} LEFT OUTER JOIN TB_OGBS_BLD_BAS D5 /* 빌딩기본 */ ON D5.OG_TP_CD = D1.OG_TP_CD AND D5.BLD_CD = D1.BLD_CD AND D5.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ D6 /* 월파트너내역 */ ON D6.BASE_YM = D1.ASN_OJ_YM AND D6.OG_TP_CD = D1.OG_TP_CD AND D6.PRTNR_NO = D1.WARE_MNGT_PRTNR_NO AND D6.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_ADR_BAS D7 /* 주소기본 */ ON D7.ADR_ID = D5.ADR_ID AND D7.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_PRTNR_BAS D8 /* 파트너기본 */ ON D8.OG_TP_CD = D6.OG_TP_CD AND D8.PRTNR_NO = D6.PRTNR_NO AND D8.DTA_DL_YN = 'N' WHERE D1.DTA_DL_YN = 'N' AND D2.DTA_DL_YN = 'N' AND D2.PD_TP_CD = 'M' AND D3.DTA_DL_YN = 'N' AND D3.PD_EXTS_PRP_GRP_CD = 'PART' AND D1.ASN_OJ_YM = #{asnOjYm} AND D1.ASN_TN_N = #{cnt} AND D1.OSTR_WARE_NO = #{ostrWareNo}
AND D4.HGR_WARE_NO = #{strWareNo}
AND D1.ITM_PD_CD = #{itmPdCd}
AND D3.PD_PRP_VAL19 = #{itmKndCd}
AND D1.STR_WARE_NO LIKE #{wareDvCd} || '%'
ORDER BY D1.ASN_TN_N, D5.BLD_NM, D6.PRTNR_KNM, D1.ITM_PD_CD
