mapper.select:
SELECT T1.ITM_PD_CD , TO_CHAR(TO_NUMBER(P1.SVPD_SAP_CD)) SAP_MAT_CD , P1.SVPD_NM_KOR AS ITM_PD_NM /* BTD_STOC_A_GD_QTY(기초재고A등급수량) + BTD_STOC_B_GD_QTY(기초재고B등급수량) +BTD_STOC_E_GD_QTY(기초재고E등급수량)+ BTD_STOC_R_GD_QTY(기초재고R등급수량) + STR_QTY(입고수량) + USE_QTY(반품 사용수량) - OSTR_QTY(출고수량) - USE_QTY(반품외 사용수량)의 SUM*/ , T1.BTD_STOC_A_GD_QTY + T1.BTD_STOC_B_GD_QTY + T1.BTD_STOC_E_GD_QTY + T1.BTD_STOC_R_GD_QTY + NVL(T2.STR_QTY, 0) + (NVL(T2.RTNGD_STR_INSD_QTY, 0) + NVL(T2.RTNGD_STR_OTSD_QTY, 0)) - NVL(T3.OSTR_QTY, 0) - (NVL(T3.RTNGD_OSTR_INSD_QTY, 0) + NVL(T3.RTNGD_OSTR_OTSD_QTY, 0)) AS BAS_STOC_QTY /* 입고수량 */ , NVL(T2.PRCHS_STR_QTY, 0) AS PRCHS_STR_QTY , NVL(T2.NOM_STR_QTY, 0) AS NOM_STR_QTY , NVL(T2.QOM_ASN_STR_QTY, 0) AS QOM_ASN_STR_QTY , NVL(T2.QOM_MMT_STR_QTY, 0) AS QOM_MMT_STR_QTY , NVL(T2.RTNGD_STR_INSD_QTY, 0) AS RTNGD_STR_INSD_QTY , NVL(T2.RTNGD_STR_OTSD_QTY, 0) AS RTNGD_STR_OTSD_QTY , NVL(T2.ETC_STR_QTY, 0) AS ETC_STR_QTY , NVL(T5.STR_CTR_QTY, 0) AS STR_CTR_QTY , NVL(T6.CNFM_PITM_STR_GAP_QTY, 0) AS CNFM_PITM_STR_GAP_QTY /* 출고수량 */ , NVL(T3.NOM_OSTR_QTY, 0) AS NOM_OSTR_QTY , NVL(T3.SVC_NOM_OSTR_QTY, 0) AS SVC_NOM_OSTR_QTY , NVL(T3.SELL_NOM_OSTR_QTY, 0) AS SELL_NOM_OSTR_QTY , NVL(T3.QOM_ASN_OSTR_QTY, 0) AS QOM_ASN_OSTR_QTY , NVL(T3.QOM_MMT_OSTR_QTY, 0) AS QOM_MMT_OSTR_QTY , NVL(T3.RTNGD_OSTR_INSD_QTY, 0) AS RTNGD_OSTR_INSD_QTY , NVL(T3.RTNGD_OSTR_OTSD_QTY, 0) AS RTNGD_OSTR_OTSD_QTY , NVL(T4.USE_QTY, 0) AS USE_QTY , NVL(T4.YEAR_IN_USE_QTY, 0) AS YEAR_IN_USE_QTY , NVL(T4.YEAR_OUT_USE_QTY, 0) AS YEAR_OUT_USE_QTY , NVL(T3.REFR_OSTR_QTY, 0) AS REFR_OSTR_QTY , NVL(T3.SELL_OSTR_QTY, 0) AS SELL_OSTR_QTY , NVL(T3.DSU_OSTR_QTY, 0) AS DSU_OSTR_QTY , NVL(T3.ETC_OSTR_QTY, 0) AS ETC_OSTR_QTY , NVL(T5.OSTR_CTR_QTY, 0) AS OSTR_CTR_QTY , NVL(T6.CNFM_PITM_OSTR_GAP_QTY, 0) AS CNFM_PITM_OSTR_GAP_QTY , NVL(T3.MMT_STOC_QTY, 0) AS MMT_STOC_QTY FROM ( SELECT X1.ITM_PD_CD , SUM(X1.BTD_STOC_A_GD_QTY) BTD_STOC_A_GD_QTY , SUM(X1.BTD_STOC_B_GD_QTY) BTD_STOC_B_GD_QTY , SUM(X1.BTD_STOC_E_GD_QTY) BTD_STOC_E_GD_QTY , SUM(X1.BTD_STOC_R_GD_QTY) BTD_STOC_R_GD_QTY FROM TB_SVST_MCITM_STOC_IZ X1 INNER JOIN TB_SVST_MCBY_WARE_IZ Z1 ON X1.WARE_NO = Z1.WARE_NO WHERE X1.BASE_YM BETWEEN SUBSTR(#{stFromYmd}, 1, 6) AND SUBSTR(#{edToYmd}, 1, 6)
AND Z1.WARE_DV_CD = #{strWareDvCd}
AND Z1.HGR_WARE_NO = #{strWareNoM}
AND Z1.WARE_NO = #{strWareNoD}
GROUP BY X1.ITM_PD_CD ) T1 INNER JOIN () P1 ON T1.ITM_PD_CD = P1.SVPD_PD_CD LEFT JOIN ( SELECT X1.ITM_PD_CD , SUM(X1.STR_QTY) STR_QTY , SUM(CASE WHEN X1.STR_TP_CD = '110' THEN X1.STR_QTY END) AS PRCHS_STR_QTY , SUM(CASE WHEN X1.STR_TP_CD = '121' THEN X1.STR_QTY END) AS NOM_STR_QTY , SUM(CASE WHEN X1.STR_TP_CD = '122' THEN X1.STR_QTY END) AS QOM_ASN_STR_QTY , SUM(CASE WHEN X1.STR_TP_CD = '123' THEN X1.STR_QTY END) AS QOM_MMT_STR_QTY , SUM(CASE WHEN X1.STR_TP_CD = '161' THEN X1.STR_QTY END) AS RTNGD_STR_INSD_QTY , SUM(CASE WHEN X1.STR_TP_CD = '162' THEN X1.STR_QTY END) AS RTNGD_STR_OTSD_QTY , SUM(CASE WHEN X1.STR_TP_CD = '117' THEN X1.STR_QTY END) AS ETC_STR_QTY FROM TB_SVST_ITM_STR_IZ X1 INNER JOIN TB_SVST_MCBY_WARE_IZ X2 ON X1.STR_WARE_NO = X2.WARE_NO AND SUBSTR(X1.STR_RGST_DT,1,6) = X2.APY_YM WHERE 1 = 1 AND X1.STR_RGST_DT BETWEEN #{stFromYmd} AND #{edToYmd}
AND X2.WARE_DV_CD = #{strWareDvCd}
AND X2.HGR_WARE_NO = #{strWareNoM}
AND X2.WARE_NO = #{strWareNoD}
AND X1.ITM_GD_CD = #{itmGdCd}
AND X1.DTA_DL_YN = 'N' GROUP BY X1.ITM_PD_CD ) T2 ON T1.ITM_PD_CD = T2.ITM_PD_CD LEFT JOIN ( SELECT X1.ITM_PD_CD , SUM(X1.OSTR_QTY) AS OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '221' THEN X1.OSTR_QTY END) NOM_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '221' AND X1.OSTR_WARE_DV_CD = '1' THEN X1.OSTR_QTY END) SVC_NOM_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '221' AND X1.OSTR_WARE_DV_CD = '3' THEN X1.OSTR_QTY END) SELL_NOM_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '222' THEN X1.OSTR_QTY END) QOM_ASN_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '223' THEN X1.OSTR_QTY END) QOM_MMT_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '261' THEN X1.OSTR_QTY END) RTNGD_OSTR_INSD_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '262' THEN X1.OSTR_QTY END) RTNGD_OSTR_OTSD_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '218' THEN X1.OSTR_QTY END) REFR_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '211' THEN X1.OSTR_QTY END) SELL_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '212' THEN X1.OSTR_QTY END) DSU_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD = '217' THEN X1.OSTR_QTY END) ETC_OSTR_QTY , SUM(CASE WHEN X1.OSTR_TP_CD NOT IN ('211', '217') THEN X1.OSTR_QTY END) MMT_STOC_QTY FROM TB_SVST_ITM_OSTR_IZ X1 INNER JOIN TB_SVST_MCBY_WARE_IZ X2 ON X1.OSTR_WARE_NO = X2.WARE_NO AND SUBSTR(X1.OSTR_DT,1,6) = X2.APY_YM WHERE 1 = 1 AND X1.OSTR_DT BETWEEN #{stFromYmd} AND #{edToYmd}
AND X2.WARE_DV_CD = #{strWareDvCd}
AND X2.HGR_WARE_NO = #{strWareNoM}
AND X2.WARE_NO = #{strWareNoD}
AND X1.ITM_GD_CD = #{itmGdCd}
AND X1.DTA_DL_YN = 'N' GROUP BY X1.ITM_PD_CD ) T3 ON T1.ITM_PD_CD = T3.ITM_PD_CD LEFT JOIN ( SELECT X1.ITM_PD_CD , SUM(X1.USE_QTY) USE_QTY , SUM(CASE WHEN TRUNC(TO_DATE(X1.OSTR_DT) - TO_DATE(X2.IST_DT)) <= 365 THEN X1.USE_QTY END) YEAR_IN_USE_QTY , SUM(CASE WHEN TRUNC(TO_DATE(X1.OSTR_DT) - TO_DATE(X2.IST_DT)) > 365 THEN X1.USE_QTY END) YEAR_OUT_USE_QTY FROM TB_SVST_SV_WK_OSTR_IZ X1 INNER JOIN TB_SVPD_CST_SV_EXCN_IZ X2 ON X1.CNTR_NO = X2.CNTR_NO AND X1.CNTR_SN = X2.CNTR_SN INNER JOIN TB_SVST_MCBY_WARE_IZ X3 ON X1.WK_WARE_NO = X3.WARE_NO AND SUBSTR(X1.FNL_VST_FSH_DT,1,6) = X3.APY_YM WHERE 1 = 1 AND X1.FNL_VST_FSH_DT BETWEEN #{stFromYmd} AND #{edToYmd} AND X1.SV_BIZ_HCLSF_CD IN ('1', '2', '3', '4') AND X1.DTA_DL_YN = 'N'
AND X3.WARE_DV_CD = #{strWareDvCd}
AND X3.HGR_WARE_NO = #{strWareNoM}
AND X3.WARE_NO = #{strWareNoD}
AND X1.FNL_ITM_GD_CD = #{itmGdCd}
GROUP BY X1.ITM_PD_CD ) T4 ON T1.ITM_PD_CD = T4.ITM_PD_CD LEFT JOIN ( SELECT X1.PD_CD /* 김난화 부장님 확인 후 수정 필요 */ , SUM(X1.CTR_QTY) STR_CTR_QTY , SUM(X1.CTR_QTY) OSTR_CTR_QTY FROM TB_SVST_ITM_GD_CTR_IZ X1 INNER JOIN TB_SVST_MCBY_WARE_IZ Z1 ON X1.WARE_NO = Z1.WARE_NO WHERE 1 = 1 AND X1.CTR_WK_DT BETWEEN #{stFromYmd} AND #{edToYmd} AND X1.DTA_DL_YN = 'N'
AND Z1.WARE_DV_CD = #{strWareDvCd}
AND Z1.HGR_WARE_NO = #{strWareNoM}
AND Z1.WARE_NO = #{strWareNoD}
AND X1.AFCT_ITM_GD_CD = #{itmGdCd}
GROUP BY X1.PD_CD ) T5 ON T1.ITM_PD_CD = T5.PD_CD LEFT JOIN ( SELECT X1.PD_CD , SUM(X1.CNFM_PITM_STR_GAP_QTY) CNFM_PITM_STR_GAP_QTY , SUM(X1.CNFM_PITM_OSTR_GAP_QTY) CNFM_PITM_OSTR_GAP_QTY FROM TB_SVST_MCBY_STOC_ACINSP_IZ X1 INNER JOIN TB_SVST_MCBY_WARE_IZ Z1 ON X1.WARE_NO = Z1.WARE_NO AND X1.BASE_YM = Z1.APY_YM WHERE 1 = 1 AND X1.BASE_YM BETWEEN SUBSTR(#{stFromYmd}, 1, 6) AND SUBSTR(#{edToYmd}, 1, 6) AND X1.DTA_DL_YN = 'N'
AND Z1.WARE_DV_CD = #{strWareDvCd}
AND Z1.HGR_WARE_NO = #{strWareNoM}
AND Z1.WARE_NO = #{strWareNoD}
AND 'A' = #{itmGdCd}
GROUP BY X1.PD_CD ) T6 ON T1.ITM_PD_CD = T6.PD_CD WHERE 1 = 1
AND P1.SVPD_ITEM_KND = #{itmKnd}
AND P1.SVPD_USE_YN = #{useYn}
AND P1.SVPD_PD_CD = #{itmPdCdFrom}
AND P1.SVPD_SAP_CD BETWEEN #{sapMatCdFrom} AND #{sapMatCdTo}
AND P1.SVPD_SAP_CD IN
TRIM(#{sapMatDpct})
