mapper.select:
SELECT * FROM TB_SVST_MCBY_WARE_IZ WHERE WARE_DV_CD = '2' AND APY_YM = TO_CHAR(SYSDATE,'YYYYMM') AND WARE_ICHR_NO = '000'
AND OG_TP_CD = #{ogTpCd}
AND OG_ID = #{ogId}

WITH PD_INFO AS ( SELECT T1.PD_CD , T1.PD_CLSF_ID , T1.PD_TP_CD /* 상품유형코드 : C 복합상품, P 기준상품, S 서비스, M 교재제품, Y 융합상품 */ , T1.SAP_MAT_CD , T1.PD_ABBR_NM AS PD_NM , T2.PD_EXTS_PRP_GRP_CD /* 상품확장속성그룹코드 */ , T2.PD_PRP_VAL01 /* ASIS 상품코드 */ , T2.PD_PRP_VAL31 /* ASIS 상품코드 */ , T2.PD_PRP_VAL19 , T2.PD_PRP_VAL20 , F_CMZ_CD_NM('TNT_WELLS', 'AS_MAT_ITM_KND_CD', T2.PD_PRP_VAL19, 'ko') AS AS_MAT_ITM_KND_CD --T2.PD_PRP_VAL19 /* 품목종류코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'AS_MAT_ITM_GRP_CD', T2.PD_PRP_VAL20, 'ko') AS AS_MAT_ITM_GRP_CD -- T2.PD_PRP_VAL20 /* 제품 그룹 코드 */ , F_CMZ_CD_NM('TNT_WELLS', 'SV_IST_PCSV_DV_CD', T2.PD_PRP_VAL01, 'ko') AS SV_IST_PCSV_DV_CD_NM , T2.PD_PRP_VAL02 , F_CMZ_CD_NM('TNT_WELLS', 'IST_BZS_CD', T2.PD_PRP_VAL02, 'ko') AS IST_BZS_CD_NM , T1.SELL_TP_CD , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', T1.SELL_TP_CD, 'ko') AS SELL_TP_CD_NM , T1.SV_PD_TP_CD /* 서비스상품유형코드(AS-IS 용도구분) : 1 방문, 2 택배, 3 홈케어, 4 혼합(방문+홈케어), 5 혼합(택배+홈케어)*//*SV_TP_CD 0 공통, 1 일반, 2 업소, 3 특별１, 4 특별２, 5 홈케어, 6 특별(조리수), 7 택배1, 8 택배2, 9 예외 */ FROM WSMDBS.TB_PDBS_PD_BAS T1 /* 상품기본 */ LEFT OUTER JOIN WSMDBS.TB_PDBS_PD_ECOM_PRP_DTL T2 /* 상품각사속성상세 */ ON T1.PD_CD = T2.PD_CD AND T2.PD_EXTS_PRP_GRP_CD IN ('PART') /* AND T2.PD_EXTS_PRP_GRP_CD IN ('SPP', 'SCHD', 'PDCT') */ /*상품확장속성그룹코드 CMN 공통, AFS AS, PRC 가격, STLM 결제, CNTR 계약, EXCH 교환/반품, ETC 기타, SL 매출, GO 발주, SPP 배송, ANA 분석, FEE 영업수수료, HIST 이력, SCHD 일정관리, COCN 전집, LRNN 학습/외국어, LV 학습단계, PART AS부품, FINC 재무, PDCT 판매제품*/ WHERE 1=1 AND (T2.PD_PRP_VAL19 = '4' OR T2.PD_PRP_VAL31 = '813010' OR T2.PD_PRP_VAL01||T2.PD_PRP_VAL31 IN('42000811030', '42000811040', '42000811050', '42000811060') OR (T1.PD_NM LIKE '%특별자재%') OR (T1.PD_NM LIKE '%발주X TANK%')) AND T2.PD_PRP_VAL01 NOT IN ('42050', '42970', '42990') AND T2.PD_PRP_VAL20 NOT IN ('10', '11', '94') AND T1.PD_NM NOT LIKE '%리페어%' AND T2.PD_PRP_VAL20 IS NOT NULL -- AND T2.PD_PRP_VAL20 IN ('1') --- ----- 정수기 ) /* END OF WITH FOR PAGING */ SELECT P1.PD_CD , P1.PD_CLSF_ID , AS_MAT_ITM_GRP_CD AS PD_CLSF_NM , P1.PD_NM , P1.SAP_MAT_CD , NVL(SUM(CASE WHEN FNL_ITM_GD_CD = 'E' THEN 1 ELSE 0 END),0) AS FNL_E_SUM , NVL(SUM(CASE WHEN FNL_ITM_GD_CD = 'R' THEN 1 ELSE 0 END),0) AS FNL_R_SUM , NVL(SUM(CASE WHEN FNL_ITM_GD_CD = 'X' THEN 1 ELSE 0 END),0) AS FNL_X_SUM FROM PD_INFO P1 LEFT OUTER JOIN TB_SVST_SV_WK_OSTR_IZ O1 /* 서비스작업출고내역 st163 */ ON O1.ITM_PD_CD = P1.PD_CD AND O1.SV_BIZ_HCLSF_CD = '6'
AND O1.OSTR_CONF_DT BETWEEN #{startDate} AND #{endDate}
INNER JOIN TB_SVST_MCBY_WARE_IZ O2 /* 월별창고내역 ST102 */ ON O2.APY_YM = SUBSTR(#{startDate},1,6) AND O1.WK_WARE_NO = O2.WARE_NO AND O2.HGR_WARE_NO = #{svCnrCd}
WHERE 1=1
AND O1.RTNGD_PROCS_TP_CD = #{rtngdProcsTpCd}
GROUP BY P1.PD_CD, P1.PD_CLSF_ID, P1.PD_NM, P1.SAP_MAT_CD, AS_MAT_ITM_GRP_CD
