mapper.insert:
INSERT INTO TB_SVST_ITM_OSTR_IZ (  /* 품목출고내역 */
       ITM_OSTR_NO              /* 품목출고번호 */
     , OSTR_SN                  /* 출고일련번호 */
     , OSTR_TP_CD               /* 출고유형코드 */
     , OSTR_WARE_NO             /* 출고창고번호 */
     , OSTR_DT                  /* 출고일자 */
     , ITM_KND_CD               /* 품목종류코드 */
     , ITM_PD_CD                /* 품목상품코드 */
     , OSTR_WARE_DV_CD          /* 출고창고구분코드 */
     , MNGT_UNIT_CD             /* 관리단위코드 */
     , BOX_UNIT_QTY             /* 박스단위수량 */
     , ITM_GD_CD                /* 품목등급코드 */
     , OSTR_QTY                 /* 출고수량 */
     , WARE_MNGT_PRTNR_NO       /* 창고관리파트너번호 */
     , WARE_MNGT_PRTNR_OG_TP_CD /* 창고관리파트너조직유형코드 */
     , OSTR_RSON_CD             /* 출고사유코드 */
     , ACB_DT                   /* 회계일자 */
     , EVID_DV_CD               /* 증빙구분코드 */
     , STR_TP_CD                /* 입고유형코드 */
     , STR_WARE_NO              /* 입고창고번호 */

     , ITM_STR_NO               /* 품목입고번호 */
     , STR_SN                   /* 입고일련번호 */

     , DTA_DL_YN                /* 데이터삭제여부 */
     , RMK_CN                   /* 비고내용 */
     )
SELECT #{itmOstrNo}
     , ${ostrSn}
     , #{ostrTpCd}
     , #{ostrWareNo}
     , #{ostrDt}
     , #{itmKndCd}
     , #{itmPdCd}
     , #{wareDvCd}
     , #{mngtUnitCd}
     , 0
     , #{itmGdCd}
     , ${ostrQty}
     , #{session.employeeIDNumber}
     , (SELECT T1.OG_TP_CD
          FROM TB_SVST_MCBY_WARE_IZ T1
        WHERE 1 = 1
          AND T1.APY_YM = SUBSTR(#{ostrDt}, 1, 6)
          AND T1.WARE_NO = #{ostrWareNo}
          AND T1.WARE_MNGT_PRTNR_NO = #{session.employeeIDNumber})
     , #{ostrRsonCd}
     , #{acbDt}
     , #{evidDvCd}
     , (CASE #{ostrTpCd} WHEN '261' THEN '161' WHEN '262' THEN '162' ELSE '' END)
     , #{strWareNo}

     , #{itmStrNo}
     , ${strSn}

     , 'N'
     , #{rmkCn}

  FROM DUAL

INSERT INTO TB_SVST_ITM_STR_IZ (  /* 품목입고내역 */
       ITM_STR_NO                /* 품목입고번호 */
     , STR_SN                    /* 입고일련번호 */
     , STR_TP_CD                 /* 입고유형코드 */
     , STR_RGST_DT               /* 입고등록일자 */
     , STR_WARE_NO               /* 입고창고번호 */
     , WARE_MNGT_PRTNR_NO        /* 창고관리파트너번호 */
     , WARE_MNGT_PRTNR_OG_TP_CD  /* 창고관리파트너조직유형코드 */
     , STR_WARE_DV_CD            /* 입고창고구분코드 */
     , DLVG_DLPNR_NO             /* 납품거래처번호 */
     , ITM_PD_CD                 /* 품목상품코드 */
     , ITM_GD_CD                 /* 품목등급코드 */
     , MNGT_UNIT_CD              /* 관리단위코드 */
     , STR_QTY                   /* 입고수량 */
     , STR_UPRC_AMT              /* 입고단가금액 */
     , ACB_DT                    /* 회계일자 */
     , EVID_DV_CD                /* 증빙구분코드 */
     , ITM_OSTR_NO               /* 품목출고번호 */
     , OSTR_SN                   /* 출고일련번호 */
     , OSTR_TP_CD                /* 출고유형코드 */
     , OSTR_WARE_NO              /* 출고창고번호 */
     , OSTR_DT                   /* 출고일자 */
     , RMK_CN                    /* 비고내용 */
     , DRT_STR_YN                /* 직접입고여부 */
     , DTA_DL_YN                 /* 데이터삭제여부 */
     )
SELECT #{itmStrNo}
     , ${strSn}
     , (CASE #{ostrTpCd} WHEN '261' THEN '161' WHEN '262' THEN '162' ELSE '' END)
     , #{ostrDt}
     , #{strWareNo}
     , ( SELECT T1.WARE_MNGT_PRTNR_NO
              FROM TB_SVST_MCBY_WARE_IZ T1
             WHERE APY_YM = TO_CHAR(SYSDATE, 'YYYYMM')
               AND WARE_NO = #{strWareNo}
               AND DTA_DL_YN = 'N'
               AND WARE_USE_YN = 'Y')
     , (SELECT T1.OG_TP_CD
          FROM TB_SVST_MCBY_WARE_IZ T1
        WHERE 1 = 1
          AND T1.APY_YM = SUBSTR(#{ostrDt}, 1, 6)
          AND T1.WARE_NO = #{strWareNo})
     , #{strWareDvCd}
     , #{ostrWareNo}
     , #{itmPdCd}
     , #{itmGdCd}
     , #{mngtUnitCd}
     , ${ostrQty}
     , 0
     , #{acbDt}
     , #{evidDvCd}
     , #{itmOstrNo}
     , ${ostrSn}
     , #{ostrTpCd}
     , #{ostrWareNo}
     , #{ostrDt}
     , #{rmkCn}
     , 'N'
     , 'N'

  FROM DUAL

mapper.delete:
UPDATE TB_SVST_ITM_OSTR_IZ /* 품목출고내역 */
   SET DTA_DL_YN = 'Y'
 WHERE ITM_OSTR_NO = #{itmOstrNo}
   AND OSTR_SN = ${ostrSn}

UPDATE TB_SVST_ITM_STR_IZ /* 품목입고내역 */
   SET DTA_DL_YN = 'Y'
 WHERE ITM_STR_NO = #{itmStrNo}
   AND STR_SN = ${strSn}

mapper.select:
SELECT T1.ITM_OSTR_NO                  AS OSTR_AK_NO
     , T1.OSTR_SN                      AS OSTR_AK_SN
  FROM TB_SVST_ITM_OSTR_IZ T1
 WHERE T1.ITM_OSTR_NO = #{itmOstrNo}
   AND T1.OSTR_SN = #{ostrSn}
