SELECT A1.CST_SV_ASN_NO , A1.SAP_CD , A1.ITM_PD_CD , A1.ITM_PD_NM , A1.ITEM_GR_NM , A1.ITEM_GR , A1.STKR_PRNT_YN , A1.IST_DT , A1.REQD_DT , A1.VST_FSH_DT , A1.RTNGD_CONF_YN , A1.USE_DAY , A1.USE_MTHS , A1.ERROR_CHECK , A1.REFURBISH_YN , A1.FNL_ITM_GD_CD , A1.USE_QTY , A1.REFR_AS_RCP_YN , A1.CNTR_DTL_NO , A1.RCGVP_KNM , A1.SELL_TP_CD , A1.SELL_TP_NM , A1.MNGT_UNIT_NM , A1.REFER_ARTC , A1.SV_BIZ_DCLSF_CD , A1.SV_BIZ_DCLSF_NM , A1.OSTR_CONF_DT , A1.OSTR_CONF_DT1 , A1.OSTR_DT , A1.RTNGD_PROCS_TP_CD , A1.RTNGD_PROCS_TP_CD1 , A1.RMK_CN , A1.CNTR_NO_NEW , A1.BAR_CD , A1.AS_LCT_NM , A1.AS_PHN_NM , A1.AS_CAUS_NM , A1.SV_PROCS_CN , A1.ICHR_PRTNR_NO , A1.EMP_NM , A1.RCP_ICHR_PRTNR_NO , A1.FST_RGST_USER_NM , A1.CNSL_MO_CN , A1.OG_NM , A1.RCST_DV , A1.PRTNR_KNM , A1.BAD_DV_NM , A1.WK_OSTR_SN , A1.SV_BIZ_HCLSF_CD , A1.WK_WARE_NO , A1.WARE_NM , A1.MNGR_DV_CD , A1.ITM_OSTR_NO , A1.STKR_R_PRNT_YN , A1.RTNGD_RVPY_PROCS_YN , A1.ITEM_KND , A1.MGT_UNT , A1.CNTR_NO , A1.CNTR_SN FROM (SELECT
/*+ INDEX(T1 IX_SVST_SV_WK_OSTR_IZ_01) */
T1.CST_SV_ASN_NO /*고객서비스배정내역*/ , T5.SVPD_SAP_CD AS SAP_CD /*SAP코드*/ , T1.ITM_PD_CD /*품목상품코드*/ , T5.SVPD_NM_KOR AS ITM_PD_NM /*품목상품명*/ , CASE WHEN T5.SVPD_ITEM_GR IN ('1', '3', '4', '92', '11', '10', '93', '95', '91') THEN F_CMZ_CD_NM('TNT_WELLS', 'PD_GRP_CD', T5.SVPD_ITEM_GR) WHEN T5.SVPD_ITEM_GR = '2' AND T5.SVPD_ITEM_CD = '42990' THEN '커피머신' WHEN T5.SVPD_ITEM_CD IN ('49010', '49020') THEN '커피머신' WHEN T5.SVPD_ITEM_GR IN ('5', '7', '8') THEN '기타' WHEN T5.SVPD_ITEM_GR IN ('9') AND T5.SVPD_ITEM_CD IN ('49200', '49060', '49070', '49080') THEN '기타' ELSE F_CMZ_CD_NM('TNT_WELLS', 'PD_GRP_CD', T5.SVPD_ITEM_GR) END AS ITEM_GR_NM , T5.SVPD_ITEM_GR AS ITEM_GR , T1.STKR_PRNT_YN /*스티커출력여부*/ , T3.IST_DT /*설치일자*/ , (SELECT A1.REQD_DT FROM TB_SSCT_CNTR_WELLS_DTL A1 WHERE 1 = 1 AND A1.CNTR_NO = X1.CNTR_NO) AS REQD_DT /*철거일자*/ , TO_CHAR(TO_DATE(T4.VST_FSH_DT,'YYYYMMDD'),'YYYY-MM-DD') || ' ' || TO_CHAR(TO_DATE(T4.VST_FSH_HH,'HH24MISS'),'HH24:MI:SS') AS VST_FSH_DT /*작업일자*/ , T1.RTNGD_CONF_YN /*반품확인여부*/ , TO_DATE(T1.FNL_VST_FSH_DT) - TO_DATE(T3.IST_DT) AS USE_DAY , CEIL(MONTHS_BETWEEN(T1.FNL_VST_FSH_DT, T3.IST_DT)) AS USE_MTHS /*사용개월*/ , (CASE WHEN SUBSTR(T1.SV_BIZ_DCLSF_CD, 0, 2) = '34' AND T5.SVPD_ITEM_KND = '4' AND T1.FNL_ITM_GD_CD = 'X' THEN '' WHEN X2.FRISU_YN = 'Y' THEN '' WHEN SUBSTR(T1.SV_BIZ_DCLSF_CD, 0, 2) = '34' AND T5.SVPD_ITEM_KND = '4' AND (CASE WHEN X2.SELL_TP_CD = '1' THEN 'E' /*일시불(할부)판매고객*/ WHEN T9.USE_DAYS <= 7 AND X2.ALNCMP_CD = '45' AND X2.SELL_TP_CD IN ('2', '4') THEN 'E' /*K멤버스 7일 이하*/ WHEN T9.USE_DAYS > 7 AND X2.ALNCMP_CD = '45' AND X2.SELL_TP_CD IN ('2', '4') THEN 'R' /*K멤버스 7일 초과*/ WHEN T9.USE_DAYS <= 14 AND X2.ALNCMP_CD <> '45' AND X2.SELL_TP_CD IN ('2', '4') THEN 'E' /*K멤버스외 14일 이하*/ WHEN T9.USE_DAYS > 14 AND X2.ALNCMP_CD <> '45' AND X2.SELL_TP_CD IN ('2', '4') THEN 'R' /*K멤버스외 14일 초과*/ ELSE 'R' END ) <> T1.FNL_ITM_GD_CD THEN '1' ELSE '' END) AS ERROR_CHECK , (CASE WHEN MONTHS_BETWEEN(T1.FNL_VST_FSH_DT, T3.IST_DT) <= 1 THEN 'Y' ELSE 'N' END) AS REFURBISH_YN --리퍼여부 , T1.FNL_ITM_GD_CD /*등급*/ , T1.USE_QTY /*사용수량*/ , CASE WHEN T1.REFR_AS_RCP_YN = '1' THEN '접수완료' ELSE '' END REFR_AS_RCP_YN /*리퍼접수*/ , T1.CNTR_NO || '-' ||T1.CNTR_SN AS CNTR_DTL_NO /*고객번호*/ , X4.RCGVP_KNM , X2.SELL_TP_CD /*판매유형코드*/ , F_CMZ_CD_NM('TNT_WELLS', 'SELL_TP_CD', X2.SELL_TP_CD) AS SELL_TP_NM /*판매유형명*/ , F_CMZ_CD_NM('TNT_WELLS', 'PRD_MNGT_TP_CD', X2.SELL_TP_CD) AS MNGT_UNIT_NM /* 관리단위 */ , (CASE WHEN X2.FRISU_YN = 'Y' THEN '무료체험' WHEN X2.ALNCMP_CD = '45' THEN F_CMZ_CD_NM('TNT_WELLS', 'ALNCMP_CD', X2.ALNCMP_CD) ELSE '' END) AS REFER_ARTC /*참고사항*/ , T1.SV_BIZ_DCLSF_CD /*서비스업무세분류코드 BA04*/ , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_DCLSF_CD', T1.SV_BIZ_DCLSF_CD) AS SV_BIZ_DCLSF_NM /*업무유형*/ , T1.OSTR_CONF_DT /*확인일자*/ , T1.OSTR_CONF_DT AS OSTR_CONF_DT1 /*확인일자*/ , T1.OSTR_DT /*전산반품*/ , T1.RTNGD_PROCS_TP_CD /*반품처리유형코드*/ , T1.RTNGD_PROCS_TP_CD AS RTNGD_PROCS_TP_CD1 /*반품처리유형코드*/ , T1.RMK_CN /*특이사항*/ , (CASE WHEN SUBSTR(T1.CST_SV_ASN_NO,1,1) = '9' THEN T2.CNTR_NO ELSE '' END) AS CNTR_NO_NEW /*신규 고객번호*/ , (CASE WHEN T1.FNL_ITM_GD_CD = 'E' AND T1.SV_BIZ_DCLSF_CD = '3210' THEN T1.FST_BC_NO ELSE T4.BC_NO END) AS BAR_CD /*제조번호*/ , F_CMZ_CD_NM('TNT_WELLS', 'AS_LCT_CD', T4.AS_LCT_CD) AS AS_LCT_NM /*AS위치코드*/ , F_CMZ_CD_NM('TNT_WELLS', 'AS_PHN_CD', T4.AS_PHN_CD) AS AS_PHN_NM /*AS현상코드*/ , F_CMZ_CD_NM('TNT_WELLS', 'AS_CAUS_CD', T4.AS_CAUS_CD) AS AS_CAUS_NM /*AS원인코드*/ , T4.SV_PROCS_CN /*서비스처리내용*/ , T1.ICHR_PRTNR_NO /*담당엔지니어사번*/ , (SELECT A1.PRTNR_KNM FROM TB_OGBS_PRTNR_BAS A1 WHERE 1 = 1 AND A1.PRTNR_NO = T1.ICHR_PRTNR_NO AND A1.OG_TP_CD = T1.OG_TP_CD) AS EMP_NM /*담당엔지니어명*/ , T2.RCP_ICHR_PRTNR_NO /*철거요청사번*/ , (SELECT A.PRTNR_KNM FROM TB_OGBS_PRTNR_BAS A WHERE 1 = 1 AND A.PRTNR_NO = T2.RCP_ICHR_PRTNR_NO AND A.OG_TP_CD IN ('W02','W06','HR1') AND ROWNUM = 1) AS FST_RGST_USER_NM /*철거요청자명*/ , T2.CNSL_MO_CN /*접수내역*/ , D1.OG_NM /*접수자소속*/ , T7.PRTNR_KNM /*접수자명*/ , CASE WHEN SUBSTR(D2.DEPT_CD, 1, 1) BETWEEN 'A' AND 'H' THEN '빙판' WHEN SUBSTR(D2.DEPT_CD ,1, 1) BETWEEN 'Y' AND 'Z' OR D2.DEPT_CD = '75050' THEN '신규채널' ELSE '기타' END AS RCST_DV /*접수자 구분*/ , F_CMZ_CD_NM('TNT_WELLS', 'BAD_DV_CD', T4.BAD_DV_CD) AS BAD_DV_NM /*불량구분*/ , T1.WK_OSTR_SN /*작업출고일련번호*/ , T1.SV_BIZ_HCLSF_CD /*서비스대분류코드*/ , T1.WK_WARE_NO /*작업창고번호*/ , T8.WARE_NM /*창고명*/ , T1.MNGR_DV_CD /*관리자 구분코드*/ , T1.ITM_OSTR_NO /*품목출고번호*/ , T1.STKR_R_PRNT_YN /*스티거R출력여부*/ , DECODE(T1.RTNGD_RVPY_PROCS_YN, 'Y','Y','N') AS RTNGD_RVPY_PROCS_YN , T5.SVPD_ITEM_KND AS ITEM_KND , T5.SVPD_MGT_UNT AS MGT_UNT , T1.CNTR_NO , T1.CNTR_SN FROM TB_SVST_SV_WK_OSTR_IZ T1 /*LC_STOCK_ST163TB*/ INNER JOIN TB_SVPD_CST_SVAS_IST_OJ_IZ T2 /*LC_ALLOCATE_AC211TB*/ ON T2.CST_SV_ASN_NO = DECODE(SUBSTR(T1.CST_SV_ASN_NO,1,1),'9', '1', T1.CST_SV_ASN_NO) INNER JOIN TB_SVPD_CST_SV_EXCN_IZ T3 /*LC_ALLOCATE_AC201TB*/ ON T3.CNTR_NO = T1.CNTR_NO AND T3.CNTR_SN = T1.CNTR_SN INNER JOIN TB_SVPD_CST_SV_WK_RS_IZ T4 /*LC_VISIT_VS106TB*/ ON T4.CST_SV_ASN_NO = T1.CST_SV_ASN_NO AND T4.CNTR_NO = T1.CNTR_NO AND T4.CNTR_SN = T1.CNTR_SN INNER JOIN () T5 ON T5.SVPD_PD_CD = T1.ITM_PD_CD LEFT OUTER JOIN TB_SSCT_CNTR_BAS /*계약기본*/ X1 ON X1.CNTR_NO = T3.CNTR_NO AND X1.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_DTL /*계약상세*/ X2 ON X2.CNTR_NO = X1.CNTR_NO AND X2.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_ADR_REL X3 ON X3.DTL_CNTR_NO = X2.CNTR_NO AND X3.DTL_CNTR_SN = X2.CNTR_SN AND X3.ADRPC_TP_CD = '3' AND TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') BETWEEN X3.VL_STRT_DTM AND X3.VL_END_DTM AND X3.DTA_DL_YN = 'N' INNER JOIN TB_SSCT_CNTR_ADRPC_BAS X4 ON X4.CNTR_ADRPC_ID = X3.CNTR_ADRPC_ID LEFT OUTER JOIN (SELECT A.* FROM TB_SVPD_CST_SVAS_IST_OJ_IZ A INNER JOIN TB_SVPD_CST_SVAS_IST_ASN_IZ B ON A.CST_SV_ASN_NO = B.CST_SV_ASN_NO WHERE 1 = 1 AND A.SV_BIZ_DCLSF_CD LIKE '11%' AND A.MTR_STAT_CD != '3' AND B.WK_PRGS_STAT_CD = '20' AND A.DTA_DL_YN = 'N' AND B.DTA_DL_YN = 'N' AND ROWNUM = 1) T6 ON T6.CST_SV_ASN_NO = T1.CST_SV_ASN_NO LEFT OUTER JOIN TB_OGBS_MM_PRTNR_IZ T7 ON T7.PRTNR_NO = T6.FST_RGST_USR_ID AND T7.BASE_YM = SUBSTR(T6.FST_RGST_DTM, 1, 6) AND T7.OG_TP_CD IN ('W06', 'W02', 'HR1') AND T7.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_OGBS_MM_OG_IZ D1 ON D1.BASE_YM = T7.BASE_YM AND D1.OG_TP_CD = T7.OG_TP_CD AND D1.OG_ID = T7.OG_ID AND D1.DTA_DL_YN = 'N' LEFT OUTER JOIN TB_GBCO_DEPT_BAS D2 ON D2.DEPT_CD = D1.OG_CD AND D2.DTA_DL_YN = 'N' INNER JOIN (SELECT WARE_NO , MAX(WARE_NM) AS WARE_NM , HGR_WARE_NO , WARE_DV_CD FROM TB_SVST_MCBY_WARE_IZ WHERE 1 = 1 AND APY_YM BETWEEN SUBSTR(#{stFnlVstFshDtFrom},1,6) AND SUBSTR(#{edFnlVstFshDtTo},1,6)
AND HGR_WARE_NO = #{strWareNoM}
AND DTA_DL_YN = 'N' GROUP BY WARE_NO , HGR_WARE_NO , WARE_DV_CD ) T8 ON T8.WARE_NO = T1.WK_WARE_NO INNER JOIN (SELECT A.CST_SV_ASN_NO , (CASE WHEN B.IST_DT IS NULL THEN 0 WHEN C.CNTR_RCP_FSH_DTM != '00000000' AND C.CNTR_RCP_FSH_DTM IS NOT NULL THEN TO_DATE(SUBSTR(C.CNTR_RCP_FSH_DTM, 0, 8), 'YYYYMMDD') - TO_DATE(B.IST_DT, 'YYYYMMDD') ELSE TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD'), 'YYYYMMDD') - TO_DATE(B.IST_DT, 'YYYYMMDD') END ) AS USE_DAYS FROM TB_SVPD_CST_SVAS_IST_OJ_IZ A INNER JOIN TB_SVPD_CST_SV_EXCN_IZ B ON A.CNTR_NO = B.CNTR_NO AND A.CNTR_SN = B.CNTR_SN INNER JOIN TB_SSCT_CNTR_BAS C ON A.CNTR_NO = C.CNTR_NO INNER JOIN TB_SSCT_CNTR_DTL D ON A.CNTR_NO = D.CNTR_NO AND A.CNTR_SN = D.CNTR_SN ) T9 ON T1.CST_SV_ASN_NO = T9.CST_SV_ASN_NO WHERE T1.DTA_DL_YN = 'N' AND T1.SV_BIZ_HCLSF_CD = '6'
AND T1.FNL_ITM_GD_CD = #{fnlItmGdCd}
AND T5.SVPD_ITEM_KND = #{itmKndCd}
AND T5.SVPD_PD_CD = #{itmPdCd}
AND T1.FNL_VST_FSH_DT BETWEEN #{stFnlVstFshDtFrom} AND #{edFnlVstFshDtTo}
AND T1.RTNGD_PROCS_TP_CD = #{stRtngdProcsTpCd} AND T1.OSTR_CONF_DT BETWEEN #{stFnlVstFshDtFrom} AND #{edFnlVstFshDtTo}
AND T8.WARE_DV_CD = #{strWareDvCd}
AND T8.HGR_WARE_NO = #{strWareNoM}
AND T8.WARE_NO = #{strWareNoD}
AND T1.SV_BIZ_DCLSF_CD = #{svBizDclsfCd}
AND T1.OSTR_CONF_DT = #{stOstrConfDt}
AND T1.OSTR_CONF_DT IS NOT NULL
AND T1.OSTR_CONF_DT IS NULL ) A1 WHERE 1 = 1
AND A1.ERROR_CHECK > 0
ORDER BY A1.CST_SV_ASN_NO

