mapper.select:
SELECT CASE WHEN T1.REFRI_DV_CD = '1' THEN 'X' WHEN T1.REFRI_DV_CD = '2' THEN CASE WHEN T1.OSTR_CNFMDT IS NOT NULL THEN 'C' ELSE 'R' END ELSE 'N' END AS DP_YN /* 입금 */ , CASE WHEN T1.OSTR_CNFMDT IS NOT NULL THEN 'Y' ELSE 'N' END AS OSTR_YN /* 출고 */ , F_CMZ_CD_NM('TNT_WELLS', 'REFRI_DV_CD', T1.REFRI_DV_CD, #{session.langId}) AS REFRI_DIV /* 유/무상구분 */ , F_CMZ_CD_NM('TNT_WELLS', 'ITM_IOST_DV_CD', T1.ITM_IOST_DV_CD, #{session.langId}) AS SHIP_DIV /* 배송구분 */ , F_CMZ_CD_NM('TNT_WELLS', 'SV_BIZ_HCLSF_CD', T1.SV_BIZ_HCLSF_CD, #{session.langId}) AS RECEIPT_DIV /* 접수구분 */ , T1.CNTR_NO /* 계약번호 */ , T1.CNTR_NO || '-' || T1.CNTR_SN AS CNTR_DTL_NO /* 계약상세번호 */ , NVL(T10.RCGVP_KNM, T6.CST_KNM) AS CST_NM /* 고객명 */ , T1.SDING_SPP_NO AS SPP_ORD_NO /* 배송번호 */ , T4.PD_NM AS MCHN_MODEL /* 기기모델 */ , T2.OJ_DTL_CNTR_NO AS MCHN_CST_NO /* 기기고객번호 */ , T2.OJ_DTL_CNTR_NO || '-' || T2.OJ_DTL_CNTR_SN AS MCHN_CST_DTL_NO /* 기기계약상세번호 */ , T6.CST_KNM AS MCHN_CST_NM /* 기기고객명 */ , T7.PD_NM AS CTRL_PKG /* 현재패키지 */ , T8.PD_NM AS SHIP_PKG /* 배송패키지 */ , SUBSTR(T1.SDING_INFO1, 1, INSTR(T1.SDING_INFO1, '|', 1, 1) -1) AS SDING_PD_CD1 /* 모종1 상품코드 */ , SUBSTR( T1.SDING_INFO1 , INSTR(T1.SDING_INFO1, '|', 1, 1) + 1 , INSTR(T1.SDING_INFO1, '|', 1, 2) - INSTR(T1.SDING_INFO1, '|', 1, 1) - 1) AS SDING1 /* 모종1 */ , SUBSTR( T1.SDING_INFO1 , INSTR(T1.SDING_INFO1, '|', 1, 2) + 1 , INSTR(T1.SDING_INFO1, '|', 1, 3) - INSTR(T1.SDING_INFO1, '|', 1, 2) - 1) AS QTY1 /* 수량1 */ , SUBSTR(T1.SDING_INFO1, INSTR(T1.SDING_INFO1, '|', 1, 3) + 1) AS SOW_DT1 /* 파종일자1 */ , SUBSTR(T1.SDING_INFO2, 1, INSTR(T1.SDING_INFO2, '|', 1, 1) -1) AS SDING_PD_CD2 /* 모종2 상품코드 */ , SUBSTR( T1.SDING_INFO2 , INSTR(T1.SDING_INFO2, '|', 1, 1) + 1 , INSTR(T1.SDING_INFO2, '|', 1, 2) - INSTR(T1.SDING_INFO2, '|', 1, 1) - 1) AS SDING2 /* 모종2 */ , SUBSTR( T1.SDING_INFO2 , INSTR(T1.SDING_INFO2, '|', 1, 2) + 1 , INSTR(T1.SDING_INFO2, '|', 1, 3) - INSTR(T1.SDING_INFO2, '|', 1, 2) - 1) AS QTY2 /* 수량2 */ , SUBSTR(T1.SDING_INFO2, INSTR(T1.SDING_INFO2, '|', 1, 3) + 1) AS SOW_DT2 /* 파종일자2 */ , SUBSTR(T1.SDING_INFO3, 1, INSTR(T1.SDING_INFO3, '|', 1, 1) -1) AS SDING_PD_CD3 /* 모종3 상품코드 */ , SUBSTR( T1.SDING_INFO3 , INSTR(T1.SDING_INFO3, '|', 1, 1) + 1 , INSTR(T1.SDING_INFO3, '|', 1, 2) - INSTR(T1.SDING_INFO3, '|', 1, 1) - 1) AS SDING3 /* 모종3 */ , SUBSTR( T1.SDING_INFO3 , INSTR(T1.SDING_INFO3, '|', 1, 2) + 1 , INSTR(T1.SDING_INFO3, '|', 1, 3) - INSTR(T1.SDING_INFO3, '|', 1, 2) - 1) AS QTY3 /* 수량3 */ , SUBSTR(T1.SDING_INFO3, INSTR(T1.SDING_INFO3, '|', 1, 3) + 1) AS SOW_DT3 /* 파종일자3 */ , SUBSTR(T1.SDING_INFO4, 1, INSTR(T1.SDING_INFO4, '|', 1, 1) -1) AS SDING_PD_CD4 /* 모종4 상품코드 */ , SUBSTR( T1.SDING_INFO4 , INSTR(T1.SDING_INFO4, '|', 1, 1) + 1 , INSTR(T1.SDING_INFO4, '|', 1, 2) - INSTR(T1.SDING_INFO4, '|', 1, 1) - 1) AS SDING4 /* 모종4 */ , SUBSTR( T1.SDING_INFO4 , INSTR(T1.SDING_INFO4, '|', 1, 2) + 1 , INSTR(T1.SDING_INFO4, '|', 1, 3) - INSTR(T1.SDING_INFO4, '|', 1, 2) - 1) AS QTY4 /* 수량4 */ , SUBSTR(T1.SDING_INFO4, INSTR(T1.SDING_INFO4, '|', 1, 3) + 1) AS SOW_DT4 /* 파종일자4 */ , SUBSTR(T1.SDING_INFO5, 1, INSTR(T1.SDING_INFO5, '|', 1, 1) -1) AS SDING_PD_CD5 /* 모종5 상품코드 */ , SUBSTR( T1.SDING_INFO5 , INSTR(T1.SDING_INFO5, '|', 1, 1) + 1 , INSTR(T1.SDING_INFO5, '|', 1, 2) - INSTR(T1.SDING_INFO5, '|', 1, 1) - 1) AS SDING5 /* 모종5 */ , SUBSTR( T1.SDING_INFO5 , INSTR(T1.SDING_INFO5, '|', 1, 2) + 1 , INSTR(T1.SDING_INFO5, '|', 1, 3) - INSTR(T1.SDING_INFO5, '|', 1, 2) - 1) AS QTY5 /* 수량5 */ , SUBSTR(T1.SDING_INFO5, INSTR(T1.SDING_INFO5, '|', 1, 3) + 1) AS SOW_DT5 /* 파종일자5 */ , T1.REQD_DT AS MCHN_DEM_DT /* 기기철거일자 */ , T1.RCPDT AS RECEIPT_DT /* 접수일자 */ , T1.VST_CNFMDT AS VST_DT /* 방문일자 */ , T1.OSTR_DUEDT AS OSTR_SCHE_DT /* 출고예정일자 */ , T1.BS_FSH_DT /* B/S완료일자 */ , T1.DP_DT /* 입금일자 */ , T1.OSTR_CNFMDT AS OSTR_CNFM_DT /* 출고확정일자 */ , T13.CNR_NM AS SDING_RCG_WARE_NM /* 모종수령창고 */ , T1.OG_NM AS VST_CENTER /* 방문센터 */ , T11.PRTNR_KNM AS VST_ICHR /* 방문담당 */ , T11.CRAL_LOCARA_TNO AS ICHR_CRAL_LOCARA_TNO /* 담당휴대지역전화번호 */ , T11.MEXNO_ENCR AS ICHR_MEXNO_ENCR /* 담당전